/* 整體 */
::-webkit-scrollbar{
  width: 11px;
  height: 11px;
  border-radius: 8px / 8px;
}

/* 滑塊 */
::-webkit-scrollbar-thumb{
    background-color: #808080;
    border: 4px solid transparent;
    border-radius: 8px / 8px;
    background-clip: content-box;
}
::-webkit-scrollbar-thumb:hover{
    border: 3px solid transparent;
}

/* 按鈕 */
::-webkit-scrollbar-button:single-button{
    background-repeat: no-repeat;
}

/* 垂直按鈕 */
::-webkit-scrollbar-button:single-button:vertical{
    background-position: left, right;
    background-size: calc(50% + 0.5px) 40%;
}
::-webkit-scrollbar-button:single-button:vertical:hover{
    background-size: calc(50% + 0.5px) 50%;
}
/* 水平按鈕 */
::-webkit-scrollbar-button:single-button:horizontal{
  	background-position: top, bottom;
    background-size: 40% calc(50% + 0.5px);
}
::-webkit-scrollbar-button:single-button:horizontal:hover{
  	background-position: top, bottom;
    background-size: 50% calc(50% + 0.5px);
}

/* 垂直按鈕(上) */
::-webkit-scrollbar-button:single-button:vertical:decrement{
    background-image:
        linear-gradient(to bottom right, transparent 62.5%, #808080 0),
        linear-gradient(to bottom left, transparent  62.5%, #808080 0);
}
::-webkit-scrollbar-button:single-button:vertical:decrement:hover{
    background-image:
        linear-gradient(to bottom right, transparent 50%, #808080 0),
        linear-gradient(to bottom left, transparent 50%,  #808080 0);
}
/* 垂直按鈕(下) */
::-webkit-scrollbar-button:single-button:vertical:increment{
    background-image:
        linear-gradient(to top right, transparent 62.5%, #808080 0),
        linear-gradient(to top left, transparent  62.5%, #808080 0);
}
::-webkit-scrollbar-button:single-button:vertical:increment:hover{
    background-image:
        linear-gradient(to top right, transparent 50%, #808080 0),
        linear-gradient(to top left, transparent 50%,  #808080 0);
}
/* 水平按鈕(左) */
::-webkit-scrollbar-button:single-button:horizontal:decrement{
    background-image:
        linear-gradient(to bottom right, transparent 62.5%, #808080 0),
        linear-gradient(to top right, transparent  62.5%, #808080 0);
}
::-webkit-scrollbar-button:single-button:horizontal:decrement:hover{
    background-image:
        linear-gradient(to bottom right, transparent 50%, #808080 0),
        linear-gradient(to top right, transparent 50%,  #808080 0);
}
/* 水平按鈕(右) */
::-webkit-scrollbar-button:single-button:horizontal:increment{
    background-image:
        linear-gradient(to bottom left, transparent 62.5%, #808080 0),
        linear-gradient(to top left, transparent  62.5%, #808080 0);
}
::-webkit-scrollbar-button:single-button:horizontal:increment:hover{
    background-image:
        linear-gradient(to bottom left, transparent 50%, #808080 0),
        linear-gradient(to top left, transparent 50%,  #808080 0);
}

/* 角落隱藏 */
::-webkit-scrollbar-corner{
    visibility: hidden;
}
