style.css:
@-moz-document domain("pikabu.ru") {
.story-comments__all {
    display: flex;
    flex-direction: column;
}

.comments {
    order: 1;
}

.story-comments__menu {
    padding-bottom: 1em;
    border-bottom: none;
}

section[data-role="answer"] {
    order: 0;
    border-top: none;
    border-bottom: none;
    padding: 16px 24px;
}

@media screen and (max-width: 830px) {
    section[data-role="answer"] {
        padding: 15px 15px;
    }
}

section[data-role="answer"] div[class^="app__editor--"] {
    padding: 12px 16px;
}

.comment-reply__controls, section[data-role="answer"] div[class^="controls-view__host"] {
/*     justify-content: space-between; */
    padding: 12px 16px;
    background-color: white;
    border-top: none;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
}

section[data-role="answer"] div[class^="submit-btn-view__host"] {
    order: 1;
}

section[data-role="answer"] span[class^="controls-view__attachBtn"] {
    margin-left: 0;
}

section[data-role="answer"] span[class^="controls-view__draftDate"] {
    margin: 1px 16px 0 auto;
}


section[data-role="answer"] div[class^="app__host"] {
    border: 1px solid var(--color-black-440);
    border-radius: 10px;
}
}
