style.css:
@-moz-document domain("app.intercom.io"), domain("app.intercom.com"){
	/* C's name top*/
.inbox2__conversation-stream { 
    background-image: linear-gradient(to left, #DECBA4, #3E5151); 
    min-width: 400px;
}
  
	/* LEFT Side bar background*/
.flex.flex-col.flex-1.h-full.relative{
    background-image: linear-gradient(to right, #DECBA4, #3E5151);
}

	/* C LEFT side chat boxes*/
.absolute.w-full.inbox2__animated-list-item-container.inbox2__animated-list-transitions{
  background: linear-gradient(122deg, rgba(207, 69, 32,0.4) 16.98%, rgba(97, 0, 39,0.4) 100.00%); 
	border-radius: 9%;
  animation: none;
  transition: none;
  
}
.ember-view.group.no-underline.flex.flex-row.gap-2.p-3.rounded-3xl.transition-colors.cursor-pointer.duration-100{
 border-radius: 9%;
  padding: 14px;
}
.active.ember-view.group.no-underline.flex.flex-row.gap-2.p-3.rounded-3xl.transition-colors.cursor-pointer.duration-100{
  background: linear-gradient(244deg, rgba(207, 69, 32,0.4) 16.98%, rgba(97, 0, 39,0.4) 100.00%); 

}    

.list-none.pb-1{
border-radius: 9%;
}
  
  /*MENU letter color */
  
.inbox2__left-nav.o__expanded {
    width: var(--inbox2__inbox-list-width);
    color: #3dbfbf;
}

	/* MENU - number color */
.inline-flex {
    display: inline-flex;
  color: #fff !important;
}

.break-all {
    word-break: break-all;
    color: #ffffff
}

	/*RIGHT side bar boxes*/

.accordion-new__body{
  background-image: linear-gradient(to right, #DECBA4, #3E5151);
  max-width: 600px;
}
.inbox2__conversation-stream__content-part {
    max-width: 548px;
background: none;}
.accordion-new__header{
    background-image: linear-gradient(to right, #DECBA4, #3E5151);

}
.sticky.top-0.h-16.pl-6.p-4{
      background-image: linear-gradient(to right, #DECBA4, #3E5151);

}
.flex.flex-col.py-4.px-6{
        background-image: linear-gradient(to right, #DECBA4, #3E5151);

}
  
	/* RIGHT side bar text color*/

  .dark .dark\:text-dm-gray {
    -tw-text-opacity: 1;
    color: #fff;
}

	/* tag - mention */ 
  
.entity_mention{
background: linear-gradient(292deg, rgb(39, 43, 44) 0.00%, rgb(241, 60, 43) 100.00%);
}
  
  
  
	/* NOTES  COLOR */
  
.dark .dark\:bg-dm-yellow-dark {
    -tw-bg-opacity: 1;
background: linear-gradient(132deg, rgb(114, 43, 54) 0.00%, rgb(238, 178, 123) 100.00%) !important;}

	/* REPLY TYPING BOX COLOR */ 

.inbox2__composer__container.flex.flex-col.rounded-2xl.break-words.overflow-x-hidden{
background: linear-gradient(132deg, rgb(41, 110, 90) 0.00%, rgb(33, 35, 34) 100.00%);
}

	/* YOUR REPLY BACKGROUND COLOR*/

.bg-blue-light-ib2 {
    -tw-bg-opacity: 1;
  background: linear-gradient(132deg, rgb(41, 110, 90) 0.00%, rgb(33, 35, 34) 100.00%);
}

  /* CHAT BACKGROUND */
.flex.flex-grow.flex-1.flex-col{
      background-image: linear-gradient(to left, #DECBA4, #3E5151);
}

	/* Events in ticket*/

.flex.flex-row.justify-end{
  background-color: #3E5151; 
  background: linear-gradient(to left, #cdbfa2 5%, #4b5a58 80%);
  margin-bottom: 0
}
  
  /*Closing Bot */
.mx-8{
background: linear-gradient(to left, #cdbfa2 5%, #4b5a58 80%);
 /* use RGBA with 0, 0, 0, 0.1 for transparency */
	}
}
