style.css:
@-moz-document domain("online.mospolytech.ru"), url-prefix("http://online.mospolytech.ru") {
body {
  font-family: system-ui,Arial, Geneva, times new roman,"DefaultFont",sans-serif;
  font-weight: 400;
}

#page-wrapper {
  padding: 0 80px;
}

.clt_course_title {
  height: 63px !important;
  overflow-y: scroll;
   margin-bottom: 6px;
  display: flex;
  align-items: center;
}

.clt_course_title_content {
  height: 100%;
  padding: 2px 6px;
  display: flex;
  text-align: center;
  justify-content: center;
  font-size: 0.95rem !important;
  line-height: 116%;
  color: black;
  font-weight: 500;
}

.clt_course_img {
  box-shadow: 1px 1px 0 #fff, -1px -1px 0 #fff, -1px 1px 0 #fff, 1px -1px 0 #fff!important;
  border-radius: 2px;
}

.clt_course_catname {
  font-size: 0.8rem !important;
  line-height: 114%;
  color: rgba(0, 0, 0, 0.8) !important;
  font-weight: 400;
  border-top: 1px dashed #0077b5;
  padding: 4px;
  text-align: center;
  background: #ffffff;
}

.clt_coursebox{
  padding: 0.3rem 0.3rem !important;
}

.region-main {
  padding: 0 !important;
}

.dock_item_0_title {
  font-weight: 600 !important;
}

.dockeditem:hover {
  background: none !important;
}

#page-header {
  background-image: url('https://mir-s3-cdn-cf.behance.net/project_modules/2800_opt_1/f2b29f171836573.6475a7f76eb19.jpg') !important;

  background-size: cover;
  background-repeat: no-repeat;
  padding: 6px 20px;
  z-index: 9;
  backdrop-filter: blur(20px);
  filter: drop-shadow(0 -2px 5px rgb(48, 48, 65));
}
.h_top_wrapper {
  background: transparent !important;
}
#dock_bg{
  background: transparent !important;
}
#page-navbar {
  	background:none;
    color: #0080ff;
}
.breadcrumb {
   padding: 1px 0;
   font-size: 0.8rem;
  font-weight: 400;
}
.card-body{
  padding: 0rem!important;
}
.card-text {
  padding: 0rem!important;
}
.block {
  padding: 0.1% 0.9% !important;
  border: none !important;
}
.unlist li div{
  	cursor: pointer;
    color: #290098;
    border-bottom: 2.4px solid;
    margin-bottom: 0.3em;
    border-bottom: 2px solid transparent;
    padding: 3px 6px;
  	border-image: linear-gradient(0.25turn, rgba(255,249,34), rgba(255,0,128), rgba(56,2,155,0));
    border-radius: 13px;
    border-image-slice: 1;
}
.unlist li div:hover{
  background: aliceblue;
  border-radius: 4px 0 4px 0;
}
.h_rightblock_wrapper {
  padding-top: 8px;
}

.container-fluid  .headermain{
  font-weight: 500!important;
}
#h_rightblock_wrapper {
  padding-top: 20px;
}

#h_rightblock_wrapper>div.usernav {
    margin: 12px 0 0 10px;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .usertext {
  padding-right: 0;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .userbutton .avatars {
      right: auto;
  	position: relative;
}

#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .userbutton {
	display: flex;
  flex-direction: row-reverse;
  align-items: center;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .userbutton .avatar.current img {
  width: auto;
}

.instancename {
  font-weight: 400;
}

.sectionname {
  font-weight: 500 !important;
  font-size: 1.54rem;
  
}
#page-wrapper ul.topics>li, #page-wrapper ul.weeks>li, #page-wrapper ul.format_opentechnology_sections>li {
  border: none;
  margin:0;
}
.section.main:nth-child(6n+2) {
  background-color: #fee4cb;
  background-image: linear-gradient(to top, #ecd1e6 0%, #eed0e7 1%, #f2e9f5 100%);
  border-radius: 12px;
}
.section.main:nth-child(6n+3) {
  background-color: #e9e7fd;
  background-image: linear-gradient(-75deg, #edfffa 0%, #FFE6FA 100%);
  border-radius: 12px;
}
.section.main:nth-child(6n+4) {
  background-color: #dbf6fd;
  background-image: linear-gradient(to top, #fff1eb 0%, #dbebf3 100%);
  border-radius: 12px;
}
.section.main:nth-child(6n+5) {
  background-color: #ffd3e2;
  background-image: linear-gradient(45deg, #ffe4e7 0%, #ffdde1 100%);
  border-radius: 12px;
}
.section.main:nth-child(6n+6) {
  background-color: #c8f7dc;
  background-image: linear-gradient(to top, #cff5d2 0%, #deecdd 100%);
  border-radius: 12px;
}
.section.main:nth-child(6n+7) {
  background-color: #e2e8ff;
  border-radius: 12px;
}


body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionname, body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionhead, body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionname a, body.path-course-view #page-wrapper .course-content>ul>li>.content .sectionhead a {
  background: none;
}
#page-wrapper ul.topics>li+li, #page-wrapper ul.weeks>li+li, #page-wrapper ul.format_opentechnology_sections>li+li {
  margin-top: 10px;
}

.activityinstance{
  cursor: pointer !important;
}
#dock #dockeditempanel {
  border-radius: 8px;
	border: 0px;
}
.has_dock_top_horizontal #dock .dockeditem .dockedtitle h2 {
  font-weight: 500 !important;
}
.btn, .nav-item .nav-link {
  border-radius: 6px !important;
    background-color: #4784ee;
    filter: brightness(1.2) contrast(1.3);
  color: white !important;
}


.nav-item .nav-link {
  padding: 6px 12px !important;
  height: 36px !important;
  margin-right: 6px;
}

.btn-primary {
  font-weight: 500 !important;
  background: rgb(29, 151, 151) !important;
  border-color: transparent !important;
  transition: all 200ms ease-in-out;
}

.btn-primary:hover {
    background: rgb(18, 138, 138) !important;
}

.block a, #page-wrapper .block a {
  display: block;
}
#h_rightblock_wrapper .usermenu .moodle-actionmenu .dropdown-toggle .usertext {
  padding: 0 0px 0 20px;
}
.has_dock_top_horizontal #dock .dockeditem .dockedtitle {
    border-radius: 12px;
}
.has_dock_top_horizontal #dock .dockeditem .dockedtitle:hover {
    background: #b395ff30 !important;
}
.clt_courseinfo_below_image {
  background: bisque;
  border-radius: 6px;
}

.clt_courseinfo_below_image  div{
  font-size: 0.8rem !important;
  display: flex !important;
  align-items: center;
  justify-content: space-evenly;
}

/* Background*/
#page-wrapper {
  background: white !important;
	background-repeat: no-repeat !important;
  background-size: cover !important;
  
}
.block_topten {
  background: #ffffff !important;
}
.region-main-content {
  background: #ffffff !important;
}
#page-wrapper .container-fluid {
  background: #ffffff !important;
}


.clt_coursesblock:not(.clt_passe_partout) .clt_coursebox:hover .clt_course_img:after, .clt_coursesblock:not(.clt_passe_partout) .clt_course_img:hover:after {
  background:  linear-gradient(0.25turn, rgba(255,249,34), rgba(255,0,128), rgba(56,2,155,0)) !important;
	height: 2px !important;
   bottom: -3px;
  border-radius: 2px;
}
.section .activity .contentwithoutlink>a, .section .activity .activityinstance>a {
    display: inline-block;
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.80);
    text-indent: -31px;
    text-decoration: none;
  padding: 5px 40px 5px;
  transition: all 220ms ease-in-out;
  background-color: #fff;
  position: relative;
  margin-bottom: 2px;
  transition: 0.5s;
  font-size: 16px;
  border-radius: 0.4em;
  color: #0077b5;
}

.path-course-view #page-wrapper li.activity form.togglecompletion .btn img, .path-course-view li.activity span.autocompletion img, .path-course-view li.activity form.togglecompletion .btn, .path-course-view form.togglecompletion .btn, .path-course-view form.togglecompletion .btn img, .path-course-view span.autocompletion img {
  filter: hue-rotate(225deg)
}
.section .activity .contentwithoutlink>a, .section .activity .activityinstance>a:hover{
  background: #FFF;
}
 .iconlarge {
	border-radius: 4px;
    filter: brightness(1.1);
}

.section .activity .contentwithoutlink>a, .section .activity .activityinstance>a:hover {
  background: #e6eef999 !important;
}
.path-mod-quiz #mod_quiz_navblock .qnbutton {
  color: #6c757d !important;
    font-weight: 600;
}
.answer input, .answer label {
  cursor: pointer;
  border-radius: 24px;
}
.answer input:hover, .answer label:hover{
 color: black;
  font-weight: 500;
}
.draghome, .drop li {
border-radius: 8px;
}
.drop {
  border-radius: 12px
}
.course-content .section .content .sectionname {
  border-radius: 4px;
  background: none;
  padding: 20px;
}
#h_rightblock_wrapper .usermenu div.menu {
  border-radius: 6px !important;
}
header {
  z-index: 5;
}
.buttons_container  {
  z-index: 1;
}
.card-body .card-text .column {
  border: 1px solid rgb(127, 58, 184);
  border-radius: 6px;
}
.column.c1 a:hover {
  text-decoration: none !important;
  color: black !important;
}

.aalink.focus, #page-footer a:not([class]).focus, .arrow_link.focus, a:not([class]).focus, .activityinstance>a.focus, .aalink:focus, #page-footer a:not([class]):focus, .arrow_link:focus, a:not([class]):focus, .activityinstance>a:focus {
    outline: 1px solid rgba(181, 101, 255, 0.192);
    background: rgb(181, 120, 241);
    box-shadow: none !important;
    text-decoration: none;
}

.search-program {
  width: 60%;
  font-size: 18px !important;
  height: 34px;
  outline: 1px solid rgb(48, 150, 233);
  padding: 4px 2px 4px 8px !important;
  margin-left: 20px !important;
  border-radius: 6px;
  border-width: 2px !important;
  border-color: black !important;
}
.btn-sm {
  color: black !important;
}
.container-fluid .columnleft {
  position: absolute;
  right: 0
}

.course-section-header {
  background-color: transparent !important;
  padding: 20px
}

.activity-item {
  padding: 0.2rem !important;
  border: 0.1 !important;
  border-color: rgba(214, 214, 214, 0.13) !important;
}

.courseicon {
  height: 36px !important;
  width: 36px !important;
}

.collapsed-icon, .icons-collapse-expand {
color: black !important;
}

.content fieldset > :nth-child(2) > span:last-child {
  position: absolute;
  bottom: -14px;
  cursor: pointer;
  font-weight: 600;
  color: white;
  padding: 0px 8px 2px;
  font-size: 1rem;
  background-color: #4cad74;
  border: 0.6px solid #277246;
  border-radius: 4px;
  transition: all 200ms ease-in-out;
}
  .content fieldset > :nth-child(2) > span:last-child:hover {
      background-color: #1c9955;
  }

.drawer-toggles .drawer-toggler .btn[data-target="theme_opentechnology-drawers-breadcrumbs"]::before {
  background: #0080ff !important;
  /* display: none !important; */

}

.drawer-toggles .drawer-toggler .btn {
  background-color: #0077b5 !important;
  height: 28px !important;
  min-width: 28px !important;
  width: 36px !important;
  right:20px !important; 
}

.drawer {
  display: block !important;
  /* visibility: visible !important; */
  /* position: absolute; */
  /* top: 200px !important; */
}

.btn-outline-secondary:hover {
color: white !important;
}

.drawer-toggles .drawer-toggles-left .drawer-toggler, .drawer-toggles .drawer-toggles-right .drawer-toggler {
  top: 230px !important;
}

.float-right .btn.btn-link {
  background-color: #1276dae1 !important;
  font-size: 0.8rem !important;
}

.float-left .btn.btn-link {
  background-color: #384047b2 !important;
  font-size: 0.8rem !important;
}

.hide-empty-dock {
  overflow: hidden;
}

#h_rightblock_wrapper .primary-navigation .langmenu #lang-menu-toggle, .flex-column #h_rightblock_wrapper .usernav .button_mycourses {
  margin-right: 20px !important;
}

.drawer-toggler-breadcrumbs {
  margin-top: -30px !important;
  margin-left: 20px !important;
  border-radius: 0px !important;
}

.drawer-toggler-breadcrumbs button:before {
  display: none !important;
}
}
