style.css:
@-moz-document domain("instructables.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.home-content h1, .home-content h2,
.search-bar-label
{
	text-transform: capitalize!important;
}

#search-wrapper .scroll-to-top,

.contest-entry,
.discuss .field button,
.file-info,
.pagination ul li a,
.site-header-bottom .btn-category-header,
.sticky-header,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.post .text
{
	font-size: 1.85rem!important;
}

.icon-featured,
.icon-favorite
{
	font-size: 1.52rem!important;
}

.post .text
{
	line-height: 2.5rem!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.header-stats p {
    margin-right: 71px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.pagination ul li a
{
	padding: 13px 26px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.category-projects .category-projects-ible .ible-stats
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.article-header,
.category-landing-content-wrap,
.comment-type-selector,
.discussion,
.step,
.step-body,
.step-discussion
{
	max-width: none!important;
}

.category-projects-list-wrapper
{
	width: 98%!important;
}

.icon-views1, .icon-views2,
.icon-volume-higher, .icon-volume-lower, .icon-volume-mute,
.icon-windows,
.icon-wrench
{
    width: 25px!important;
}

}

@-moz-document domain("instructables.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.pagination ul li.active a,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
