@-moz-document domain("instructables.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Avatar ______________________________________________________________________________*/

[src*="https://content.instructables.com/static/image/user.default"] {
	background: url(https://i.ibb.co/f2wDXnH/Avatar.png) no-repeat!important;
	box-sizing: border-box;
}

[src*="https://content.instructables.com/static/image/user.default"] {
    background-size: 55px!important;
    height: 56px!important;
    padding-left: 56px!important;
    width: 1px!important;
}

/* Checkmark _______________________________________________________________________________*/

	/* Big Check ___________________________*/

	/* Checked ___________________________*/

	selector
	{
		background: url(https://i.ibb.co/ZMNmHwc/Mcrw-IRxnnrq.png) no-repeat!important;
		box-sizing: border-box;
	}

	/* Unchecked ________________________*/

	selector
	{
		background: url(https://i.ibb.co/thJkp9V/big-check-off.png) no-repeat!important;
		box-sizing: border-box;
	}

	selector {
		background-size: 31px!important;
		height: 32px!important;
		padding-left: 32px!important;
		width: 1px!important;
	}

	/* Checked ___________________________*/

	input[type="checkbox"]:checked
	{
		background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
		box-sizing: border-box;
	}

	/* Unchecked ________________________*/

	input[type="checkbox"]
	{
		background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
		box-sizing: border-box;
	}

	input[type="checkbox"]:checked,
	input[type="checkbox"]
	{
		background-size: 25px!important;
		height: 26px!important;
		padding-left: 26px!important;
		width: 1px!important;
	}

