style.css:
@-moz-document url-prefix("https://10fastfingers.com")
{

/* ==UserStyle==
@name           10fastfingers.com - 27/07/2022, 05:58:50
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Rishi581b 
==/UserStyle== */

:root {
    --backgroundColor: #16ffbd; 
    --darkskyColor: #439f76;
    --pink:#f070a1;
    --darkpink:#c34371;
    --lessdarkskyColor:#12c998; 

}

::-webkit-scrollbar
{
    display: none;
}

body {
    font-family: 'Pinyon Script', cursive!important;
    font-size: 14px;
    line-height: 1.428571429;
/*     color: #16ffbd; */
    background: #17c090!important;
    
}
.container-modified{
    background:#04c48d;
}
#bs-example-navbar-collapse-1{
    background-color:var(--darkskyColor);   
}

.top-first-bg{
    background:var(--darkskyColor);   
    box-shadow:0px 10px 10px black;
}
.row{
    background-color:#13d49d;
    color:var(--backgroundColor);   
}
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th{
    background-color:var(--pink);   
}
.control-label{
    color:#000000f7;
}
#language-list{
    background:var(--pink);
}

#speedtest-main #words{    
    font-family:'Pinyon Script',cursive;
    background:var(--pink);
    height:210px;
    border-radius:12px;
    border-bottom:1px solid var(--darkpink);
    border-right:1px solid var(--darkpink);
    border-top:none;
    box-shadow:12px 12px 10px var(--darkpink);
}
#speedtest-main #words span.highlight{
    background:#ffffffe6;
    box-shadow: #c34371fa 0px 0px 15px 0px inset;
}
#speedtest-main #input-row #inputfield{
    border:2px solid #ffffffe6;   
    border-radius:10px;
    color: darkred;
    background:#ffffffed;
    font-family:'Pinyon Script',cursive;
    box-shadow: rgba(50, 50, 93, 0.25) 0px 25px 30px -12px inset, rgba(0, 0, 0, 0.3) 0px 19px 36px -18px inset;
    backdrop-filter:blur(45px);    
}

#input-row.row::after{
    border:5px solid #17c090;
}

#speedtest-main #input-row{
    background:transparent;
}
#speedtest-main #input-row #timer{
    background:var(--pink);
    border-radius:5px;
    color:var(--backgroundColor);
    box-shadow:3px 3px 5px var(--darkpink);
}
.btn-primary{
    background-color:var(--pink);
    color:var(--backgroundColor);
    border:none;
    box-shadow:3px 3px 5px var(--darkpink);
    
}
.btn-primary:hover{
    background:var(--darkpink);
}
.nav-tabs>li>a{
    background:var(--pink);
    border:1px solid var(--darkpink);
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color:var(--backgroundColor);
    background-color:var(--darkpink);
}
.nav-tabs>li>a{
    border-radius:10px 10px 10px 10px ;

}

a.list-group-item{
    color:white;
    font-size:1.1em;
    border-bottom-right-radius:11px;
    border-bottom-left-radius:11px;
    border-top-right-radius:11px;
    border-top-left-radius:11px;
    box-shadow:10px 2px 10px black;
}
.list-group{
    border-radius:10px;
   
}
#switch-typing-test-language{
    background:var(--lessdarkskyColor);
    box-shadow: rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset;
}
.list-group-item{
    left:-10px;
    background-color:var(--pink);
    border:1px solid var(--darkskyColor);
    border-radius:10px;
    box-shadow:10px 2px 10px black;
}

.highscore-box #table-data{
    background:var(--pink);
    border-radius:10px 10px 10px 10px;
    box-shadow:10px 1px 20px 10px var(--darkpink);
}
table{   
    background-color:var(--pink);    
    border-top-left-radius:10px;
    border-top-right-radius:10px;
    border-bottom-left-radius:10px;
    border-bottom-right-radius:10px;
}
a{
    color:var(--darkpink);    
}
#speedtest-main #auswertung-box #auswertung-result #result-table td#wpm strong {
    color:var(--darkskyColor);
}
#speedtest-main #auswertung-box #auswertung-result h3{
    
    background:var(--darkpink);
}
#speedtest-main #auswertung-box #auswertung-result{
    background:var(--pink);
}
#share-on-facebook-button{
    background:var(--darkpink);
}
#badge-box{
    background:var(--darkpink);
    border-radius:20px ;
    background-color:var(--darkpink);
}
a.list-group-item.active, a.list-group-item.active:hover, a.list-group-item.active:focus{
    color:var(--backgroundColor);
    background-color:var(--darkpink);
    border-color:var(--darkpink);
}
a.list-group-item:hover{
    color:green;
}
#main-content-trenner{
    background:var(--backgroundColor);
}
#footer-bg{
    background:var(--darkskyColor);
}
.col-sm-4{
    color:var(--darkskyColor);

    font-size:18px;    
}
#content-bg{
    background:var(--lessdarkskyColor);
}

#activities #myActivitiesTabContent{
    border:2px solid var(--darkpink);
    border-radius:10px ;
}

.main-layout #main-content, .main-layout .main-content{
    background:var(--darkskyColor);   
}
div.tab-content{
    background:var(--darkskyColor);
}
#practice-mode-selection-menu{
    background:var(--lessdarkskyColor);
}
 #text-practice.row.main-layout {
    background:var(--lessdarkskyColor);
}
#text-practice-content.tab-pane.active{
    background:var(--lessdarkskyColor);
}
#row1.workspace{
    background:var(--pink);
    box-shadow: rgba(6, 24, 44, 0.4) 0px 0px 0px 2px, rgba(6, 24, 44, 0.65) 0px 4px 6px -1px, rgba(255, 255, 255, 0.08) 0px 1px 0px 0px inset; 
}
#row1.workspace span.highlight{
    background:white;  
}
.row{
    color:black;
}
#words.row{
    box-shadow: var(--darkpink) 0px 2px 4px 0px, var(--darkpink) 0px 7px 13px -3px, var(--darkpink) 0px -5px 0px 0px inset;
    border:none;
    background:var(--pink);   
    border-radius:10px;
}
#auswertung-result{
    background:var(--pink);
}

#input-row{
    background:var(--backgroundColor);
}
#input-row button{
    background:var(--pink);
    border:none;
}
#input-row button:hover{
    background:var(--darkpink);
}
#input-row #timer{
    background:var(--pink);
    border-radius:10px;
}
#main-content.col-xs-12.col-sm-12.col-md-12.col-lg-12 {
    background:var(--lessdarkskyColor)!important;
}

#practice-main.row.main-layout{
    background:var(--darkskyColor)!important;
}
div.col-sm-12 pre{
    background:var(--backgroundColor)!important;
    border-radius:10px;
    border:none;
}
div.row button,input.btn.btn-info{
    border:1px solid #00000087;
}
div.col-sm-12 pre table.tg{
    border:3px solid black;
}
table.table.table-striped.table-responsive tbody.list td.title a,small.text-muted{
    color:#0000009e;
    font-size:1em;
}
.nav.nav-tabs a:hover{
    color:green;
}
a.btn.btn-primary{
    color:white;
}

}
