style.css:
@-moz-document regexp("https://(?:.+.)?waze.com/(?:.+/)?editor.*") {
.panel:not(.mapUpdateRequest):not(.place-update-edit):not(.mapProblem), .turn-instructions-panel {
	    width: unset;
	  }

	  .turn-preview-inner .turn-preview-content .primary-markup, .turn-preview-inner .turn-preview-content .secondary-markup, .turn-instructions-panel .exit-sign-item>input, .turn-instructions-panel .turn-instruction-item>input, .turn-instructions-panel .panel-content {
	    direction: rtl
	  }

	  .mapUpdateRequest.panel .description .content, .conversation-view .comment .text, .new-comment-form .new-comment-text, wz-textarea#tts {
  	  direction: rtl;
  }
}
