style.css:
@-moz-document domain("phileweb.com") {
    div#main {
        min-width: 960px;
        width: 95%;
    }

    #main #contentWrap {
        min-width: 740px;
        width: calc(100% - 310px);
    }

    div#headLineTop, #pickupBox {
       /* min-width: 430px; */
        width: calc(100% - 30px);
        margin-left: 15px;
    }

    #headLineTop > .article {
        min-width: 360px;
        width: calc(50% - 5px);
    }

    #headLineTop #closeupNewBox {
        width: 100%;      
    }

    #headLine {
        margin-left: 20px;        
    }

    #pickupBox .article a .closeUpText, #pickupBox .article a .artileInfowrap, #headLine .article a .closeUpText, #headLine .article a .artileInfowrap{
        min-width: 499px;
        width: calc(100% - 250px);
    }

    #relatedArticles .title_article{
        min-width: 580px;
        width: calc(100% - 170px);
    }
    
    #pickupBox > .articles{
        width: 100%;     
    }
    
    #closeupBox .entries li, #hometheater_ch .entries li {
        min-width: 330px;
        width: calc(50% - 5px);
    }
    
    #closeupBox .entries li .title, #closeupBox .entries li .articleTitle, #hometheater_ch .entries li .title, #hometheater_ch .entries li .articleTitle{
        min-width: 160px;
        width: calc(100% - 170px);
    }

    .dc_channel_closeup .list li, .audio_channel_closeup .list li {
        min-width: 360px;
        width: calc(50% - 10px);
    }

