style.css:
@-moz-document domain("chess.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

selector,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.lesson-component
{
	font-size: 2.5rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.game-over-modal-component .game-over-modal-ads
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

selector
{
	padding: 1.2rem!important;
}

.ui_pagination-item-component.ui_pagination-item-light, .ui_pagination-item-component:not(.ui_pagination-item-dark)
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.tournaments-table-name,
.layout-component
{
	max-width: none!important;
}

}

@-moz-document domain("chess.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {

.tournaments-table-name {
    width: 50%;
}

.layout-column-two {
    grid-column: 3!important;
}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("chess.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.ui_pagination-item-component.ui_pagination-item-active,
.index-post-author,
.index-quote-author,
.modal-game-over-user-username,
.vertical-move-list .move:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.course-extra-info,
.level-desc,
.game-over-modal-component .game-over-modal-createAccount,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.modal-game-over-header-title,
.table-component thead th,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.course-author,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.lesson-info-item:first-child,
.video-details-item:first-child,

time
 {
	color: #07beff!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

.white
{
	color: #f48f8f!important;
}
.black
{
	color: #f4c58f!important;
}
}

@-moz-document domain("chess.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.lesson-status,
.modal-game-over-rounded-green,
.ui_pagination-item-component.ui_pagination-item-light, .ui_pagination-item-component:not(.ui_pagination-item-dark)
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Orange 
_____________________________________________________________________________________________________*/

.ui_v5-button-component.ui_v5-button-basic,
.ui_v5-button-component.ui_v5-button-basic-dark,
.ui_v5-button-component.ui_v5-button-basic-light
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.ui_v5-button-component.ui_v5-button-basic::before,
.ui_v5-button-component.ui_v5-button-basic-dark::before,
.ui_v5-button-component.ui_v5-button-basic-light::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.ui_v5-button-component.ui_v5-button-basic:hover::before,
.ui_v5-button-component.ui_v5-button-basic-dark:hover::before,
.ui_v5-button-component.ui_v5-button-basic-light:hover::before
{
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.ui_v5-button-component.ui_v5-button-primary
{	
	background: radial-gradient(circle, #7fa650a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.ui_v5-button-component.ui_v5-button-primary::before
{
	background: radial-gradient(circle, #000 0%, #7fa650 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.ui_v5-button-component.ui_v5-button-primary:hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.modal-first-time-component .ui_modal-body,
.section-container-component,
.ui_modal-body.ui_modal-dark-light
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.modal-first-time-component .ui_modal-body:hover,
.section-container-component:hover,
.ui_modal-body.ui_modal-dark-light:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.modal-game-over-header-green
{
	background: linear-gradient(to bottom, #85a94e 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.modal-game-over-header-green
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}


/* Highlight Background 
_____________________________________________________________________________________________________*/

.table-component:not(.table-no-borders) tr:hover,
.vertical-move-list .move:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

selector
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#000 70%)!important;
	padding: 13px 26px!important;
}

selector
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#000 70%)!important;
	padding: 13px 26px!important;
}

.ui_pagination-item-component.ui_pagination-item-active
{
	background: radial-gradient(ellipse at center, #00809d 0%,#000 70%)!important;
	padding: 13px 26px!important;
}






}

@-moz-document domain("chess.com") {
/* Image Settings */

.course-component,
.course-item,
.index-suggestion-component,
.lesson-component,
.table-component:not(.table-no-borders) tr,
.vertical-move-list .move
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.course-component:hover,
.course-item:hover,
.index-suggestion-component:hover,
.lesson-component:hover,
.table-component:not(.table-no-borders) tr:hover,
.vertical-move-list .move:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("chess.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


/* Avatar ______________________________________________________________________________*/

[alt="Guest"] {
	background: url(https://i.ibb.co/f2wDXnH/Avatar.png) no-repeat!important;
	box-sizing: border-box;
}

[alt="Guest"] {
    background-size: 65px!important;
    height: 66px!important;
    padding-left: 66px!important;
    width: 1px!important;
}

/* Chess960 ______________________________________________________________________________*/

.icon-font-chess.chess960:before {
    content: "";
}

.tournaments-table-tournament-icon[v-tooltip="Chess960"] {
    font-size: 5.54rem!important;
    margin-top: -15px!important;
}

.icon-font-chess.chess960:before {
	background: url(https://i.ibb.co/TH9BPFp/chess960.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-font-chess.chess960:before {
    background-size: 40px!important;
    padding-left: 41px!important;
    width: 1px!important;
}

/* No Vacation ______________________________________________________________________________*/

.icon-font-chess.circle-dashboard:before {
    content: "";
}

.icon-font-chess.circle-dashboard:before {
	background: url(https://i.ibb.co/9Tyqxpp/Blocked.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-font-chess.circle-dashboard:before {
    background-size: 20px!important;
    padding-left: 21px!important;
    width: 1px!important;
}

/* Official Tournaments ______________________________________________________________________________*/

.icon-font-chess.nav-pawn:before {
    content: "";
}

.icon-font-chess.nav-pawn:before
 {
    background-size: 30px!important;
    padding-left: 31px!important;
    width: 1px!important;
}

.tournaments-table-tournament-icon[v-tooltip="Official Tournaments"] {
    font-size: 6.54rem!important;
    margin-top: -28px!important;
}

.icon-font-chess.chess-pawn-rook:before {
	background-image: url(https://i.ibb.co/M5kJTDN/pieces-White-Pawn.png)!important;
	box-sizing: border-box;
}

/* Play ______________________________________________________________________________*/

.icon-font-chess.play:before {
    content: "";
}

.lesson-status-ready .lesson-status-icon {
    font-size: 8.2rem!important;
    margin-left: -2.7rem!important;
}

.icon-font-chess.play:before
{
	background: url(https://i.ibb.co/7VB6KK3/red-play.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-font-chess.play:hover:before
{
	background: url(https://i.ibb.co/qdS3kwB/red-play-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-font-chess.play:hover:before,
.icon-font-chess.play:before
{
	background-size: 65px!important;
	padding-left: 66px!important;
	width: 100%!important;
}

.lesson-info-icon.play:hover:before,
.lesson-info-icon.play:before
{
	background-size: 25px!important;
	padding-left: 26px!important;
	width: 100%!important;
}

/* Set Position ______________________________________________________________________________*/

.icon-font-chess.chess-pawn-rook:before {
    content: "";
}

.tournaments-table-tournament-icon[v-tooltip="Set Position"] {
    font-size: 5.54rem!important;
    margin-top: -10px!important;
}

.icon-font-chess.chess-pawn-rook:before {
	background: url(https://i.ibb.co/6YrdwXN/white-pawn-rook.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-font-chess.chess-pawn-rook:before {
    background-size: 30px!important;
    padding-left: 31px!important;
    width: 1px!important;
}

/* Standard Tournament ______________________________________________________________________________*/

.dark-mode .icon-font-chess.chess-board:not(.light-mode):before, .icon-font-chess.chess-board.dark-mode:before {
    content: "";
}

.tournaments-table-tournament-icon[v-tooltip="Standard Tournament"] {
    font-size: 5.54rem!important;
    margin-top: -10px!important;
}

.dark-mode .icon-font-chess.chess-board:not(.light-mode):before, .icon-font-chess.chess-board.dark-mode:before {
	background: url(https://i.ibb.co/rF5dRxr/Chess-Board.png) no-repeat!important;
	box-sizing: border-box;
}

.dark-mode .icon-font-chess.chess-board:not(.light-mode):before, .icon-font-chess.chess-board.dark-mode:before {
    background-size: 40px!important;
    padding-left: 41px!important;
    width: 1px!important;
}


}

