style.css:
@-moz-document url("https://catwar.su/cw3/") {
	/*полосы прокрутки*/
body::-webkit-scrollbar,#fightLog::-webkit-scrollbar {
    width:5px;}
#text::-webkit-scrollbar {
    width:15px;height:10px;}
#family::-webkit-scrollbar,#history::-webkit-scrollbar,#dein::-webkit-scrollbar,#chat_msg::-webkit-scrollbar,#cws_chat_msg::-webkit-scrollbar,#itemList::-webkit-scrollbar,#akten::-webkit-scrollbar {
    width:15px;}
#text::-webkit-scrollbar-track,#family::-webkit-scrollbar-track,#history::-webkit-scrollbar-track,#dein::-webkit-scrollbar-track,#chat_msg::-webkit-scrollbar-track,#cws_chat_msg::-webkit-scrollbar-track,#itemList::-webkit-scrollbar-track,#akten::webkit-scrollbar-track {
    background:var(--bg-color0)!important;
    border-radius:10px;}
#text::-webkit-scrollbar-thumb,#family::-webkit-scrollbar-thumb,#history::-webkit-scrollbar-thumb,#dein::-webkit-scrollbar-thumb,#chat_msg::-webkit-scrollbar-thumb,#cws_chat_msg::-webkit-scrollbar-thumb,#itemList::-webkit-scrollbar-thumb,#akten::-webkit-scrollbar-thumb {
    background:var(--bg-color5)!important;
    border:.5px solid var(--bg-color4)!important;
    border-radius:10px;}
#text::-webkit-scrollbar-corner,#family::-webkit-scrollbar-corner,#history::-webkit-scrollbar-corner,#dein::-webkit-scrollbar-corner,#chat_msg::-webkit-scrollbar-corner,#cws_chat_msg::-webkit-scrollbar-corner,#itemList::-webkit-scrollbar-corner,#akten::-webkit-scrollbar-corner {
  background:transparent;}
#chat_msg,#cws_chat_msg,#akten,#dein,#itemList,#history,#family {
    overflow-y:auto;
    overflow-x:hidden;}

/*цветовая палитра*/
html {
    --bg-color0:#111111;
    --bg-color1:#191919;
    --bg-color2:rgba(34,34,34,1);
    --bg-color3:rgba(45,45,45,1);
    --bg-color4:rgba(40,40,40,1);
    --bg-color5:#3c3c3c;
    --bg-color6:rgba(188,188,188,1);
    --bg-color7:#2d2d2d;
    --tx-color0:#000000;
    --tx-color1:#838383;
    --tx-color2:#b2b2b2;
    --tx-color3:#ababab;
    --bg-fight1:#482419;
    --bg-fight2:#353535;}

/*всплывающие окна*/
#reveal {
    background:var(--bg-color4) url(http://d.zaix.ru/frqx.png) no-repeat -200px -80px!important;}
#reveal_info>div>table>tbody {
    border:.5px solid var(--tx-color1)!important;}

/*выделение*/
::selection {
    background-color:var(--tx-color3);}
span:focus {
    outline:0;}
#text:focus,#msg_send:focus,#mit:focus,#mitok:focus,#do_exchange:focus,#rabbit_my:focus,#rabbit_his:focus {
    outline: 2px solid var(--tx-color1)!important;
    outline-offset:-1px;}

/*ссылки и линии*/
a {
    color:var(--tx-color2);
    text-decoration:none;}
a:hover {
    color:#fff;
    text-decoration:underline;}
hr {
    border: none;
    background-color:var(--bg-color2);
    color:var(--bg-color4);
    height:2px;}

/*изображение загрузки*/
#chat_msg_loading,#family_block>img,#pairs-loading {
   content:url(http://d.zaix.ru/hgx3.gif);} 

/*погода, температура, время*/
#tr_tos>td>table>tbody>tr>td>a>img {
    left:0px;
    position:fixed;
    top:244px;}
#tos {
    border:.5px solid var(--bg-color1);
    border-radius:5px;
    left:-65px;
    top:104px;
    transform: rotate(90deg);
    position:fixed;}
#hour {
    left:262px;
    top:196px;
    transform:rotate(90deg);
    position:fixed;}
#hour>img {
    border-radius:5px;}

/*параметры и навыки*/
#parameter {
    background-color:#292829;
    padding:0px 5px 5px 5px;
    right:670px;
    position:fixed;
    top:100px;
    width:182px;}

/*название локации*/
#location {
    background-color:var(--bg-color4);
    color:var(--tx-color1)!important;
    font-weight:8;
    right:463px;
    padding:7px;
    position:fixed;
    text-align:center;
    top:307px;
    width:191px;}

/*сообщение ошибки или действий*/
#error {
    background-color:#4a4a4a!important;
    color:#bab9ba;
    border:.5px solid var(--bg-color4);
    left:705px!important;
    top:930px!important;
    width:240px;
    position:fixed;}

/*мой кот, чат, лс*/
.small {
    background-color:var(--bg-color4);
    border:.5px solid var(--bg-color4);
    padding:7px;
    color:#848684;
    font-size:10px;
    right:463px;
    text-align:center;
    top:105px;
    position:fixed;
    width:189px;}
#newchat,#newls {
    background-color:#a2a3a2;
    color:var(--bg-color4);
    font-size:.77em;
    vertical-align:super;}
.small>a {text-decoration:none;}

/*тело и тб*/
body {
    color:var(--tx-color1)!important;
    height:1080px;
    margin-left:auto;
    margin-right:auto;
    max-width:1920px;
    position:fixed;
    overflow-x:hidden;
    font-size:11.334px!important;}
body>#app >p:not(#error){visibility: hidden;}
#black { visibility: visible;  
    background-color:#292829!important;
    border-radius:10px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color1)!important;
    padding:5px 7px 7px 7px;
    right:5px;
    text-align:center;
    top:1110px;
    width:50px;
    position:fixed;}
#black::before { content: 'ТБ: '; }


/*поле игровой*/
#tr_field {
    left:-10px;
    position:fixed;
    top:-28px;
    transform: scale(0.935)!important;
    z-index:1;}

/*убирает погодную тусклость игровой*/
#cages_div {
    opacity:1!important;}

/*цвет шрифта в игровой*/
.move_name,.cat_tooltip,.cat_tooltip>u>a,.cat_tooltip>.online,.cat_tooltip>a {
    color:var(--tx-color0)!important;}

/*блок чата*/
input {
    background-color:var(--bg-color0);
    color:var(--tx-color2) !important;}
select {
    background-color:var(--bg-color0);
    color:var(--tx-color1);}
#tr_chat {
    background-color:#292829;
    position:fixed;
    right:5px;
    top:110px;
    width:456px;}
#chat_msg, #cws_chat_msg {
    height:823px;
    max-width:436px;
    padding:5px 5px 3px 10px;}
#chat_msg>span>table>tbody>tr>td>a {
    text-decoration:none;}
#txt {
    max-height:50px;
    overflow-x:inherit;}
#text {
    max-width:470px;
    max-height:75px;
    overflow-x:inherit;
    overflow-y:auto;}
input#text {
    height:15px!important;}
#chat_form>span>b {
    display:none;}
#chat_form>span>b {
    display:none;}
#chat_form{
    height:45px;}
.chat_text {
    width: 360px!important;}
.nick {
    border-bottom:1px dotted var(--tx-color2);
    color:var(--tx-color2) !important;}
.myname {
    background-color:var(--bg-color6)!important;
    border-radius:5px;
    color:var(--tx-color0);
    padding: 3px;}

/*ползунки*/
.ui-slider {
    background:var(--bg-color0);
    border:.5px solid var(--bg-color4);
    margin-right:50px;
    right:5px;
    top:10px;
    z-index:3;}
.ui-slider .ui-slider-handle {
    background:var(--bg-color5);
    border:.5px solid var(--bg-color4);}
#layer {
    left:10px;}

/*блок действий*/
#block_mess {
    background-color:#292829;
    height:75px;
    line-height:75px;
    position:fixed;
    left:22px;
    text-indent:13px;
    top:935px;
    width:937px;}
#block_deys {
    font-size:0;
    min-height:80px;
    position:fixed;
    left:22px;
    top:945px;
    width:920px;
    z-index:3;
  width: 65%;}
#akten,#dein {
    margin-left:5px;
    max-height: 70px!important;
    max-width: 800px!important;}
#tr_actions {
    background-color:var(--bg-color2);
    border:0;}
#mit {
    background-color:var(--bg-color0);
    border-radius:5px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color1);
    font-size:14px;
    margin-top:15px;
    max-width:25px;}
#mitok {
    background-color:var(--bg-color0);
    border-radius:5px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color1);
    font-size:14px;
    margin-top:3px;}

/*блок предметов*/
#tr_mouth {
    background-color:#292829;
    border:2px solid var(--bg-color1);
    min-height:11px;
    position:fixed;
    right:4px;
    top:3px;
    width:855px;
    z-index:1;}
#tr_mouth>td>a,#tr_mouth>td>span {
    margin-left:11px;}
#thdey {
    padding: 5px 0 0 5px;}
#ctdey {
    padding:5px 0 10px 8px;}
#tir {
    margin-left:8px;}
#itemList{
    max-height:73px!important;}
#katze>td>i {
   font-size:0em;}
.active_thing {
    border-radius:8px;
    border:none;
    box-shadow:0 0 0 1px var(--tx-color1) inset;}

/*блок истории*/
#history {
    background-color:#292829;
    max-height:671px;
    padding:5px;
    position:fixed;
    right:463px;
    top:337px;
    width:388px;}
#history_clean {
    margin-left:34%;
    position:absolute;}
#ist {
    color:var(--tx-color1);}
#history {
    color:transparent;}
#history_block>div {
    background-color:transparent!important;}

/*блок родственных связей*/
#family {
    background-color:#292829;
    border:2px solid var(--bg-color1);
    max-height:140px;
    padding: 6px;
    position:fixed;
    right:461px;
    top:151px;
    width:192px;}


/*панель боя*/
#fightPanel {
    background-color:var(--bg-color7);
    border-radius:0;
    border:2px solid var(--bg-color1);
    height:350px;
    position:absolute;
    right:-300px;
    top:360px;
    width:300px!important;
    z-index:4;}
#fightLog {
    height:92%!important;
    margin-right:5px;}
.hotkey {
    background-color:var(--bg-color0);
    color:var(--tx-color1) !important;
    margin-bottom:2px;}
.log_claws {
    color:var(--bg-fight1)!important;}
.log_teeth {
    color:var(--bg-fight2)!important;}

/*кнопка возврата поля после нюха*/
#tr_tos input {
    background-color:var(--bg-color4);
    border-radius:10px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color1)!important;
    font-size:15px!important;
    padding:0px;
    position:absolute;
    width:120px;
    transform: rotate(90deg);
    left:-50px;
    top:320px;
    z-index:1;}
#cws_treeTechiesHandleWrap {background-color:#2e2d2e;
    color:#848684;}

/*убирает заглавия блоков, небо*/
#history>h2,#family>h2,#parameters.toggle,.title,#tr_sky {
    display:none;}

/*убирает оставшийся под блоками мусор*/
#main_table {
    background:none!important;}
#info_main {
    background-color:var(--bg-color2);}
#site_table {
    background-color:transparent;}

/*дополнительно*/
#history_block>hr {margin-top:25px;background-color:var(--bg-color1)!important;} 
#history_block>h2 {display:none;} 
#erase_attacker {margin-left:26%;} 
#attacker_block {color:var(--tx-color1);} 
#cleaner_block {color:var(--tx-color1);} 
#erase_cleaner {margin-left:29%;}
#fteams-wrap {position:absolute;width:320px!important;top:70%;} 
.reload,.alert {z-index:5!important;}


.mouth>li>img,.mouth>li>div>div {
    transition:.5s;}
.mouth>li>img:hover,.mouth>li>div>div:hover {
    transform:scale(1.5);}

  body { background-color:var(--bg-color1)!important;}
}
