style.css:
@-moz-document domain("bilibili.tv") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.bstar-back-top--pc,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.comment__name h5
{
	font-size: 1.85rem!important;
}

.comment__operate__item,
.comment
{
	font-size: 1.52rem!important;
}

.comment__name h5
{
	line-height: 2.5rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.bstar-header__upload-btn:before
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.comment-list__wrap__item
{
	padding: 1.2rem!important;
}

.scroll-container__item[data-v-03568ed5],
.bstar-tab--solid .bstar-tab__item,
.timeline-tab__item
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.trending-card__videolist[data-v-7926129b],
.video-play:not(.video-play--mobile):not(.video-play--large) .video-play__meta,
.video-play:not(.video-play--mobile):not(.video-play--large) .video-play__player
{
	max-width: none!important;
}

}

@-moz-document domain("bilibili.tv") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {

	.video-play:not(.video-play--mobile):not(.video-play--large) .video-play__meta,
	.video-play:not(.video-play--mobile):not(.video-play--large) .video-play__player
	{
		width: 121rem!important;
	}

	
}

@media (min-width: 1080px) and (max-width: 1920px) {

	.video-play:not(.video-play--mobile):not(.video-play--large) .video-play__meta,
	.video-play:not(.video-play--mobile):not(.video-play--large) .video-play__player
	{
		width: 84rem!important;
	}

	
}
}

@-moz-document domain("bilibili.tv") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.bstar-tab--solid .bstar-tab__item.active,
.bstar-tab--solid .bstar-tab__item:hover,
.comment-wrap__total,
.comment__operate__item,
.interactive__btn,
.scroll-container__item--active[data-v-03568ed5],
.timeline-tab__item--active,
.timeline-tab__item:hover,
.videocard-slot__playinfo-text.videocard-slot__playinfo-dm,
.videocard-slot__playinfo-text.videocard-slot__playinfo-vv,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.bstar-back-top--pc,
.bstar-cookie-info__desc,
.bstar-header__list-item,
.comment,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.live-list-title,
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.comment__time,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("bilibili.tv") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.trending-container.trending-container--pc,
.anime-timeline:not(.anime-timeline--mobile) .anime-timeline__subtitle[data-v-f640ef7a]:after
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.home-page,
.player-area-comp,
.bstar-header,
.bstar-sidebar,
.filters,
.interactive,
.trending-card[data-v-7926129b],
.trending-card__container.trending-card__ugc[data-v-7926129b]
{
	background: #070606!important;
	background-color: #070606!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.bstar-button--color-default,
.trending-card__viewmore-btn
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.bstar-button--color-default::before,
.trending-card__viewmore-btn::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.bstar-button--color-default:hover::before,
.trending-card__viewmore-btn:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.bstar-button--color-default.bstar-button--disabled
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
