{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.bstar-cookie-info
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.bstar-sidebar:not(.bstar-sidebar--mobile) .bstar-sidebar__item.active,
.bstar-header__list-item:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.back-top--position,
.bstar-back-top--mobile,
.bstar-back-top--pc,
.bstar-tab--solid .bstar-tab__item.active,
.bstar-tab--solid .bstar-tab__item:hover,
.scroll-container__item--active[data-v-03568ed5],
.timeline-tab__item--active,
.timeline-tab__item:hover
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("bilibili.tv") {
/* Image Settings */

.bstar-header__list-item,
.bstar-sidebar:not(.bstar-sidebar--mobile) .bstar-sidebar__item,
.card-section:not(.card-section--mobile) .section__list:not(.section__list--single) .section__list__item,
.comment-list__wrap__item,
.live-item,
.recommends:not(.recommends--mobile) .recommends__card,
.timeline-tab__item,
.trending-videolist li
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.bstar-header__list-item:hover,
.bstar-sidebar:not(.bstar-sidebar--mobile) .bstar-sidebar__item.active,
.bstar-sidebar:not(.bstar-sidebar--mobile) .bstar-sidebar__item:hover,
.card-section:not(.card-section--mobile) .section__list:not(.section__list--single) .section__list__item:hover,
.comment-list__wrap__item:hover,
.live-item:hover,
.recommends:not(.recommends--mobile) .recommends__card:hover,
.timeline-tab__item:hover,
.trending-videolist li:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

