style.css:
@-moz-document domain("www.kommersant.ru") {
font {
    color: inherit;
}

  body, .aside_news, .doc_header__name, .search_lenta__intro, .doc_board__name, .doc_board__canvas, .doc_board__nav_item.slider-item-current .doc_board__nav_name {
    background-color: #333;
    color: #FFFFFF;
  }
  @media only screen and (min-width: 1198px) {
    .bg_cover {
      background-color: #333;
    }
  }

  .most_readable__shadow, .ba__text--shadow, .doc_media .photo
  {
    background-color: #111;
  }

  .actual, .light_spot, .photd__name, .bqotd__name, .ba__subtitle--black, .ba__name--black, .subscription-popup-v2_push-popup
  {
    color: #EEEEEE;
    background-color: #111;
  }

  .photd__photo--angled:before
  {
    color: #EEEEEE;
    border-top-color: #111;
  }

  .actual__link, .b-article__text, .doc__text, .doc_media__text, .potd__main_name, .doc__thought, .comments-item-message, .document_inner_title, caption, .dotd__name, .doc ol:not([class]), .doc ul:not([class])
  {
    color: #EEEEEE;
  }

  .brand_color, a, .incut__more, .doc_footer__tumbler_text, .potd__button, .ba__tag--black .ba__link_more .link, .sharing, .icon_text
  {
    color: #00a5f3;
  }

  .incut
  {
    border-left-color: #00a5f3;
  }

  .potd__arrow
  {
    fill: #00a5f3;
  }

  .top_news_main__name, .top_news__name, .poll_chart__text
  {
    color: #FFFFFF;
  }

  .title_20, .title_15, .opinion_big__name, .opinion__name, .title_28, .vote__name, .potd__name, .most_readable__name
  {
    color: #dedede;
  }

  .opinion__tag, .title_12, .vote__text, .aside_news__name, .aside_news__subheader, .doc_header__subheader, .incut__note_name, .incut__note_text
  {
    color: #b6b6b6;
  }

  .section_name
  {
    color: #b6b6b6;
  }

  .stretch_photo img
  {
    opacity: .5;
  }

  .site_menu__lift, .footer
  {
    filter: invert(1);
  }

  .multimedia__scroll_indicator.multimedia__nav_button--next:after
  {
    box-shadow: -15px 0 30px 30px #333;
  }

  .doc--preview:after
  {
    box-shadow: 0 -10px 30px 30px #333;
  }

  .doc_media__caption_limit:after
  {
    box-shadow: 0 0 14px 10px #333;
  }
}
