style.css:
@-moz-document domain("aniplaylist.com") {
:root {
    --primary-bg-color: hsl(0, 0%, 9%);
    --primary-text-color: hsl(0, 0%, 94%);
    --card-bg-color: hsl(0deg 0% 14%);
    --border-color: hsl(0, 0%, 22%);
    --inputs-bg-color: hsl(0deg 0% 20%);
}

body,
#refinementsMenuFilters {
    background-color: var(--primary-bg-color);
    color: var(--primary-text-color);
}

*, :after, :before {
    border: 0 solid transparent;
}

.w-full.grow,
.bg-white {
    background-color: var(--card-bg-color);
}

.shadow-card,
.shadow-player,
.shadow-sm {
    box-shadow: none;
}

header {
    border-bottom: 1px solid var(--border-color);
}

.bg-gray-100 {
    background-color: hsl(0, 0%, 6%);
}

section.bg-sgreen {
    background-color: hsl(0, 0%, 11%);
}

.bg-gray-200 {
    background-color: hsl(0, 0%, 36%);
}

.text-slate-900,
.text-gray-700 {
    color: var(--border-color);
}

.text-slate-500 {
    color: hsl(215deg 16% 65%);
}

.bg-slate-400 {
    background-color: hsl(215deg 20% 30%);
}

.bg-slate-100 {
    background-color: hsl(0, 0%, 19%);
}

.bg-yellow-300 {
    color: hsl(210, 2%, 20%);
}

.text-gray-500,
.text-gray-600,
.text-gray-700,
.text-gray-800 {
    color: var(--primary-text-color);
}

.bg-applemusic {
    background-color: hsl(353deg 80% 60%);
}

.bg-sgreen {
    background-color: hsl(141deg 65% 42%);
}

input:not(:checked),
select,
textarea {
    background-color: var(--inputs-bg-color) !important;
    color: var(--primary-text-color) !important;
    border-color: var(--border-color) !important;
}

input::placeholder,
textarea::placeholder {
    color: hsl(215, 20%, 80%) !important;
}

#refinementsMenuFilters button.border {
    border: 1px solid hsl(0, 0%, 50%);
}

/* Hover states */

.hover\:bg-gray-100:hover {
    background-color: hsl(215deg 5% 22%);
}

.hover\:bg-gray-200:hover {
    background-color: hsl(220deg 13% 20%);
}

.focus\:border-gray-400:focus {
    color: hsl(215deg 16% 50%);
    border: 1px solid var(--border-color);
}

.group:hover .group-hover\:lg\:bg-gray-100 {
    background-color: hsl(0, 0%, 23%);;
}

.hover\:text-gray-700:hover {
    color: inherit;
}

.hover\:bg-sgreen-dark:hover {
    background-color: hsl(141deg 75% 35%);
}

.bg-applemusic:hover {
    background-color: hsl(353deg 80% 65%);
}

.hover\:text-slate-700:hover {
    color: hsl(215deg 16% 50%);
}

.hover\:bg-white:hover {
    background-color: hsl(0deg 0% 20%);
}
.group:hover .group-hover\:lg\:bg-gray-100,
.hover\:bg-gray-100:hover {
	background-color: #3a3a3a;
}
}
