@-moz-document domain("duckduckgo.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

selector,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

selector
{
	font-size: 2.5rem!important;
}

.footer__card__title,
.module--carousel-products.bing .module--carousel__item .module--carousel__body__pricing .module--carousel__body__price,
.module--carousel-autoads.bing .module--carousel__item .module--carousel__body__pricing .module--carousel__body__price,
.result__a,

[data-testid="result-title-a"]
{
	font-size: 1.85rem!important;
}

.result__timestamp,
.result__snippet,
.OgdwYG6KE2qthn9XQWFC
{
	font-size: 1.52rem!important;
}

.footer__card .footer__text,
.result__pagenum
{
	font-size: 1.2rem!important;
}

.module--carousel-products.bing .module--carousel__item .module--carousel__body__pricing, .module--carousel-autoads.bing .module--carousel__item .module--carousel__body__pricing
{
	line-height: 2.5rem!important;
}

selector
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.footer,
.footer--mobile,
.metabar__in,
.serp__results,
.tile-wrap .no-results,
.welcome,
.zci__detail,
.zci__main,
.zcm-wrap
{
    padding-left: 0;
}

.module--carousel-products.bing .module--carousel__item .module--carousel__body__pricing,
.module--carousel-autoads.bing .module--carousel__item .module--carousel__body__pricing
{
    bottom: 15px;
}

/* Margins 
_____________________________________________________________________________________________________*/

.results--main
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

selector
{
	padding: 1.2rem!important;
}

selector
{
	padding: 13px 26px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/


.c-base,
.c-detail,
.c-icon,
.c-info,
.c-list,
.c-product,
.forecast-wrapper .module--forecast .module__detail--hours__labels,
.is-link-style-exp.is-not-mobile-device .footer_cards,
.results--main,
.zci__main--answer,
.zci__main.has-aux,
.zcm-wrap--header
{
	max-width: none!important;
}

.footer__card__title
{
	width: auto!important;
}

selector
{
	width: 98%!important;
}

.results--main {
    width: 77%!important;
}

}

