style.css:
@-moz-document domain("www.windowscentral.com") {
body{
    background:black;
}
.flexi-page.responsive #content, .listingResult article, #slice-container-affiliateDisclaimerBar, .popular-box, .slice-container.trending-bar-container, .popular-box__label__tab--active, .locale-links, .listingResult, .flexi-page.responsive .news-article, #main, .table__wrapper--inbodyContent .table_body__data, body .hawk-nest .hawk-sticky-product-browser-cards-wrapper, body .hawk-nest .hawk-product-card-card-highlight {
  background: #333;
}
html, body, div, span, h1, h2, h3, p, a, img, small, i, ol, ul, li, form, label, article, aside, figure, figcaption, footer, header, nav, section, time, .listingResult .synopsis, .listingResult .article-name, .popular-box__article-list__link:link, .popular-box__label__tab--active, .wdyn-list-view-grid-toggle:checked ~ .wdyn-list-view .wdyn-list-view-label-list, .wdyn-list-view-grid-toggle:checked ~ .wdyn-list-view .wdyn-list-view-label-grid, .news-article .text-copy h3, .news-article .text-copy h2, .wdyn-list-view-label-grid, .wdyn-list-view-label-list, .news-article > header .strapline, article .credit, #article-body figure .credit, .flexi-page #article-body a:not([class*="hawk-"]), .widget-home-carousel .whc-carousel .whc-item-title, .flexi-page .widget-dynamic-carousel-rounded.widget .whc-carousel .whc-item-dek, .flexi-page #article-body a:not([class*="hawk-affiliate-link"]):not(.view-deal):not([class*="hawk-link"]){
  color: #fff;
}
.listingResult a:hover, .feature-block.top-featured .feature-block-item-wrapper a:hover, .listingResult a:hover:hover, .feature-block.top-featured .feature-block-item-wrapper a:hover:hover, .listingResult a:visited:hover, .feature-block.top-featured .feature-block-item-wrapper a:visited:hover, .listingResult a:hover .article-name, .feature-block.top-featured .feature-block-item-wrapper a:hover .article-name, .feature-block.top-featured .feature-block-item-wrapper a:hover figcaption, .listingResult a:hover:hover .article-name, .feature-block.top-featured .feature-block-item-wrapper a:hover:hover .article-name, .feature-block.top-featured .feature-block-item-wrapper a:hover:hover figcaption, .listingResult a:visited:hover .article-name, .feature-block.top-featured .feature-block-item-wrapper a:visited:hover .article-name, .feature-block.top-featured .feature-block-item-wrapper a:visited:hover figcaption, .affiliate-disclaimer__copy a, .popular-box__article-list__number-text,a:visited:hover, .rating:not(.slice-container) .icon-star::before {
  color: #ce0e5f;
}
.top-featured .feature-block-item, .feature-block.top-featured .feature-block-item-wrapper, .newsletter-signup, .newsletter-signup > .separator-heading, .trending__wrapper, #articleTag.box .tag, .list-text-links .listingResult .content, .refine-filters-facets, .widget-dynamic-listin, .widget-drilldown .dropdown > ul ul, .sticky-nav.left-rail-nav .sticky-nav__scroll-wrapper,.table__wrapper--inbodyContent .table__body__row:nth-child(2n) .table_body__data, .table__wrapper--inbodyContent .table__head__heading, #PDI_container11241057 .pds-box, body .hawk-nest .hawk-product-card-card, body .hawk-nest .hawk-load-more-container, body .hawk-nest .hawk-grid-items-review {
  background-color: #666;
}
.more-about__cardItems{
  background-color: #666 !important;
}
.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(2), .feature-block.top-featured .feature-block-item-wrapper:nth-of-type(3), .feature-block.top-featured .feature-block-item-wrapper:nth-of-type(1){
    border-bottom:solid 15px #333;
}
.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(2){
    border-right:solid 15px #333;
}
#articleTag.box .tag:hover, body .hawk-nest .hawk-product-card-card-number-badge{
    background:#ce0e5f;
}
.popular-box__article-list__link:visited{
    color: #666;
}
.flexi-page .widget-dynamic-carousel-rounded.widget img{
    border: 2px solid var(--flexi-brand_color_1,#ddd);
}
body .hawk-nest .hawk-deal-widget-image-container {
  z-index: 5;
}
body .hawk-nest .hawk-sticky-product-browser-graduated-filter-right  {
  background-image: linear-gradient(90deg, rgba(2,0,36,0) 0%, rgb(51, 51, 51) 100%);
}
body .hawk-nest .hawk-sticky-product-browser-graduated-filter-left {
  background-image: linear-gradient(270deg, rgba(2,0,36,0) 0%, rgb(51, 51, 51) 100%);
}
.rating:not(.slice-container)::before{
background:#0000;
}
body .hawk-nest .hawk-grid-item-main-review, body .hawk-nest .hawk-grid-item-main-review-wide {
  z-index: 1;
}
body .hawk-nest .hawk-lazy-image-stickyproductbrowser{
    mix-blend-mode: normal;
}
}
