    --clr: 0;
    --clr2: 0%;
    --clr_link: 25;
    --clr_link2: 100%;
    --clr_link3: 60%;
    --clr_num: 120,40%;
    --theme_default_bg: hsl(var(--clr),var(--clr2),15%);
    --theme_default_glassbg: hsla(var(--clr),var(--clr2),20%,.8);
    --theme_default_postbg: hsl(var(--clr),var(--clr2),20%);
    --theme_default_postbg2: hsl(var(--clr),var(--clr2),23%);
    --theme_default_postbghighlight: hsl(var(--clr_link),calc(var(--clr_link2) / 3),20%);
    --theme_default_shadowhighlight: hsl(var(--clr_link),var(--clr_link2),var(--clr_link3),.6);
    --theme_default_spectext: hsl(289,30%,60%);
    --theme_default_red: hsl(0,100%,65%);
    --theme_default_yellow: hsl(45,90%,70%);
    --theme_default_green: hsl(120,60%,60%);
    --theme_default_mod: hsl(211,44%,70%);
    --theme_default_sage: hsl(0,100%,70%);
    --theme_default_ophui: hsl(120,40%,60%);
    --theme_default_postnum: hsl(var(--clr_num),60%);
    --theme_default_postnumhover: hsl(var(--clr_num),70%);
    --theme_default_postnumbg: hsla(var(--clr_num),60%,.1);
    --theme_default_border: hsla(var(--clr),var(--clr2),100%,.15);
    --theme_default_text: hsl(var(--clr),var(--clr2),85%);
    --theme_default_alttext: hsl(var(--clr),var(--clr2),70%);
    --theme_default_alttext2: hsl(var(--clr),var(--clr2),50%);
    --theme_default_link: hsl(var(--clr_link),var(--clr_link2),var(--clr_link3));
    --theme_default_hover: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 10%));
    --theme_default_title: hsl(210,45%,70%);
    --theme_default_drag: hsl(var(--clr),var(--clr2),38%);
    --theme_default_spoiler: hsl(var(--clr),var(--clr2),30%);
    --theme_default_icon: hsl(var(--clr),var(--clr2),50%);
    --theme_default_btntext: hsl(var(--clr),var(--clr2),85%);
    --theme_default_altbtntext: hsl(var(--clr),var(--clr2),15%);
    --theme_default_btnbg: hsl(var(--clr),var(--clr2),20%);
    --theme_default_btnborder: hsla(var(--clr),var(--clr2),100%,.15);
    --theme_default_altbtnbg: hsl(var(--clr),var(--clr2),15%);
    --theme_default_btnbghover: hsl(var(--clr),var(--clr2),23%);
    --theme_default_altbtnbghover: hsl(var(--clr),var(--clr2),13%);
    --theme_default_inputtext: hsl(var(--clr),var(--clr2),85%);
    --theme_nmswitch: hsl(var(--clr_link),100%,60%);
    --theme_mpborder: hsl(var(--clr),var(--clr2),20%);
    --theme_mubtn: hsl(var(--clr),var(--clr2),25%);
    --theme_sbthumb: hsl(var(--clr),var(--clr2),40%);
    --theme_sbthumbhover: hsl(var(--clr),var(--clr2),50%);
    --theme_sbtrack: hsl(var(--clr),var(--clr2),15%);
    --theme_default_shadow: hsla(var(--clr),var(--clr2),0%,.15);
    --theme_default_shadow2: hsla(var(--clr),var(--clr2),0%,.25);
}
html[data-theme=nightmode] {
    --theme_text: hsl(var(--clr),var(--clr2),85%);
    --theme_link: hsl(var(--clr_link),var(--clr_link2),var(--clr_link3));
    --theme_hover: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 10%));
    --theme_bg: hsl(var(--clr),var(--clr2),15%);
    --theme_postbg: hsl(var(--clr),var(--clr2),20%);
    --theme_postbg2: hsl(var(--clr),var(--clr2),23%);
    --theme_blockbg: hsl(var(--clr),var(--clr2),20%);
    --theme_border: hsla(var(--clr),var(--clr2),100%,.15);
    --theme_logo_text: var(--theme_default_link);
    --theme_logo_st2: #1B1B1B;
    --theme_logo_text_1: #FFA90A;
    --theme_logo_text_2: #FFA00D;
    --theme_logo_text_3: #FE8616;
    --theme_logo_text_4: #FE6E1F;
    --theme_inputbg: hsl(var(--clr),var(--clr2),15%);
    --theme_inputtext: hsl(var(--clr),var(--clr2),85%);
    --theme_inputborder: hsla(var(--clr),var(--clr2),100%,.15);
    --theme_btnbg: hsl(var(--clr),var(--clr2),20%);
    --theme_shadow: hsl(var(--clr),var(--clr2),25%);
    --theme_title: hsl(210,45%,70%);
}

html {font-family: 'IBM Plex Sans', sans-serif; font-size: 16px;}
html[data-theme=nightmode] {color-scheme: dark;}
body {font-family: 'IBM Plex Sans', sans-serif !important; font-size: 16px;}

@media only screen and (min-width: 480px) {
::-webkit-scrollbar {background: var(--theme_default_bg); height: 14px; width: 14px;}
::-webkit-scrollbar-track {background-clip: padding-box; border: solid transparent; border-width: 0;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background-color: var(--theme_sbthumb); background-clip: padding-box; border: 2px solid transparent; border-radius: 4px;}
::-webkit-scrollbar-thumb:hover {background-color: var(--theme_sbthumbhover);}
::-webkit-scrollbar-thumb:active {background-color: var(--theme_sbthumbhover);}
::-webkit-scrollbar-corner {background: var(--theme_default_bg);}
}

*::selection {background: var(--theme_default_shadowhighlight); color: var(--theme_default_altbtntext);}
*::-moz-selection {background: var(--theme_default_shadowhighlight); color: var(--theme_default_altbtntext);}
*::-webkit-selection {background: var(--theme_default_shadowhighlight); color: var(--theme_default_altbtntext);}

a {color: var(--theme_default_link); transition: color .1s;}
a:hover {color: var(--theme_default_hover);}
.orange {color: var(--theme_default_link);}
a[href*="youtube.com"] {border-bottom: none;}
a[href$="sage"], a[href$="Sage"], a[href$="SAGE"] {color: var(--theme_default_sage);}

.icon {width: 16px; height: 16px; vertical-align: -3px;}
.icon:hover {filter: none;}

/*===== Main Page =====*/
.header__sub {color: var(--theme_default_text);}

.main__meta {background: var(--theme_default_postbg); border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_default_shadow); font-family: inherit; padding: 15px 20px;}
.buttons {padding: 15px 0 0;}
.buttons__button {box-shadow: 0 0 0 1px var(--theme_default_btnborder); color: var(--theme_default_text); transition: .1s;}
.buttons__button:hover {background: var(--theme_default_btnbghover); color: var(--theme_default_text); filter: none;}

.main__block.boards {background: var(--theme_default_postbg); border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_default_shadow);}
.boards__col {padding: 15px 5px 0px 20px;}

.main__block.news {background: var(--theme_default_postbg); border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_default_shadow);}
.main__title {line-height: 2.5;}
.news__data {padding: 10px 20px;}
li.news__item {text-align: left;}

.main__block.news + .main__block {background: var(--theme_default_postbg); border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_default_shadow);}
.ui-widget {font-family: inherit;}
.ui-widget-header {padding: 0 8px;}
#tabbed li {background: var(--theme_default_postbg); border-bottom: 1px solid var(--theme_border); border-radius: 6px; font-weight: bold; transition: .1s;}
#tabbed li.ui-tabs-active {background: var(--theme_default_bg); margin-bottom: 0; padding-bottom: 2px;}
.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited, a.ui-button, a:link.ui-button, a:visited.ui-button, .ui-button {color: var(--theme_default_text);}
.ui-widget-content {border: none;}
.summary__row:nth-of-type( even ) {background: var(--theme_default_postbg2);}
.ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {font-family: inherit;}

/*===== Rules, Ads, Passcode =====*/
.box {background: var(--theme_default_postbg); border: none; border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_default_shadow);}
.box__header {font-family: inherit}
.box_type_nf {background: none; box-shadow: none;}
.nf__img {border: none; box-shadow: 0 0 0 1px var(--theme_default_border);}

.body-stickers .sticker {background: var(--theme_default_postbg); border-color: var(--theme_default_border); border-radius: 6px; box-shadow: 0 1px 3px var(--theme_default_shadow); color: var(--theme_default_text);}
.body-stickers .sticker-info {border-color: var(--theme_default_border);}
.body-stickers .sticker-info {background: none; border-top: none;}

.box ~ .box[id*="post"] {margin-top: 8px;}
.box[id*="post"] .box__data {padding: 4px;}
.box[id*="post"] .post-details {color: var(--theme_default_alttext2); font-size: 0.9em; padding: 4px 4px 0 16px;}
.box[id*="post"] .post-details .reflink a {color: var(--theme_default_alttext);}
.box[id*="post"] .post-details .reflink a:hover {color: var(--theme_default_link);}
.box[id*="post"] .post-message {margin: 16px;}

.cloudheader {background: var(--theme_default_postbg2); border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border);}
.cloudheader .cloudoption {color: var(--theme_default_icon);}
.cloudheader .cloudoption:hover {color: var(--theme_default_alttext);}
.board__speed {color: var(--theme_default_alttext2);}

/*===== Catalog =====*/
.header {color: var(--theme_default_text);}
.header__meta {background: var(--theme_default_postbg); border: none; box-shadow: 0 0 0 1px var(--theme_default_border); font-size: .9em; padding: 6px; position: fixed; height: 46px; top: 0; left: 0; right: 0; z-index: 1000;}

.header__logo svg {padding-top: 50px; width: 180px !important;}
.st1 {fill: var(--theme_default_link);}
.header__title {margin: 0;}

.ctlg {margin: 0 auto; max-width: 1320px;}
.ctlg__thread {border-radius: 6px; -moz-border-radius: 6px; -webkit-border-radius: 6px; margin: 5px;}
.ctlg__thread_abs {border: none; box-shadow: 0 0 0 1px var(--theme_default_border), 0 6px 16px var(--theme_default_shadow); margin: 0; margin-top: -20px; margin-left: -70px;}
.ctlg__img {padding: 10px 10px 0 10px;}
.ctlg__img img {border-radius: 4px; height: auto; width: auto;}
.ctlg__meta {background: none; color: var(--theme_default_alttext2); padding: 2px 6px;}
.ctlg__meta, .ctlg__title {font-family: inherit; font-size: .9rem;}
.ctlg__title {color: var(--theme_default_text);}
.mod {color: var(--theme_default_mod);}

/*===== Header =====*/
.cntnt__header {margin-left: 290px;}

@media only screen and (min-width: 480px) {
    .header__opts {background: none; border: none; display: block; flex-direction: column; padding: 5px; position: fixed; top: 45px; left: 0; width: 250px; z-index: 100; transform: none; height: 210px; overflow: auto; scrollbar-color: var(--theme_sbthumb) var(--theme_sbtrack); scrollbar-width: thin;}
    .header__opts::-webkit-scrollbar {width: 4px; height: 4px;}
