    .replypage .tn:first-of-type .button_mob:first-child {background: var(--theme_default_postbg); border: none; border-radius: 50%; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_default_shadow); display: block !important; font-size: 0; padding: 7px 11px !important; position: fixed; height: 46px; width: 46px; bottom: 72px; left: 20px; z-index: 201;}
    .replypage .tn:first-of-type .button_mob:first-child:hover {background: var(--theme_default_btnbghover);}
    .replypage .tn:first-of-type .button_mob:first-child::before {color: var(--theme_default_alttext); content: "arrow_back"; font: bold 24px/32px 'Material Icons', sans-serif;}
    
    .button_mob[href="#top"], .button_mob[href="#bottom"] {display: none !important;}
    
    .tn .button_mob[href$="catalog.html"] {display: none !important;}
    .tn:first-of-type .button_mob[href$="catalog.html"] {border: none; border-radius: 0; display: block !important; font-size: 0; position: fixed; z-index: 900; padding: 0 !important; height: 55px; width: 20%; bottom: 0; left: 40%; text-align: center;}
    .button_mob[href$="catalog.html"]::before {color: var(--theme_default_alttext) !important; content: "apps"; font: 24px/55px 'Material Icons', sans-serif;}
    .button_mob:hover {background: var(--theme_default_btnbghover);}
    
    .button_mob[onclick*="updateThread(); return false;"] {display: none !important;}
    .js-update-thread::before {background: var(--theme_default_postbg); border-radius: 25px; color: var(--theme_default_alttext); padding: 11px; top: unset; bottom: 72px; left: 80px; z-index: 202; transform: none; transition: .1s;}
    .js-update-thread:hover::before {background: var(--theme_default_postbg2); transform: none;}
    
    .tn__item.desktop {display: block !important;}
    #js-hidefm, #js-posts + .tn .autorefresh {display: none !important;}
    span.autorefresh {background: var(--theme_default_postbg); border-radius: 25px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_default_shadow); font-size: 0; padding: 11px 0 11px 46px; position: fixed; height: 46px; left: 80px; bottom: 72px; z-index: 201; margin-right: 0 !important;}
    span.autorefresh input[type="checkbox"] {visibility: hidden; margin-left: -24px; transition: .2s;}
    .autorefresh span {font-size: .9rem; color: var(--theme_default_text); margin: 0; vertical-align: -3px;}
    span.autorefresh input[type="checkbox"]:checked + .autorefresh-countdown {margin-right: 10px;}
    .js-update-thread:focus + span.autorefresh input[type="checkbox"] {visibility: unset; margin-left: 8px; margin-right: 10px;}
    
    .tn span[title] {display: none;}
    #js-posts + .tn span[title] {display: inline;}
    
    .thread_hidden div {width: 100vw; padding: 0 10px;}
    
    .post_type_oppost {border-radius: 0; margin: 0;}
    .oppost-options-mob {margin: 0; padding: 5px 10px;}
    .post_type_reply {border-radius: 0; -moz-border-radius: 0; -webkit-border-radius: 0; margin: 0;}
    .post_type_watched:before, .post_type_replied:before {top: 0; bottom: 0; left: 0;}
    
    .post__details {padding: 8px 30px 2px 10px !important; position: relative; overflow: unset;}
    .post__detailpart.desktop {display: flex !important;}
    .post__detailpart.desktop a {display: none;}
    .post__title {font-size: 1em;}
    .post__btn_type_favorite {display: block !important;}
    .post__btn_type_menu {display: block !important; position: absolute; right: 5px; top: 5px; transform: rotate(90deg);}
    
    .post__file-attr a {display: inline !important;}
    .post__file-attr svg {display: inline !important;}
    
    #ABU-select {left: unset !important; right: 35px;}
    .modal a {font-size: 1.1em; padding: 6px 12px;}
    
    .post__message {font-size: .95rem; padding: 8px 16px;}
    .post__images_type_single {float: none;}
    .post__images_type_single + .post__message {padding: 8px 16px 8px;}
    
    /*===== Quick Reply =====*/
    .qr {border-radius: 0; width: 100%; height: calc(100vh); padding: 10px; overflow: auto; left: 0 !important; top: 0 !important;}
    .qr__header {font-size: 1.4em; padding: 2px 6px 10px 6px;}
    .qr__close {padding: 6px; margin: -6px;}
    .qr .postform__input_type_m:nth-last-child(2) {margin-right: 4px;}
    
    /*===== Bookmarks =====*/
    .bb {border-radius: 6px; border: none; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_default_shadow); font-size: .9rem; display: block !important; bottom: 70px; left: calc(-100vw); right: unset; width: calc(100vw - 20px); transition: left .2s; z-index: 900;}
    .bb:hover {left: 10px;}
    .bb:hover .bb__header::before {background: var(--theme_default_btnbghover);}
    .bb__header {font-size: 1.1rem; margin-right: 35px; padding: 10px 10px 5px;}
    .bb__header::before {background: var(--theme_default_btnbg); color: var(--theme_default_alttext); content: "star"; font: 24px/55px 'Material Icons', sans-serif; position: fixed; left: 20%; bottom: 0; height: 55px; width: 20%; text-align: center; transition: .1s;}
    .bb__header_new::before {color: var(--theme_default_link);}
    .bb__update {top: 12px; right: 12px;}
    .bb__panels {display: block !important;}
    .bb__panel {padding: 5px 10px;}
    .fav__fa {float: right; margin-left: 5px; margin-right: 0;}
    .bb__row {margin: 6px 0;}
    .bb__row a span:first-child {font-weight: normal; margin-right: 4px; opacity: .7;}
    .bb__row a span:nth-child(2), .bb__row a span:nth-child(3) {display: none;}
    .bb__row .fav__new {background: var(--theme_default_alttext2); border-radius: 4px; color: var(--theme_default_altbtntext); float: right; margin-left: 5px; margin-right: 0; min-width: unset; padding: 0 4px;}
    .bb__row .fav__new:last-of-type {background: var(--theme_default_link); border-radius: 4px; color: var(--theme_default_altbtntext);}
    .bb__bstitle {max-width: calc(100% - 90px);}
    .bb__bstitle + span {float: right; width: 90px; text-align: right; word-spacing: -2px;}
    .bb__bstitle + span svg {float: right; margin-left: 5px;}
    .bb__footer {font-size: 0;}
    .bb__footer div {display: flex; flex-wrap: wrap; align-items: center;}
    .bb__footer div::before {content: "Следить за:"; font-size: .9rem; padding-right: 4px;}
    .bb__footer label {font-size: 0; display: inline-block; margin-top: 4px;}
    #qr-fav-autowatchmyposts {display: flex; width: unset;}
    #qr-fav-autowatchmyposts::after {color: var(--theme_default_text); content: "постами"; font-size: .9rem; padding: 0 4px;}
    #qr-fav-autowatchmythreads {display: flex; width: unset;}
    #qr-fav-autowatchmythreads::after {color: var(--theme_default_text); content: "тредом"; font-size: .9rem; padding: 0 4px;}
    .bb__tabs {margin: 10px;}
    .bb__tab {background: none; border-radius: 6px; margin-right: 5px; padding: 6px 12px;}
    .bb__tab_active {background: var(--theme_default_bg); border-top-color: var(--theme_default_border);}
    
    /*===== Pop-Up Shit =====*/
    .warningbox {border-radius: 0; box-shadow: none ; top: 0; left: 0; right: 0; bottom: 0; margin: 0; width: unset;}
    .mv__main:before, .mv__main:after {opacity: 1;}
    .mv__main:before {top: 4px;}
    .mv__main:after {background: var(--theme_default_glassbg); box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_default_shadow); color: var(--theme_default_text); text-shadow: none; position: fixed; top: 0; padding: 4px 8px;}
    
    #plashque, .plashque {left: 5px;}
    .plashque_mob {display: none; background-color: var(--theme_default_glassbg);}
    .plashque__close {font-size: 0;}
    .plashque__close::before {color: var(--theme_default_alttext); content: "close"; font: 36px/1 'Material Icons', sans-serif;}
    
    .alert {pointer-events: none;}
    
    .na {background: var(--theme_default_icon); border-radius: 50%; display: block !important; height: 46px; width: 46px; left: unset; right: 20px; padding: 13px 15px;}
    .na__icon {fill: var(--theme_default_altbtntext) !important;}
    .na_type_up {top: unset; bottom: 100px;}
    .na_type_down {bottom: 100px;}
    .na_type_up[style="display: none;"], .na_type_down[style="display: none;"] {display: none !important;}
    
    /*===== Footer =====*/
    .footer {margin: 0 0 110px !important;}
    .footer__info {display: none;}
    
    /*===== Dollchan =====*/
    
    .de-runned .bb, .de-runned .tn:first-of-type .button_mob[href$="catalog.html"] {display: none !important;}
    .de-runned .header__opts, .de-runned form[action="/user/search"] {bottom: 95px;}
    
    /* Header */
    .de-runned .cntnt__header::before {height: 80px;}
    .de-runned .header__opts::before {color: var(--theme_default_text); font: bold 24px/40px 'Material Icons', sans-serif; width: 40px; bottom: 40px; left: 0;}

    /* Windows */
    .de-win-head {font-size: 1rem; padding: 12px 55px 12px 12px;}
    .de-win-buttons {margin-right: 6px;}
    .de-win-head svg {height: 24px; width: 24px; margin: -4px 2px 0 0 !important;}
    #de-win-reply .de-win-head {padding: 12px 85px 12px 12px;}
    
    #de-cfg-bar {overflow: auto; padding-bottom: 8px;}
    .de-cfg-tab {font-size: .9em;}

    /* Dock Panel */
    #de-panel {background: none; border-radius: 0; box-shadow: none; bottom: 0; width: calc(100% - 40px);}
    #de-panel-logo {display: none;}
    #de-panel-buttons {border: none; display: flex !important;}
    .de-panel-svg {width: 30px; height: 30px;}
    #de-panel-logo, .de-panel-logo-svg, .de-panel-button {width: 40px; height: 40px; padding: 5px;}
    #de-panel-goback, #de-panel-gonext, #de-panel-refresh, #de-panel-goup, #de-panel-godown {display: none;}
    #de-panel-info {font-size: 1em;}
    
    /* Confg */
    #de-win-cfg.de-win, #de-win-fav.de-win, #de-win-hid.de-win, #de-win-vid.de-win {border-radius: 0; box-shadow: none; height: calc(100vh - 80px); width: 100% !important;}
    #de-win-hid {max-width: unset;}
    #de-win-cfg.de-win-fixed, #de-win-fav.de-win-fixed, #de-win-hid.de-win-fixed, #de-win-vid.de-win-fixed {bottom: 95px !important; width: calc(100% - 20px) !important; margin: 0 10px;}
    
    /*#de-popup-edit-cfg {width: 100% !important;}*/
    .de-editor {width: 90vw;}
    
    /* Reply */
    .de-parea #de-pform .postform {border-radius: 0;}
    #de-win-reply {border-radius: 0;}
    #de-win-reply.de-win-inpost {margin: 0; width: 100% !important;}
    #de-win-reply.de-win {border-radius: 0; box-shadow: none; left: 0; bottom: 80px;}
    #de-win-reply.de-win #shampoo.de-textarea {width: 100% !important;}
    
    /* Thread */
    .de-runned form[action="/user/search"]::after {color: var(--theme_default_text); font: bold 24px/40px 'Material Icons', sans-serif; width: 40px; left: 0;}
    
    .replypage .de-thr-updater-link::after {background: var(--theme_default_postbg); border-radius: 50%; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_default_shadow); color: var(--theme_default_alttext); font: 24px/46px 'Material Icons', sans-serif; padding: 0 11px; left: 80px; top: unset; bottom: 95px;}
    .replypage #de-updater-count {background: var(--theme_default_postbg); border-radius: 6px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_default_shadow); color: var(--theme_default_alttext); padding: 3px 6px; width: unset; top: unset; bottom: 105px; left: 140px; pointer-events: unset; z-index: 201;}
    
    .replypage.de-runned .tn:first-of-type .button_mob:first-child {bottom: 95px;}
    .de-runned #up-nav-arrow {border-radius: 25px 25px 0 0; bottom: 166px; z-index: 200;}
    .de-runned #down-nav-arrow {border-radius: 25px 25px 0 0; bottom: 120px; z-index: 200;}
    
    .de-runned .js-post-saveimg.desktop, .de-runned .js-post-findimg.desktop {display: none !important;}
    
    .de-runned .post__message {padding-left: 0;}
    
    /* Quick Reply */
    .de-runned .qr {box-shadow: none; height: calc(100vh - 80px);}
}
}
