/* ==UserStyle==
@name           YouTube Early 2018 Material Layout
@namespace      example.com
@version        1.2.0
@description    This userstyle brings back the late 2017 - early 2018 YouTube material layout.
@author         Magma_Craft
==/UserStyle== */

@-moz-document domain("youtube.com") {
/* Topbar, sidebar, button and icons adjustments */
#masthead-container.ytd-app::after {
  margin-bottom: -5px;
  box-shadow: inset 0px 4px 8px -3px rgba(17, 17, 17, .06)
}
    
ytd-app[hide-header-shadow] #masthead-container.ytd-app::after {
  opacity: 1;
}

#guide-inner-content.ytd-app {
  background-color: rgba(0, 0, 0, .04);
  letter-spacing: 0px;
}

ytd-mini-guide-renderer.ytd-app {
  display: none;
}

ytd-guide-entry-renderer[active] {
    background-color: transparent ;
}

.guide-icon.ytd-guide-entry-renderer {
  color: #909090;
}

#center.ytd-masthead {
  flex: 1;
  margin-left: 12px;
  margin-right: 12px;
}

ytd-searchbox.style-scope.ytd-masthead {
  max-width: 656px;
  height: 32px;
}

form#search-form.style-scope.ytd-searchbox {
  height: 32px;
}

button#search-icon-legacy.style-scope.ytd-searchbox {
  width: 65px;
  height: 32px;
}

button#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
  max-width: 20px;
  max-height: 20px;
  opacity: 0.6;
}

#voice-search-button.ytd-masthead {
  display: none;
}

#container.ytd-searchbox {
  margin-left: 0px;
}

#container.ytd-searchbox > [slot=search-input] input {
  box-sizing: border-box;
  padding: 1px 6px;
  margin: 0;
}

ytd-searchbox[has-focus] #search-icon.ytd-searchbox {
  display: none;
}

ytd-searchbox[has-focus] #container.ytd-searchbox {
  padding: 2px 6px 2px 6px;
  margin: 0;
}
    
div.sbqs_c::before {
  display: none;
}

yt-icon.ytd-logo {
  width: 80px;
  height: 24px;
}

#guide-icon.ytd-masthead {
  opacity: 0.6;
}

yt-icon.style-scope.ytd-topbar-menu-button-renderer {
  opacity: 0.6;
}

tp-yt-paper-button.style-scope.ytd-button-renderer.style-suggestive.size-small {
  border-color: transparent;
  letter-spacing: 0;
  margin-right: 4px;
  margin-left: -4px;
}

ytd-button-renderer[is-paper-button] yt-icon.ytd-button-renderer {
  display: none;
}
#button.ytd-button-renderer yt-icon.ytd-button-renderer+yt-formatted-string.ytd-button-renderer {
  color: #ff0000;
}

tp-yt-paper-button.style-scope.ytd-button-renderer.style-suggestive.size-default {
  border: none;
  padding-top: 0px;
  padding-bottom: 0px;
  padding-right: 0px;
  padding-left: 0px;
  margin-left: -15px;
  letter-spacing: 0px;
}

.yt-spec-icon-badge-shape__icon {
  opacity: 0.6;
}

.yt-spec-icon-badge-shape--type-notification .yt-spec-icon-badge-shape__badge {
  background-color: #ff0000;
}

div.yt-spec-icon-badge-shape__badge {
  left: 14px;
  min-width: 10px;
  max-width: 10px;
  height: 18px;
  border: 0px;
  font-size: 10px;
  line-height: 18px;
}

ytd-multi-page-menu-renderer {
  letter-spacing: 0px;
  border: 0px;
  box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.4);
}

ytd-active-account-header-renderer.style-scope.ytd-multi-page-menu-renderer {
  background-color: rgba(128, 128, 128, .14);
}

yt-icon.style-scope.ytd-badge-supported-renderer {
  opacity: 0.598
}

ytd-button-renderer.style-destructive[is-paper-button] {
  letter-spacing: 0px;
  background-color: #ff0000;
}

tp-yt-paper-button.style-scope.ytd-button-renderer.style-primary.size-default {
  letter-spacing: 0px;
  background-color: #1e88e5;
}

#label.yt-dropdown-menu {
  opacity: 0.8;
}

#icon-label.yt-dropdown-menu {
  letter-spacing: 0px;
}

#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer.style-scope[title="Shorts"] {
  display: none;
}

#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer.style-scope[title="Fashion & Beauty"] {
  display: none;
}

#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer.style-scope[title="Learning"] {
  display: none;
}

#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer.style-scope[title="Report history"] {
  display: none;
}

#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer.style-scope[title="Downloads"] {
  display: none;
}

#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer.style-scope[title="Podcasts"] {
  display: none;
}

/* Homepage, search and channel adjustments */
ytd-feed-filter-chip-bar-renderer.style-scope.ytd-rich-grid-renderer {
  display: none;
}

#contents.ytd-rich-grid-renderer {
  max-width: 1284px !important;
  margin-top: 10px;
  margin-bottom: 10px;
  margin-left: 0px;
  margin-right: 0px;
  padding-left: 4%;
    --ytd-rich-grid-items-per-row: 6 !important;
}

#contents > ytd-rich-grid-row,
#contents > ytd-rich-grid-row > #contents {
  display: contents;
}

ytd-rich-section-renderer
{
  display: none;
}

#grid-container.ytd-expanded-shelf-contents-renderer > *.ytd-expanded-shelf-contents-renderer
{
  width: 50% !important;
}


ytd-rich-item-renderer.style-scope.ytd-rich-grid-row {
  margin-left: 0px;
  margin-right: 4px;
}

ytd-rich-item-renderer {
  width: 210px;
}

#content.ytd-rich-item-renderer {
  width: 210px;
}

ytd-thumbnail.ytd-rich-grid-media {
  width: 210px;
  max-height: 144.5px;
}

#avatar-link.ytd-rich-grid-media {
  display: none;
}

#video-title.ytd-rich-grid-media {
  font-size: 14px;
  line-height: 1.7rem;
}

ytd-rich-grid-media[mini-mode] #video-title.ytd-rich-grid-media {
  font-size: 14px;
  line-height: 1.7rem;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string {
  font-size: 14px;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:visited {
  font-size: 14px;
}

ytd-video-meta-block[rich-meta][mini-mode] #metadata-line.ytd-video-meta-block {
  font-size: 13px;
  line-height: 1.7rem;
}

ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block {
  font-size: 13px;
  line-height: 1.7rem;
}

#rich-shelf-header.ytd-rich-shelf-renderer {
  margin-top: 24px;
  margin-bottom: 24px;
  margin-left: 0px;
  margin-right: 0px;
}

#title.ytd-rich-shelf-renderer {
  font-size: 16px;
  font-weight: 500;
}

ytd-rich-section-renderer[align-within-rich-grid] #content.ytd-rich-section-renderer {
  margin: 0;
    --ytd-rich-grid-items-per-row: 6 !important;
}

ytd-rich-shelf-renderer.style-scope.ytd-rich-section-renderer {
  border-top-width: 1px;
}

#contents.ytd-rich-shelf-renderer ytd-rich-item-renderer.ytd-rich-shelf-renderer {
  margin-left: 6px;
  margin-right: -2px;
}

ytd-button-renderer.style-scope.ytd-rich-shelf-renderer.style-opacity.size-default {
  border-bottom-width: 1px; 
}

#metadata.ytd-channel-renderer {
  font-size: 13px;
}

#description.ytd-channel-renderer {
  font-size: 13px;
}

.metadata-snippet-container.ytd-video-renderer {
  font-size: 13px;
}

a.channel-link.yt-simple-endpoint.style-scope.ytd-channel-renderer {
  margin-left: -114px;  
}

ytd-video-renderer[is-search] ytd-thumbnail.ytd-video-renderer {
  max-width: 246px;
  max-height: 138px;
}

yt-img-shadow.style-scope.ytd-video-renderer.no-transition {
  display: none;
}

ytd-video-renderer[is-search] #channel-info.ytd-video-renderer {
  padding-top: 5px;
  padding-bottom: 5px;
  margin-left: -8px;
}

ytd-playlist-renderer[is-search] ytd-playlist-thumbnail.ytd-playlist-renderer {
  max-width: 246px;
}

ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer {
  line-height: 1.7rem;
}

ytd-grid-video-renderer:not([rich-meta]) #metadata-line.ytd-grid-video-renderer {
  font-size: 13px;
}

#details-container.ytd-channel-about-metadata-renderer tr.ytd-channel-about-metadata-renderer {
  font-size: 13px;
}

#link-list-container.ytd-channel-about-metadata-renderer a.yt-simple-endpoint.ytd-channel-about-metadata-renderer {
  font-size: 13px;
}

/* Watch layout adjustments */
div.style-scope.yt-chip-cloud-renderer {
  display: none;
}

div.style-scope.ytd-watch-flexy {
  background-color: #fff;
}

div.html5-video-player.ytp-transparent.ytp-exp-bottom-control-flexbox.ytp-exp-ppp-update.ytp-fit-cover-video.ytp-hide-info-bar.ytp-autonav-endscreen-cancelled-state.paused-mode.ytp-heat-map.ytp-large-width-mode {
  background-color: #000000;
}

div.html5-video-player.ytp-transparent.ytp-exp-bottom-control-flexbox.ytp-exp-ppp-update.ytp-fit-cover-video.ytp-hide-info-bar.ytp-autonav-endscreen-cancelled-state.ytp-heat-map.ytp-large-width-mode.playing-mode {
  background-color: #000000;
}

div.html5-video-player.ytp-transparent.ytp-exp-bottom-control-flexbox.ytp-exp-ppp-update.ytp-fit-cover-video.ytp-hide-info-bar.ytp-autonav-endscreen-cancelled-state.paused-mode.ytp-heat-map {
  background-color: #000000;
}

div.html5-video-player.ytp-transparent.ytp-exp-bottom-control-flexbox.ytp-exp-ppp-update.ytp-fit-cover-video.ytp-hide-info-bar.ytp-autonav-endscreen-cancelled-state.ytp-heat-map.playing-mode {
  background-color: #000000;
}

div.html5-video-player.ytp-transparent.ytp-exp-bottom-control-flexbox.ytp-exp-ppp-update.ytp-hide-info-bar.ytp-fit-cover-video.ytp-autonav-endscreen-cancelled-state.ytp-iv-drawer-enabled.paused-mode {
  background-color: #000000;
}

div.html5-video-player.ytp-transparent.ytp-exp-bottom-control-flexbox.ytp-exp-ppp-update.ytp-hide-info-bar.ytp-fit-cover-video.ytp-autonav-endscreen-cancelled-state.ytp-iv-drawer-enabled.playing-mode {
  background-color: #000000;
}

div.ytp-autonav-toggle-button-container {
  display: none
}

button.ytp-miniplayer-button.ytp-button {
  display: none;
}

#sponsor-button.ytd-video-owner-renderer, #purchase-button.ytd-video-owner-renderer, #analytics-button.ytd-video-owner-renderer {
  display: none;
}

ytd-button-renderer #button.ytd-button-renderer {
  letter-spacing: 0px;
}
 
#channel-name.ytd-video-owner-renderer {
  line-height: 1.6rem;
}
 
#owner-sub-count.ytd-video-owner-renderer {
  font-size: 13px;
}

span.date.style-scope.ytd-video-secondary-info-renderer {
  font-size: 13px;
}
 
#video-title.ytd-compact-video-renderer {
  line-height: 16px;
}
 
#channel-name.ytd-video-meta-block {
  font-size: 13px;
}
 
ytd-video-meta-block:not([rich-meta]) #metadata-line.ytd-video-meta-block {
  font-size: 13px;
}

yt-icon.style-scope.ytd-toggle-button-renderer {
  width: 20px;
  height: 20px;
}

yt-icon.style-scope.ytd-button-renderer {
  width: 20px;
  height: 20px;
}

yt-formatted-string.style-scope.ytd-toggle-button-renderer.style-text {
  font-size: 1.3rem;
  font-weight: 400;
  letter-spacing: 0.007px;
}

yt-formatted-string.style-scope.ytd-toggle-button-renderer.style-default-active {
  font-size: 1.3rem;
  font-weight: 400;
  letter-spacing: 0.007px;
}

yt-formatted-string.style-scope.ytd-button-renderer.style-default.size-default {
  font-size: 1.3rem;
  font-weight: 400;
  letter-spacing: 0.007px;
  display: none;
}

yt-formatted-string#text.style-scope.ytd-button-renderer.style-visibly-disabled.size-default {
  display: none;
}

ytd-button-renderer.style-scope.ytd-menu-renderer.force-icon-button.style-default.size-default {
  padding-right: 2px;
}

a.yt-simple-endpoint.style-scope.ytd-toggle-button-renderer {
  padding-right: 8px;
}

ytd-button-renderer.force-icon-button:not([has-no-text]) a.ytd-button-renderer {
  padding-right: 0px;
}
}
