@-moz-document domain("dev.to") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.crayons-reaction__count,
.crayons-subtitle-2,
.opacity-50,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.color-base-70,
.crayons-notice,
.mr-1,
.registration__hr-label,
.sidebar-data,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.user-metadata-details .key,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.crayons-story__tertiary,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

em
{
	color: #ffdfdf!important;
}


}

@-moz-document domain("dev.to") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.crayons-article__cover__image__feed,
.crayons-footer,
.registration__hr-label
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.c-cta,
.crayons-btn
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.c-cta::before,
.crayons-btn.crayons-btn::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.c-cta:hover::before,
.crayons-btn:hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.crayons-article__cover__image,
.crayons-story__comments
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.crayons-article-actions,
.crayons-card,
.crayons-story
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.crayons-article-actions:hover,
.crayons-article__cover__image:hover,
.crayons-card:hover,
.crayons-story__comments:hover,
.crayons-story:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.crayons-notice
{
	background: linear-gradient(to bottom, #590505 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.crayons-header
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Code 
_____________________________________________________________________________________________________*/

pre
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.crayons-link--contentful:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}


}

@-moz-document domain("dev.to") {
/* Image Settings */

.block,
.crayons-link--contentful,
.crayons-story
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.block:hover,
.crayons-link--contentful:hover,
.crayons-story:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

.crayons-article__cover__image__feed,

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

selector:hover
 {
	opacity: 1!important;
}

}

@-moz-document domain("dev.to") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Bookmark _________________________________________________________________________________*/

.bookmark-button svg,
[aria-label*="Save post"] svg,
[aria-label="Add to reading list"] svg
{
	background: url(https://i.ibb.co/K2jW2Q1/Yellow-Stitch-short.png) no-repeat!important;
	box-sizing: border-box;
}

.bookmark-button svg,

[aria-label*="Save post"] svg
 {
    background-size: 15px!important;
    height: 29px!important;
    padding-left: 21px!important;
    width: 1px!important;
}

[aria-label="Add to reading list"] svg
 {
    background-size: 20px!important;
    height: 29px!important;
    padding-left: 21px!important;
    width: 1px!important;
}

/* Comment _________________________________________________________________________________*/

.crayons-story__details a:nth-child(2) svg,

[aria-label="Jump to Comments"] svg,
[data-tracking-name="comment_reply_button"] svg
{
	background: url(https://i.ibb.co/98nZdCB/comment1.png) no-repeat!important;
	box-sizing: border-box;
}

.crayons-story__details a:nth-child(2) svg,

[aria-label="Jump to Comments"] svg,
[data-tracking-name="comment_reply_button"] svg
 {
    background-size: 25px!important;
    height: 21px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Heart ________________________________________________________________________________*/

/* On ___________________*/

[aria-label="Like"] svg
{
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________________*/

.crayons-story__details a:first-child svg,

[aria-label="like"] svg
{
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

.crayons-story__details a:first-child svg,

[aria-label="Like"] svg,
[aria-label="like"] svg
{
    background-size: 25px!important;
    height: 21px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Notification ________________________________________________________________________*/

[aria-label="Notifications"] svg
{
	background: url(https://i.ibb.co/8Px680k/Notification.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label="Notifications"] svg
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}


}

@-moz-document domain("dev.to") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
