@-moz-document url-prefix(https://meet.google.com/) {
/* Sorry cuz my English is not well, i just want to comment all the things for you to make some changes here :D */

/* Animations */
@keyframes BorderRainbow
  {
    0% {border: 4px solid rgb(235, 64, 52);}
    9% {border: 4px solid rgb(235, 131, 52);}
    18% {border: 4px solid rgb(235, 201, 52);}
    27% {border: 4px solid rgb(168, 235, 52);}
    36% {border: 4px solid rgb(86, 235, 52);}
    45% {border: 4px solid rgb(52, 235, 168);}
    54% {border: 4px solid rgb(52, 235, 220);}
    63% {border: 4px solid rgb(52, 192, 235);}
    72% {border: 4px solid rgb(52, 55, 235);}
     81% {border: 4px solid rgb(143, 52, 235);}
    90% {border: 4px solid rgb(232, 52, 235);}
    97% {border: 4px solid rgb(235, 52, 147);}
    100% {border: 4px solid rgb(235, 64, 52);}
}

@keyframes ShadowRainbow
{
  0% {box-shadow: 0px 2px 16px 8px rgb(235, 64, 52);}
  9% {box-shadow: 0px 2px 16px 8px rgb(235, 131, 52);}
  18% {box-shadow: 0px 2px 16px 8px rgb(235, 201, 52);}
  27% {box-shadow: 0px 2px 16px 8px rgb(168, 235, 52);}
  36% {box-shadow: 0px 2px 16px 8px rgb(86, 235, 52);}
  45% {box-shadow: 0px 2px 16px 8px rgb(52, 235, 168);}
  54% {box-shadow: 0px 2px 16px 8px rgb(52, 235, 220);}
  63% {box-shadow: 0px 2px 16px 8px rgb(52, 192, 235);}
  72% {box-shadow: 0px 2px 16px 8px rgb(52, 55, 235);}
  81% {box-shadow: 0px 2px 16px 8px rgb(143, 52, 235);}
  90% {box-shadow: 0px 2px 16px 8px rgb(232, 52, 235);}
  97% {box-shadow: 0px 2px 16px 8px rgb(235, 52, 147);}
  100% {box-shadow: 0px 2px 16px 8px rgb(235, 64, 52);}
}

@keyframes PresenterBlackWhite
{
  0% {background-color: rgba(255, 255, 255, .8);}

  50% {background-color: rgb(59, 59, 59);}

  100% {background-color: rgba(255, 255, 255, .8);}
}    

/* Kích thước nút rời cuộc họp - Leave Button Size */
.NKaD6
{
  border-radius: 10px;
  height: 40px;
  width: 500px;
}

/* ---------------------------------------------------------------------------------------------------------------- 
Border and Shadow are synchronized with each other */  
/* Bóng thanh header người đang trình bày - Header Shadow ( Someone is presenting ) */
.MON6Vd
{
  background-color: transparent;
  animation: ShadowRainbow 8s infinite linear;
}

/* Viền thanh header người đang trình bày Header Border ( Someone is presenting )*/
.H0YpEc
{
  background-color: transparent;
  border-radius: 8px;
  margin-left: -16px;
  height: 89%;
  animation: BorderRainbow 8s infinite linear;
}
/* ---------------------------------------------------------------------------------------------------------------- */

/* Header Text Color ( Someone is presenting ) */
.N1c98
{
  color: #fff;
  font-size: 20px;
  padding-left: 20px;
}

/* Viền người nói - When a user talk, this css will be apply to the user's border*/
.tC2Wod {
    box-sizing: border-box;
    height: 100%;
    position: absolute;
    top: 0;
    width: 100%;
    border: none;
    box-shadow: inset 0px 0px 25px 4px rgb(255 255 255);
  }

/* Màu Bóng người nói - User Shadow Color*/
.zWfAib:not(.zTETae):not(.n9oEIb)>.Zf0RDc {
	background-color: transparent;
    box-shadow: 0px 0px 32px 4px rgba(255, 255, 255, 60%);
}

/* Màu hộp người đàm thoại - User Box Background Color */

.P9KVBf .dkjMxf {
  background-color: transparent;
}

/*[[bbtn]]*/

/* "Stop Presenting" Text */
.VfPpkd-LgbsSe .VfPpkd-vQzf8d {
	color: black;
}

/* Left tab text color in settings */

.VfPpkd-vQzf8d {
    color: black;
}

/* Sound Animation ( when a user turn on their mic, this animation appear at top right of the user's block ) */
.jb1oQc .KUNJSe
{
  background-color: #000;
  border-radius: 50%;
  color: #fff;
}

/* Just a effect, don't mind this */
.RWK2Je {
	animation: PresenterBlackWhite 3s infinite;
}

.p2ZbV {
    -webkit-font-smoothing: antialiased;
    background: url(/*[[img]]*/) no-repeat;
    background-size: 100% 100%;
    direction: ltr;
    font-family: 'Roboto',arial,sans-serif;
    font-size: 12px;
    height: 100%;
    margin: 0;
    width: 100vw;
    overflow: hidden;
  }
}

