style.css:
@-moz-document domain(www.instagram.com) {

	/* Delete image overlay so you can easily copy/paste the image now */
	div[role="button"] div div div:nth-child(2), ._aagw {
		display: none !important;
		opacity: 0 !important;
		position: absolute !important;
		left: 0 !important;
		top: 0 !important;
	}
/* Main Dark Mode */

/* White text */
a,p,h1,h2,h3,h4,h5,h6,li,span {
    color:  #fff !important;
}

/* Visible SVG icons */
button[type="button"] div svg, nav div svg {
    background-image: linear-gradient(#ff45e7,#ffc300,#f60000) !important;
    border-radius: 5px !Important;
    border: 2px solid #ffffff;
}

/* Search icon removal */
nav div[role="button"] {
    display: none !important;
}


}
