style.css:
@-moz-document domain(docs.unity3d.com)
{

	/****************************************
	  ==== BODY
	****************************************/

	  body {
		background-color: #161616 !important;
	  }

	/****************************************
	  ==== MESSAGES
	****************************************/

	  div.message-error {
		background: #fd4b23 !important;
	  }

	  div.message-warning {
		background: #fdaf23 !important;
	  }

	  div.message-ok {
		background: #27c314 !important;
	  }

	/****************************************
	  ==== TYPO
	****************************************/

	  body {
		color: #ddd !important;
	  }

	  input {
		background-color: #ddd !important;
	  }

	  h1 {
		color: #ddd !important;
	  }

	  h2 {
		color: #ddd !important;
	  }

	  div.subsection div.subsection h2 {
		/*color: #455463 !important;*/
		color: #e6e6e6 !important;
	  }

	  h3 {
		color: #ddd !important;
	  }

	  h4 {
		color: #ddd !important;
	  }

	  article h4 {
		border-bottom-color: #394060 !important;
	  }

	  .alert-warning {
		color: #fcf8e3 !important;
		background-color: #30301d !important;
		border-color: #353517 !important;
	  }

	  .alert-info {
		color: #bce8f1 !important;
		background-color: #214a5f !important;
		border-color: #1b3b4b !important;
	  }

	  a, a:hover, a:focus, a:active {
		color: #7bc6f7 !important;
	  }

	  a[href^="http://"], a[href^="https://"] {
		background: none !important;
		padding-right: 0 !important;
		background-size: 0 !important;
	  }

	  code {
		color: #fbe707 !important;
		background-color: #161616 !important;
	  }

	/****************************************
	  ==== HEADER
	****************************************/

	  div.header a {
		color: #ddd !important;
	  }

	  div.header .more .filler {
		background: #222c37 !important;
	  }

	  div.header div.search-form:before, div.header .more .filler:before {
		background: none !important;
	  }

	  div.header div.search-form input.field {
		background: #14161f !important;
	  }

	  div.header div.search-form input.field:focus {
		background: #eee !important;
	  }

	/****************************************
	  ==== TOOLBAR
	****************************************/

	  div.toolbar {
		background: #364b96 !important;
		color: #ddd !important;
		border-bottom: #222c37 2px solid !important;
	  }

	  div.lang-switcher div.lang-list {
		background: #222c37 !important;
	  }

	  div.lang-switcher div.lang-list a {
		color: #7bc6f7 !important;
	  }

	  div.otherversionscontent {
		background-color: #222c37 !important;
		border-top: none !important;
	  }

	  div.otherversionscontent a {
		color: #7bc6f7 !important;
	  }

	  li.vsDivHeader {
		border-left: 8px solid #222c37 !important;
	  }

	  .vsDivHeader, #otherVersionsLegend {
		background-color: #222c37 !important;
	  }

	/****************************************
	  ==== FOOTER
	****************************************/

	  div.footer {
		background-color: #222c37 !important;
		border-radius: 2em !important;
		border-top: none !important;
		padding-left: 1em !important;
		padding-right: 1em !important;
	  }

	  div.footer div.copy a {
		color: #ddd !important;
	  }

	  div.footer div.menu a {
		color: #ddd !important;
	  }

	/****************************************
	  ==== SIDEBAR
	****************************************/

	  div.sidebar-wrap {
		background-color: #161616 !important;
	  }

	  div.sidebar-wrap {
		border-right: #e6e6e6 !important;
	  }

	  div.sidebar-menu h2 {
		border-bottom: #e6e6e6 1px solid !important;
	  }

	  div.sidebar-menu ul li div.leafnode {
		border: 0 !important;
		position: absolute !important;
		background-color: #161616 !important;
	  }

	  div.sidebar-menu ul li div.arrow, div.sidebar-menu ul li div.node {
		border-color: #161616 !important;
		background-color: #7043af !important;
	  }

	  div.sidebar-menu ul li div.leafnode.expanded {
		background: none !important;
	  }

	  div.sidebar-menu ul li a {
		color: #7bc6f7 !important;
	  }

	  div.sidebar-menu ul li a.current {
		background: none !important;
		color: #ffaa00 !important;
	  }

	  div.sidebar-wrap .nl span {
		color: #25a59b !important;
	  }

	/****************************************
	  ==== CONTENT
	****************************************/

	  .nextprev {
		background: #3d73b6 !important;
	  }

	  .content .section ul li:before {
		color: #25a59b !important;
	  }

	  .content h4 {
		color: #70a5ca !important;
	  }

	  .content div.frontpage-section h3 a {
		color: #7bc6f7 !important;
	  }

	  .content figure figcaption {
		color: #99cc88 !important;
	  }

	  .content table thead th {
		background: #222c37 !important;
	  }

	  .content table tbody tr {
		background: #14161f !important;
	  }

	  .timetoshineitem {
		background: none !important;
	  }

	  table.list tr:hover {
		outline: #25a59b 1px solid !important;
	  }

	  td, th, pre, pre code, .sidetoc, .table-bordered {
		border-color: #394060 !important;
	  }

	  .subsection .list tbody tr {
		background-color: #222c37 !important;
		border: #9e488d 1px solid !important;
		color: #ddd !important;
	  }

	  .subsection .list tbody tr:nth-child(even) {
		background-color: #202933 !important;
	  }

	/****************************************
	  ==== Feedback FORM
	****************************************/

	  .feedbackbox {
		background: #222c37 !important;
		padding: 1em !important;
		border-radius: 1em !important;
	  }

	/****************************************
	  ==== BREADCRUMBS
	****************************************/

	  .breadcrumbs ul li:after {
		color: #ffaa00 !important;
	  }

	  .breadcrumbs ul li a {
		color: #7bc6f7 !important;
	  }

	/****************************************
	  ==== CODE HIGHLIGHTING
	****************************************/

	  pre, pre code {
		color: #ddd !important;
		background-color: #212936 !important;
	  }

	  pre code, pre .subst, pre .tag .title, pre .lisp .title, pre .clojure .built_in, pre .nginx .title {
		color: #ddd !important;
	  }

	/****************************************
	  ==== Tooltips (Glossary)
	****************************************/

	  .tooltip {
		border-bottom: 1px dotted gray !important;
		color: #7bc6f7 !important;
	  }

	  .tooltip .tooltiptext::after {
		border-color: #394060 !important;
	  }

	  .tooltip .tooltiptext {
		background-color: #222c37 !important;
		color: #ddd !important;
	  }

	/****************************************
	  ==== SCROLLBAR STYLE
	****************************************/

	  #customScrollbar::-webkit-scrollbar-thumb {
		background-color: rgba(109, 67, 175, 0.4) !important;
	  }

	  #customScrollbar::-webkit-scrollbar-thumb:hover {
		background-color: rgba(109, 67, 175, 0.8) !important;
	  }

	/****************************************
	  ==== Packages
	****************************************/

	  .navbar-default {
		background-color: #3d73b6 !important;
		border-color: #000 !important;
	  }

	  .sidetoc, .sidefilter, .naw, .toc {
		background-color: #161616 !important;
	  }

	  .sidetoc, .sidefilter {
		border-width: 2px !important;
		border-color: #394060 !important;
	  }

	  .toc .nav li a {
		color: #7bc6f7 !important;
	  }

	  .toc .nav li a:hover {
		text-decoration: underline !important;
	  }

	  .toc .nav li a:hover, .toc .nav>li.active>a, .affix>ul>li.active>a, .affix>ul>li.active>a:before {
		color: #85d685 !important;
	  }

	  .affix ul>li>a {
		color: #e4d100 !important;
	  }

	  .affix ul>li>a:hover {
		color: #ddd !important;
	  }

	  .toc-filter {
		outline: none !important;
		border-radius: 3px !important;
		background: #ccc !important;
		color: #e4e4e4 !important;
		padding: 2px !important;
		margin: 3px !important;
	  }

	  .toc-filter>input {
		color: #e4e4e4 !important;
	  }

	  .component-select__current {
		color: #000 !important;
		background-color: #ddd !important;
	  }

	  .component-select__options-container {
		background-color: #ddd !important;
	  }

	  .toc-filter>input {
		color: #14161f !important;
	  }

	  .hljs {
		color: #ddd !important;
	  }

	  .hljs-keyword {
		color: #569ffa !important;
	  }

	  thead {
		background-color: #222c37 !important;
	  }

	  .hljs-title {
		color: #f7de95 !important;
	  }

	  .hljs-number {
		color: #00c9ff !important;
	  }

	  .hljs-string {
		color: #f7bfa8 !important;
	  }
}
