style.css:
@-moz-document domain("theoldreader.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.banner .all-in-one-container h1,
.device a,
.devise-form-continer .btn-primary,
.footer .nav-links a,
.learn-more a,
.nav-header,
.sign-up a
{
	text-transform: capitalize!important;
}

.btn-default,
.home-page .sponsorship,
.learn-more a,
.nav-list li.active a:focus:not(.fa):before,
.nav-list li.active a:hover:not(.fa):before,
.nav-list li.active a:not(.fa):before,
.sign-up,
.well,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.post.listview .header .list-header,
.nav-list strong {
    white-space: pre-line;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.content-body
{
	font-size: 1.85rem!important;
}

.nav-list li.nav-header a,.nav-list li.nav-header a:hover, .nav-list li.nav-header a:focus
 {
    font-size: 2rem!important;
}

.badge,
.label.label-transparent.pull-right,

body,
strong
{
	font-size: 1.52rem!important;
}

.nav-list strong
{
	font-size: 1.2rem!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.home-page dl dt {
    margin-right: 1rem;
}

.nav-list li.nav-header a, .nav-list li.nav-header a:hover, .nav-list li.nav-header a:focus {
    margin-left: 10px;
}

.reader .body-fixed-top
{
    top: 12rem;
}

.main-row .sidenav-cell
{
    top: 2rem;
}

/* Widths 
_____________________________________________________________________________________________________*/

.home-page dl dt .label,
.nav-list strong,
.post.listview .header .label-transparent
{
	max-width: none!important;
}

.col-md-7 {
    width: 76%;
}

.col-md-5 {
    width: 24%;
}

}

@-moz-document domain("theoldreader.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
	.col-md-offset-6 {
		margin-left: 65%;
	}

	.reader .body-fixed-top {
		left: 41rem;
	}
	
	.post.listview .header .list-header.no-left-label {
    width: 90%;
}
	
	.main-row .content-cell {
		width: 83%;
	}
	
	.post.listview .header .name {
    width: 17%;
}
	
	.main-row .sidenav-cell {
    width: 15%;
}

	.home-page dl dt {
		width: 37rem;
	}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

