style.css:
@-moz-document domain('monbureaunumerique.fr'), domain('education.gouv.fr') {
  :root {
    --color-secondary: #2ecc71;
    --color-border:#27ae60;
    --bgcolor-input: #b3dbff;
    --color-link:#663399;

    /*header, nav, menu*/
    --bgcolor-header: #203354;
    --bgcolor-nav-button:#192841;
    --bgcolor-active-nav-button:#1c2e4a;
    --bgcolor-nav:#23395d;

    --bgcolor-gray-main: #3B4348;

    --bgcolor-black-main:#2D3236;
    --bgcolor-black-secondary:#202528;


    --bgcolor-secondary-panel:#252A2D;
    --color-secondary-panel: #80878C;
    --color-selected-secondary-panel:#70787E;
    --bgcolor-panel-main:#515B62;

    --color-footer:#BFC3C5;

    --color-gray-main:#AFB4B7;
    --color-black-main:#4A535A;
    --color-white-main: #CFD2D4;
    --color-white-secondary: #DFE1E2;
    --color-title: #EFF0F0;
  }
}

@-moz-document domain('monbureaunumerique.fr') {
  html {
    background-color: var(--bgcolor-black-main);
  }
  body {
    color: var(--color-gray-main); 
  }
  .fo-banner, .fo-main-content, .bg--color-white, .bg--color-compl2, .fo-footer {
    background-color: var(--bgcolor-gray-main);
  }
  
  .fo-site-name__subtitle,  .fo-section__title{
    color: var(--color-white-secondary);
  }
}

/* landing pages */
@-moz-document domain('cas.monbureaunumerique.fr') {
  html {
    height: 100%;
    background-color: var(--bgcolor-gray-main);
  }
  
  .panel {
    background-color: var(--bgcolor-secondary-panel);
    color: var(--color-white-secondary);
  }
}

/*Page to login*/
@-moz-document domain('educonnect.education.gouv.fr') {
  body, .fr-header, .fr-footer{
    background-color: var(--bgcolor-gray-main);
    color: var(--color-secondary-panel); 
  }
  
  /* title */
  h1, .fr-header__service-title, .title-fs-18, .fr-header.minify .fr-header__service-title {
    color: var(--color-title);
  }
  
  .fr-header__service-tagline, .fr-logo, .titreCompte {
    color: var(--color-white-secondary);
  }
  
  .fr-footer__content-link, .fr-footer__bottom-link, .sidebar ul li {
    color: var(--color-footer);
  }
  .fr-link {
    color: var(--color-link);
  }
  
  body .sidebar, .choixProfil__btn, .fr-card, .fr-card.fr-enlarge-link {
      background-color: var(--bgcolor-secondary-panel);
      color: var(--color-secondary-panel); 
  }
  .choixProfil__btn:hover {
    background-color: var(--color-selected-secondary-panel);
  }
}

@-moz-document domain('lyc-zurcher.monbureaunumerique.fr') {
  html, #screenreader-contenu, #page, .col--bg-lightest {
    background-color: var(--bgcolor-gray-main);
    color: var(--color-white-main);
  }
  
  

   /*header, nav, menu*/
  .header, .header > .bar {
    background-color:var(--bgcolor-header);
  }
  
  .header > .burger {
  background-color:var(--bgcolor-nav-button);
  }
  
  .header > .burger:active, .header > .burger:hover {
    background-color:var(--bgcolor-active-nav-button);
  }
  .header > .menu, .btn--neutral, .btn--primary{
    background-color: var(--bgcolor-nav);
  }
  
    /*decrease menu size*/
  .header .burger, .header .menu {
    width: 17.5%;
  }
  .content.menu-is-opened  {
    margin-left: 17.5%;
  }


  .bg--white,   #navigation_cahier_texte, #filtres_criteres_contenu, .gestion fieldset, #contenu .fieldset, #popin_question fieldset {
    background-color: var(--bgcolor-panel-main);
    color: var(--color-white-secondary);
  }
  
  .content-toolbar {
        background-color: var(--bgcolor-gray-main);
        border: 3px solid var(--color-title);
        color: var(--color-title);
   }
  
  .panel, .content_filter {
    background-color: var(--bgcolor-panel-main);
  }
  
   select, select.form__control, input, button, input[type="submit"]:active, .etai-input-type-submit:active  {
        background-color: var(--bgcolor-input);
   }
  
   h1, h2, legend {
      color: var(--color-white-main);
   }
  
  .col--full {
    color: var(--color-white-secondary);
  }
  a, .btn--as-link, .btn--as-link-underline, .jstree-label {
    color: var(--color-secondary);
  }
  .tabs__button, .tabs__link {
    color: var(--color-white-secondary);
  }
  .tabs__button.active, .tabs__link.active, .tabs__button:hover, .tabs__link:hover {
    color: var(--color-secondary);
    border-color: var(--color-secondary);
  }
  .btn--secondary, .checkbox-pills__label--secondary, .radio-pills__label--secondary, [type=checkbox]:checked~label.checkbox-pills__label--primary, [type=radio]:checked~label.radio-pills__label--primary {
    color: var(--color-border);
     border-color: var(--color-border);
    background-color: transparent;
  }
  .slug[class*=slug--sm]:hover {
     border-color: var(--color-border);
  }
  .modal__body {
    background-color: var(--bgcolor-panel-main);
  } 
  article.fo-card--shadow {
    background-color: transparent;
  }
    
    /* style filter pane in the sectionl 'travail à faire'  in 'cahier de textes' https://lyc-zurcher.monbureaunumerique.fr/sg.do?PROC=TRAVAIL_A_FAIRE&ACTION=AFFICHER_ELEVES_TAF&filtreAVenir=true*/
    #screenreader-contenu > div.container.container--full.js-taf__wrapper > div.row--full.row--flex-sm.js-classeur__selector > div.col--xs-12.col--sm-6.col--md-3.col--flex-sm.col--full.js-infos-col > div {
      margin: 20px 0;  
      border: 2px solid var(--color-title);
      border-radius: 5px;
      color: var(--color-title);
    }
    
    /*file display in 'Espace des classes'*/
    .jumbofiles__files {
    background-color: var(--bgcolor-panel-main);
    }
    
  }

@-moz-document url-prefix('https://lyc-zurcher.monbureaunumerique.fr/sg.do?PROC') {
  
  #menu_contenu, .scheduler__toolbar, .content-filter  {
      background-color: var(--bgcolor-gray-main);
  }     
        
  /* table style*/
   .yui-skin-sam tr.yui-dt-even {
        background-color: var(--bgcolor-black-secondary);
  }
  .yui-skin-sam tr.yui-dt-odd {
    background: var(--bgcolor-black-main);
  }
  .yui-skin-sam .yui-dt th {
    background-color: var(--bgcolor-black-main);
  }
  .yui-dt-col-moyenneEleve, #releve-enseignant .yui-dt-col-moyenne {
    background-color: var(--color-secondary-panel);
    color: var(--color-title);
  }
  .yui-skin-sam .bulletin-note {
    text-align: center;
  } 
  td.yui-dt0-col-matiere {
    color: var(--color-white-secondary);
  }
        
    /* parameters colors when filters changes*/
    .yui-skin-sam tr.yui-dt-odd td.yui-dt-asc,
    .yui-skin-sam tr.yui-dt-even td.yui-dt-desc,
    .yui-skin-sam tr.yui-dt-even td.yui-dt-asc,
    .yui-skin-sam tr.yui-dt-odd td.yui-dt-desc {
          background-color: inherit;    
          color: inherit;
    }
    
    .yui-skin-sam tr.yui-dt-even td.yui-dt-asc.yui-dt-col-moyenneEleve,
    .yui-skin-sam tr.yui-dt-even td.yui-dt-desc.yui-dt-col-moyenneEleve,
    .yui-skin-sam tr.yui-dt-odd td.yui-dt-asc.yui-dt-col-moyenneEleve,
    .yui-skin-sam tr.yui-dt-odd td.yui-dt-desc.yui-dt-col-moyenneEleve {
      background-color: var(--color-secondary-panel);
    }
        
   
   
    .scheduler__header, .scheduler__slots {
        background: var(--bgcolor-panel-main);
    }
        .scheduler__card--session {
          background-image: linear-gradient(0deg,hsl(0deg 0% 0% / 90%) 100%,#fff 0);
          color: var(--color-title);      
        }
      .scheduler__header--today, .scheduler__slot--today {
        background-color: var(--bgcolor-gray-main);
    }
    .scheduler__week {
      color: var(--color-title);
    }  

    /*"bureautique en ligne window"*/
    .yui-skin-sam .yui-panel .bd {
      background-color: var(--bgcolor-panel-main);
    }
    .yui-skin-sam .yui-panel .hd {
      color: var(--color-title);
      background: var(--bgcolor-header);
    }

    .p-like {
      color: var(--color-white-main);
    }
    .score {
      color: var(--color-black-main);
    }
  }
