/* VENGE CUSTOM CSS TEMPLATE BY TOMOGUNCHI AND CUSTOM BY K.B.P */
/* i really want this to be global */
body {
    font-family: "Roboto", sans-serif;
    background: url("https://media.discordapp.net/attachments/885528633911939082/1196336228119887882/Tactical_Tandem_Battle_Ready.png?ex=65c07c6f&is=65ae076f&hm=7ed0b63703771dc48a2458c78a6b59479d0e3aa38f3cf2e3129fb7958f4b520f&=&format=webp&quality=lossless&width=741&height=417") no-repeat fixed;
    background-size: cover;
    width: 100%;
  }
  
  .content {
    background: rgba(0,0,0,0.27);
    width: 100%;
    margin-top: 30px;
    border-radius: 2px;
    box-shadow: 0px 2px 10px rgba(0,0,0,0.17);
    padding: 25px;
    box-sizing: border-box;
    height: auto;
    display: table;
  }
  
  .table {
    width: 100%;
    box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.1);
    border: solid 1px rgba(221,221,221,0.13);
    /* border-radius: 5px; */
    /* overflow: hidden; */
    /* margin-bottom: 30px; */
  }
  
  table {
    border-collapse: collapse;
  }
  table,
  td {
    font-size: 14px;
      color: white
  }
  table,
  td:first-child,
  th:last-child {
    width: 10px;
  }
  table,
  td:last-child,
  th:last-child {
    width: 50px;
  }
  table,
  button {
    margin-right: 5px;
    padding: 0.3vw;
    float: left;
  }
  table,
  table a {
    margin-right: 5px;
    padding: 0.3vw;
    float: left;
  }
  table a{
    color: initial;
  }
  
  .dark-mode table a{
    color: #fff;
  }
  table,
  th,
  td {
    padding: 10px;
  }
  table,
  .verification {
    height: 16px;
    float: left;
    margin-right: 5px;
  }
  table,
  tr:nth-child(even) {
    background-color: rgba(0,0,0,0.27);
  }
  table,
  tr th {
    text-align: left;
    font-size: 12px;
    background-color: rgba(57, 50, 106, 0.1);
  }
  
  table tr:hover {
    background-color: rgba(165,52,255,0.5) !important;
  }
  
  b {
      color: white
  }
  
  .alert-info,
  .alert {
    padding: 5px 10px;
    background: rgba(0,0,0,0.16);
    border: solid 1px rgba(255,255,255,0);
    border-radius: 2px;
    margin-bottom: 20px;
    width: 100%;
    display: block;
    font-size: 13px;
    box-sizing: border-box;
      color: white
  }
  
  .content-header {
    height: 55px;
    border-bottom: solid 1px #ddd;
    margin-bottom: 20px;
  }
  
  .content-header-tab {
    height: 60px;
  }
  
  .content-header-tab li {
    float: left;
    list-style: none;
    font-weight: normal;
    font-size: 21px;
    margin-right: 25px;
    padding-right: 25px;
    border-right: solid 1px #ddd;
    cursor: pointer;
    color: #ffffff;
    height: 35px;
    line-height: 35px;
  }
  
  .content-header-tab li.active {
    font-weight: bold;
    color: #8b2aff;
  }
  
  .content-header-tab li:last-child {
    border-right: none;
  }
  
  .content-header-tab li i {
    margin-right: 10px;
  }
  
  .logo {
    height: 80px;
    margin-top: -17px;
    margin-left: 21px;
      content: url('https://media.discordapp.net/attachments/1031525057836625920/1044057390699511888/Game-removebg-preview.png')
  }
  
  .profile-info .username > span {
      color: white
  }
  
  h4 {
      color: white
  }
  
  .profile-actions ul li {
      color: white
  }
  
  .stats-col li {
    background: rgba(136,36,255,0.5);
    color: #fff;
    padding: 7px 15px;
    border-radius: 3px;
    margin-bottom: 10px;
  }
  
  .stats-col li:nth-child(even) {
    background: rgba(136,36,255,0.5);
  }
  
  .stat-card {
    background: rgba(136,36,255,0.5);
    color: #fff;
    float: right;
    width: 90px;
    height: 60px;
    text-align: center;
    margin-left: 10px;
    border-radius: 4px;
    box-sizing: border-box;
  }
  
  .profile-header,
  .clans-header {
    height: 90px;
    margin-bottom: 30px;
    border-bottom: solid 1px rgba(221,221,221,0.23);
  }
  
  .content-header {
      border: 1px solid rgba(221,221,221,0);
    }
  
  .status li {
    color: #ffffff !important;
  }
  
  .table a {
      color: white
  }
  
  h3 {
      color: white
  }
  
  .clans-info .username span {
      color: white
  }
  
  .clan-owner{
    width: 25px;
    filter: brightness(99);
  }
  
  .account-balance {
      color: white
  }
  
  .popup-content {
    width: 700px;
    max-height: 98vh;
    /* height: 600px; */
    background: rgba(0,0,0,0.79);
    box-shadow: 0px 0px 10px #404040;
    left: 50%;
    top: 50%;
    position: absolute;
    transform: translate(-50%, -50%);
    overflow-x: hidden;
    overflow-y: auto;
    border-radius: 4px;
    z-index: 101;
  }
  
  .popup-content h1 {
    font-size: 20px;
    padding: 10px 25px;
    background: #9729fe;
    margin: 0px;
    color: #fff;
    height: 45px;
    box-sizing: border-box;
    text-shadow: 0px 1px 2px #6915b9;
  }
  
  .popup-alternative .secondary-title {
      color: white
  }
  
  .popup-charts .secondary-title {
      color: white
  }
  
  .balance span {
      color: white
  }
  
  p span {
      color: white
  }
  
  .field {
      color: white
  }
