style.css:
/* -------Around space */
.ui {
    background-color: dimgray  !important;
}

/* Timeline elements */
.column>.scrollable{
 background: black ;   
}

/* Detail Toot */
.detailed-status,
.detailed-status__action-bar{
 background: pink;
}

/* Elements */
.display-name,
.status__display-name *,
.account__display-name strong,
.status__content,
.notification__display-name:hover,
.notification__display-name:active,
.detailed-status,
.detailed-status__action-bar,
.detailed-status__display-name strong, 
.detailed-status__display-name span,
.account__action-bar__tab *,
.getting-started p{
    color: pink !important;
}


/* Reply Toot Textbox */
.reply-indicator{
    background-color: #EAEAEA;
}

/* Scroll bar thumb default color */
::-webkit-scrollbar-thumb	{
 	background: pink !important;   
}

/* Scroll bar thumb is acrive and hover */
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active{
	background: lightpink !important;   
}

/* Scroll bar background  */
::-webkit-scrollbar-track{
    background: dimgray !important;
}


/* Left sidebar */
.drawer__inner, .navigation-bar{
 	background: dimgray }

.autosuggest-textarea *{
	color: black;
}

/* Toot button */
.button {
    background-color: pink;
}

/* Search result list */
.search-results__hashtag, .search-results__hashtag:hover{
    color: #2B7BB9;  
}

/* Search Text Input */
.search__input,
.search__input:hover,
.search__input:active,
.search-results__section{
    color: black;
    background: white !important;
}

.search__icon 
.fa-search.active,
.search-results__section,
.search__icon .fa-times-circle.active{
	color: #292F33;   
	opacity: 1;
}

.drawer__inner.darker{
    background: white;
}

/* Link */
.status__content,
.status__content a,
span.ellipsis,
.active{
    color: whitesmoke;
}

/* showmore */
.status__content .status__content__spoiler-link,
.status__content .status__content__spoiler-link:active,
.reply-indicator__content .status__content__spoiler-link
{
    background-color: transparent !important;
    color: pink !important;
    outline: 0px !important;
    border: 1px solid pink !important;
    margin: 0px 0px 0px -1px !important;
}

/* showmore hover */
.status__content .status__content__spoiler-link:hover{
        background-color: whitesmoke !important;
	    color: black !important;
    	outline: 0px;
    	border: 1px solid #2B7BB9;    
}

/* media hidden */
.media-spoiler, .video-error-cover{
  	color: black;
    background: dimgray;
}

/* Left header */
.tabs-bar,
.drawer__header,
.drawer__header *,
.search-results__header {
    background-color: #292F33;
    color: #F5F8FA;
}
.column-header__button {
    background-color: dimgray
}

/* TL column header */
.column-header,
.column-icon,
.column-back-button{
    color: pink !important;
	 background-color: dimgray !important;
}

.column-settings__outer{
    background-color: dimgray !important;
}

.column-settings__section, .setting-toggle{
    color: black;
}

.setting-text{
 color: black !important;
}

.column-icon:hover, .column-collapsable__button:hover{
    background-color: dimgray !important;
    color: pink !important;
}

/* Navigation */
.column-subheading{
	color: pink;
	background: dimgray;
}

.column-link {
    color: dimgray;
	background: #292f33
}

.fa-reply:hover, .fa-reply-all:hover, .dropdown__icon:hover{
	color: pink;
}

.fa.fa-fw.fa-retweet:hover{
    background-position: 0 100% !important;
    color: pink;
}

.empty-column-indicator, .empty-column-indicator{
	background: black;
}

.media-spoiler__warning, .media-spoiler__trigger{
 color: pink;   
}

/* Fav */
.fa-star:hover{
 color: pink;   
}
.display-name, .status__display-name *, .account__display-name strong, .status__content, .notification__display-name:hover, .notification__display-name:active, .detailed-status, .detailed-status__action-bar, .detailed-status__display-name strong, .detailed-status__display-name span, .account__action-bar__tab *, .getting-started p {
    color: default !important;
}

/* Follow, Follower Load more */
.load-more:hover{
    background: #EAEAEA;
}

.dropdown--active .dropdown__content>ul, .dropdown__content-list-link{
    background: white !important;
}

.dropdown__content-list-link:hover, .dropdown__content-list-link:active{
    background: #2b7bb9 !important;
    color: white !important;
}

.detailed-status, .detailed-status__action-bar {
    background: black;
}

