style.css:
@-moz-document domain(web.roblox.com) {
	
/*BACKGROUND*/
.container-main, .content,.spacer
    {background:#101010!important}
    
/*GAME CARDS AND CATALOG CARDS*/
div.game-card-container, a.item-card-container
    {background:#2b2b2b!important;transition: background 0ms !important;border-radius:10px!important}
    
div.game-card-container:hover ,a.item-card-container:hover
    {background:#ffffff2e!important;transform:scale(1.01);transition: transform 100ms;}
    
div.game-card-container:active, a.item-card-container:active
    {transform:scale(0.98);transition:100ms;}    
    

/*SIDEBAR ON LEFT*/
.left-col-list > li
{left:0px; transition:250ms;background:#333;border-radius:5px}
.left-col-list > li:hover
{position:relative;left:7px;transition:250ms}

    
    
/*HEADERS ON HOME PAGE*/
div[class="container-header"],.game-carousel
    {background:#1e1e1e!important;border:2px solid #1e1e1e;border-radius:10px}
div[class="container-header"]
    {position:relative;top:6px;border-bottom-right-radius:0px!important;border-bottom-left-radius:0px!important}
.game-carousel
    {border-top-left-radius:0px!important;border-top-right-radius:0px!important;border-bottom:5px solid #1e1e1e!important;}
    
    
/*PLAY BUTTON*/
.game-details-play-button-container{
    overflow:clip!important;position:relative
}

.game-details-play-button-container:hover:before{
    content:"";position:absolute;transform:rotate(10deg);bottom:-5px;left:-5px;
    width:40px;height:300px;border-radius:10px!important;
    background:linear-gradient(to right, rgba(255,255,255,0), rgba(255,255,255,0.72), rgba(255,255,255,0))!important;
    animation:slide 1.5s infinite;opacity:0;cursor:pointer
}

@keyframes slide{
    0%{left:-5px;opacity:0}
    1%{left:-5px;opacity:1}
    80%{left:370px;opacity:1}
    81%{left:-50000px;opacity:0}
    100%{left:-5px;opacity:1}
}



/*CATALOG CARDS - FREE ITEM SHINING*/
a.item-card-container {
    overflow:hidden!important
}

a.item-card-container  .text-label:after
{
    content:"";
    position:absolute;width:75px;height:200%!important;bottom:-10px;
    background:linear-gradient(to right, rgba(255,255,255,0), rgba(255,255,255,0.16), rgba(255,255,255,0));
    animation: slide1 1.5s infinite;transform:rotate(10deg)
}

@keyframes slide1{
    from{left:-50px}
    to{left:200px}
}
    
.games-list-container *{
    opacity: 0.99;
    }
.games-list-container:hover *{
    opacity: 1 !important;  transition: opacity 250ms
    }
}

@-moz-document url-prefix("https://web.roblox.com/home"),
url-prefix("https://web.roblox.com/discover#/sortName"){
    div.game-card-container:after{
        content:"";position:absolute;width:100%;height:65%;background: transparent;transition: 500ms
    }
    
    div.game-card-container:hover:after{
        box-shadow: inset 20px 20px 75px #0000002b, inset -20px -20px 75px #0000002b; pointer-events: none;
    }
}
