style.css:
@-moz-document domain("diaspodon.fr"), domain("mastodon.mim-libre.fr") {
:root {
	--yellow: #f7c548;
	--yellowlight: #f7c5484D;
	--blue: #86CFF4;
	--darkest: #252525;
	--lightgrey: #d7d7d7;
	--darkbrown: #3e3b3b;
	--notifbrown: #2d2b2b;
}

html {
	scrollbar-color: var(--darkbrown) var(--darkest);
}
.scrollbar-color,
.scrollable {
	/* Replace the color by the color of column headers for a better effect. */
	scrollbar-color: var(--darkbrown) var(--darkest);
	/* This is optional but I find the default scrollbars way too fat. */
	/*scrollbar-width: thin;*/
}
div.media-gallery img {
    border-left: 3px dashed red;
}
div.media-gallery img[alt] {
    border-left: 3px solid green;
}
body.error {
	color: var(--lightgrey);
	background: var(--darkest);
}
div[data-reactroot] {
	background: none !important;
}
body {
	background: var(--darkbrown);
}
.ui {
	background: var(--darkest);
}
body.app-body {
	background: var(--darkest);
}
.loading-bar {
	background-color: var(--blue);
}
.loading-indicator__figure {
	border: 6px solid var(--yellow);
}
.loading-indicator span {
	color: var(--yellow)
}
div.column {
	opacity: 0.9;
	flex-grow: 1;
	flex-shrink: 1;
}
div.drawer {
	opacity: 0.95;
	background-color: transparent !important;
}

.status__content a .fa,
.status__content a .fa:hover {
	color: var(--blue);
}
.status__content a,
.reply-indicator__content a,
.getting-started a,
.muted .status__content a,
.account__header .account__header__username {
	color: var(--blue);
}
.account__display-name strong,
.status__display-name strong {
	color: var(--blue);
}
.display-name__html {
    font-weight: 400;
}
.notification__display-name {
    font-weight: 400;
}
.account__section-headline {
	background: var(--darkest);
	border-bottom: 1px solid var(--yellowlight);
}
.column-header {
	background: var(--darkest);
}
.column-header__button {
	background: var(--darkest);
}
.column-header__back-button {
	background: var(--darkest);
}
.column-header__back-button {
	color: var(--blue);
}
.column-header > .column-header__back-button {
	color: var(--blue);
}
.column-header__buttons:hover {
	background-color: var(--darkbrown)
}
.status {
	border-bottom: 1px solid var(--yellowlight);
}
.account {
	border-bottom: 1px solid var(--yellowlight);
}
.status__content {
	font-size: 14px !important;
}
.search {
	width: 95%;
	margin: 0 auto;
	margin-bottom: 10px;
}
.search__input {
	background: var(--darkbrown);
	color: var(--lightgrey);
	Border-radius: 4px;
}
.search__input:focus {
	background: var(--darkbrown);
}
.search__icon .fa-times-circle:hover {
	color: var(--lightgrey);
}
.search__icon .fa-times-circle {
	color: var(--lightgrey);
}
.search__icon .fa.active {
	opacity: .8;
}
.drawer__inner__mastodon {
	background: var(--darkbrown);
	background-color: var(--darkbrown);
}
.compose-form .autosuggest-textarea__textarea,
.compose-form .spoiler-input__input {
	color: var(--lightgrey);
	background: var(--darkbrown);
	border-bottom: 1px solid var(--yellow);
	outline: 0;
}
.compose-form .compose-form__modifiers .compose-form__uploads-wrapper {
	color: var(--lightgrey);
	background: var(--darkbrown);
}
.account__header .account__header__fields dd {
	color: var(--lightgrey);
	background: var(--notifbrown);
}
.account__header .account__header__fields dt {
	color: var(--lightgrey);
	background: var(--notifbrown);
}
.account__action-bar__tab strong {
	color: var(--lightgrey);
}
.account__header > div {
	background: var(--notifbrown);
	opacity: 0.9;
}
.account__header__bar {
	border-bottom: 1px solid var(--yellowlight);
}
.account__header__fields {
	border-style: solid none;
	border-width: 1px 0;
}
.compose-form .compose-form__buttons-wrapper {
	background: var(--darkbrown);
}
.compose-form .compose-form__buttons-wrapper .character-counter__wrapper .character-counter {
	color: var(--lightgrey);
}
.text-icon-button {
	color: var(--lightgrey);
}
.star-icon.active {
	color: var(--yellow);
}
.drawer__inner__mastodon {
	display: none !important;
}
.column > .scrollable {
	background: var(--darkest);
	border-top: 1px solid var(--yellowlight);
}
.drawer__inner {
	background: var(--darkest);
}
.drawer__header {
	background: var(--darkest);
}
.drawer__header a:hover {
	background: var(--darkbrown);
	-webkit-transition: background 200ms ease-out;
	transition: background 200ms ease-out;
}
.status.status-direct:not(.read) {
	background: var(--darkbrown);
	border-bottom-color: var(--darkbrown);
}
.status__display-name,
.status__prepend .status__display-name strong {
	color: var(--blue);
}
.display-name__account {
	font-size: 14px;
	color: var(--yellow);
}
.muted .status__content p {
	color: var(--lightgrey);
}
.muted .status__content a {
	color: var(--blue);
}
.muted .status__display-name strong {
	color: var(--blue);
}
.attachment-list__list a {
	color: var(--blue);
}
.status__prepend > span {
	color: var(--lightgrey) !important;
}
.notification__message > span {
	color: var(--lightgrey) !important;
}
.notification__display-name {
	color: var(--blue);
}
.notification__display-name:hover {
    color: var(--yellow);
}
.status__relative-time {
	color: var(--blue);
}
.detailed-status {
	background: var(--notifbrown);
}
.reply-indicator {
	background: var(--darkbrown);
}
.reply-indicator__content {
	color: var(--lightgrey);
}
.detailed-status__meta {
	color: var(--lightgrey);
}
.detailed-status__action-bar {
	background: var(--notifbrown);
	border-bottom: 1px solid var(--yellowlight);
}
.icon-button.inverted {
	color: var(--lightgrey);
}
.drawer__tab {
	color: var(--lightgrey);
}
.icon-button.inverted:hover,
.icon-button.inverted:active,
.icon-button.inverted:focus {
	color: var(--lightgrey);
}
.icon-button {
	color: var(--lightgrey);
}
.icon-button:hover,
.icon-button:active,
.icon-button:focus {
	color: var(--yellow);
	-webkit-transition: color 200ms ease-out !important;
	transition: color 200ms ease-out !important;
}
.reduce-motion button.icon-button i.fa-retweet {
	color: var(--lightgrey);
}
.status.status-direct:not(.read) {
	background: var(--notifbrown);
	border-bottom-color: var(--yellowlight);
	border-left: 3px solid var(--blue);
}
.icon-button.disabled {
	color: var(--lightgrey);
}
.status__content__read-more-button {
	color: var(--blue);
}
.column-header__button:hover {
	color: var(--lightgrey);
}
.column-header__button {
	color: var(--lightgrey);
}
.status-card {
	border: 1px solid var(--yellow);
}
.status-card__title {
	color: var(--lightgrey);
}
.status-card.compact {
	border-color: var(--yellow);
}
.status-card {
	color: var(--yellow);
}
.status-card__content {
	color: var(--yellow);
}
a.status-card:hover,
a.status-card.compact:hover {
	background-color: var(--darkbrown);
}
.status-card__image:hover {
	background-color: var(--darkbrown);
}
.column-back-button {
	background: var(--darkest);
	color: var(--blue);
}
.upload-progress {
	color: var(--lightgrey);
	background: var(--darkbrown);
}
.upload-progress__tracker {
	background: var(--blue);
}
.navigation-bar a {
	color: var(--blue);
}
.navigation-bar strong {
	color: var(--yellow);
	font-size: 1.2em;
}
.column-header__collapsible-inner {
	background: var(--notifbrown);
}
.column-header__button.active:hover {
	background: transparent !important;
}
.react-toggle--checked .react-toggle-track {
	background-color: var(--yellow);
}
.react-toggle--checked .react-toggle-thumb {
	left: 27px;
	border-color: var(--yellow);
}
.react-toggle-track {
	background-color: var(--darkbrown);
}
.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track {
	background-color: var(--yellowlight);
}
.button {
	background-color: var(--yellow);
}
.button:hover {
	background-color: var(--yellowlight);
	-webkit-transition: all 200ms ease-out;
	transition: all 200ms ease-out;
}
.button:disabled {
	background-color: var(--yellowlight);
	cursor: default;
}
.column-header.active {
	-webkit-box-shadow: 0 1px 0 var(--yellowlight);
	box-shadow: 0 1px 0 var(--yellowlight);
}
.column-header__wrapper.active::before {
	background: radial-gradient(ellipse,var(--yellowlight) 50%,transparent 70%);
}
.column-header {
	background: var(--darkest);
}
.column-subheading {
	background: var(--darkest);
	color: var(--lightgrey);
	border-top: 1px solid var(--yellowlight);
}
.column-link {
	background: var(--darkest);
}
.column-link:hover {
	background: var(--darkbrown);
}
.getting-started__footer {
	background-color: var(--darkest);
	border-top: 1px solid var(--yellowlight);
}
.getting-started__footer p {
	color: var(--lightgrey);
}
.getting-started__footer p a {
	color: var(--blue);
	text-decoration: none !important;
}
.getting-started__footer p a:hover {
	text-decoration: underline !important;
}
.account__action-bar__tab.active {
	border-bottom: 2px solid var(--yellow);
}
.account__section-headline a.active::after {
	border-color: transparent transparent var(--yellow);
}
.status__action-bar__counter__label {
	color: var(--lightgrey);
}
.column-header > button {
	color: var(--yellow);
}
.column-header__button.active,
.column-header__button.active:hover {
	background: var(--notifbrown);
}
.setting-toggle__label,
.column-settings__section {
	color: var(--lightgrey);
}
.text-btn {
	color: var(--lightgrey);
}
.notification-favourite {
	background-color: var(--darkest);
}
.text-icon-button:hover,
.text-icon-button:active,
.text-icon-button:focus {
	color: var(--lightgrey);
	-webkit-transition: color 200ms ease-out;
	transition: color 200ms ease-out;
}
.detailed-status__application,
.detailed-status__datetime {
	color: var(--blue);
}
.load-gap {
	border-bottom: 1px solid var(--darkest);
}
.load-more {
	color: var(--lightgrey);
}
.load-more:hover {
	background-color: var(--darkest);
	color: var(--lightgrey);
}
.privacy-dropdown__option.active:hover {
	background: var(--blue);
}
.privacy-dropdown__option:hover,
.privacy-dropdown__option.active {
	background: var(--blue);
}
.privacy-dropdown.active .privacy-dropdown__value.active .icon-button {
	background-color: var(--blue);
}
.compose-form .compose-form__warning {
	background: var(--lightgrey);
}
.dropdown-menu {
	background: var(--lightgrey);
}
.dropdown-menu__item a {
	background: var(--lightgrey);
}
.dropdown-menu__item a:focus,
.dropdown-menu__item a:hover,
.dropdown-menu__item a:active {
	background: var(--blue);
	color: var(--darkbrown);
}
.status__content .status__content__spoiler-link,
.reply-indicator__content .status__content__spoiler-link {
	background: var(--yellow);
	color: var(--darkest);
}
.status__content .status__content__spoiler-link:hover,
.reply-indicator__content .status__content__spoiler-link:hover {
	background: var(--yellow);
	text-decoration: none;
	opacity: .8;
}
.emoji-button img {
	-webkit-filter: grayscale(70%);
	filter: grayscale(70%);
}
.focusable:focus {
	outline: 0 ! important;
	background: var(--darkbrown);
}
.column-header.active .column-header__icon {
	color: var(--yellow);
	text-shadow: none ! important;
}
.account__header__fields a {
	color: var(--yellow);
	text-decoration: none;
}
.fa-user-times::before {
	color: var(--yellow);
}
.tabs-bar {
	background: var(--darkest);
}
.tabs-bar__link {
	border-bottom: 2px solid var(--darkest);
}
.tabs-bar__link.active {
	border-bottom: 2px solid var(--yellow);
	color: var(--yellow);
}
.floating-action-button,
.floating-action-button:hover,
.floating-action-button:focus,
.floating-action-button:active {
	background: var(--yellow);
}
.video-player__spoiler.active:hover,
.video-player__spoiler.active:active,
.video-player__spoiler.active:focus {
	color: #00CE99B3;
}
.video-player__spoiler {
	color: var(--blue);
}
.media-spoiler {
	background: var(--darkbrown);
	color: var(--yellow);
}
.media-spoiler:hover,
.media-spoiler:active,
.media-spoiler:focus {
	background: var(--darkbrown);
	color: var(--yellowlight);
}
.account__disclaimer {
	color: var(--lightgrey);
}
.account__action-bar {
	border-top: 0px solid var(--darkest);
	border-bottom: 0px solid var(--darkest);
}
.account__action-bar__tab {
	border-right: 0px solid var(--darkest);
}
.account__header .account__header__fields dl {
	border-top: 0px solid var(--darkbrown);
}
.account__header__content a:hover {
	color: var(--yellow);
	text-decoration: underline !important;
}
.account__header__bio .account__header__fields {
	border-top: 0px solid var(--darkest);
}
.account__header__fields dl:last-child {
	border-bottom: 0px solid var(--darkest);
}
.account__header__fields {
	border-color: transparent !important;
}
.account__action-bar__tab > span {
	color: var(--lightgrey);
}
.fa-thumb-tack::before {
	color: var(--lightgrey);
}
.account__section-headline a {
	color: var(--lightgrey);
}
.text-icon-button.active {
	color: var(--yellow);
}
.button {
	color: var(--darkest);
}
.button:disabled {
	color: var(--darkbrown);
	cursor: default;
}
.fa-bell::before {
	content: "\f075";
}
.fa-home::before {
	content: "\f10e";
}
.fa-group::before,
.fa-users::before {
	content: "\f0e8";
}
.fa-hourglass::before {
	color: var(--yellow)
}
.fa-sliders::before {
	color: var(--yellow);
}
.search-popout {
	background: var(--lightgrey);
	color: var(--darkbrown);
}
.search-popout h4 {
	color: var(--darkbrown);
}
.search-popout em {
	color: var(--darkbrown);
}
.attachment-list.compact .fa {
	color: var(--yellow);
}
.status__prepend {
	color: var(--yellow);
}
.detailed-status__display-name strong {
	color: var(--blue);
}
.status-card__actions button,
.status-card__actions a {
	color: var(--yellow);
}
.status-card__image {
	background: transparent;
}
.icon-button.inverted.active {
	color: var(--yellow);
}
.getting-started__wrapper,
.getting-started,
.flex-spacer {
	background: var(--darkest);
}
.focusable:focus .status.status-direct {
	background: var(--notifbrown)
}
.muted .emojione {
	opacity: 1;
}
.empty-column-indicator,
.error-column {
	color: var(--lightgrey);
	background: var(--darkest);
}
.column-inline-form {
	background: var(--darkbrown);
	color: var(--lightgrey);
}
.setting-text {
	color: var(--lightgrey);
	border-bottom: 2px solid var(--lightgrey);
}
.column-back-button--slim-button {
	top: -49px;
}
.drawer__inner.darker {
	background: var(--darkest);
}
.search-results__header {
	background-color: var(--darkest);
	color: var(--lightgrey);
}
.search-results__section h5 {
	background: var(--darkest);
	border-bottom: 1px solid var(--yellow);
	color: var(--lightgrey);
}
.trends__item__name {
	color: var(--lightgrey);
}
.trends__item__name a {
	color: var(--yellow);
}
.trends__item__sparkline path {
	stroke: var(--yellow) !important;
}
.account__section-headline button,
.notification__filter-bar button {
	background: var(--darkest);
}
.account__section-headline,
.notification__filter-bar {
	background: var(--notifbrown);
}
.account__section-headline a,
.account__section-headline button,
.notification__filter-bar a,
.notification__filter-bar button {
	color: var(--lightgrey);
}
.account__section-headline a.active::after,
.account__section-headline button.active::after,
.notification__filter-bar a.active::after,
.notification__filter-bar button.active::after {
	bottom: -1px;
	border-color: transparent transparent var(--yellow);
}
.account__section-headline a.active,
.account__section-headline button.active,
.notification__filter-bar a.active,
.notification__filter-bar button.active {
	color: var(--yellow);
}
.account__moved-note {
	background: var(--darkbrown);
	border-top: 1px solid var(--darkest);
	border-bottom: 1px solid var(--darkest);
}
.account__moved-note__message {
	color: var(--yellow)
}
.emoji-mart-anchors {
	color: var(--lightgrey);
}
.emoji-mart-anchor:hover {
	color: var(--yellow);
}
.emoji-mart-bar:first-child {
	background: var(--darkest);
	border-bottom: 1px solid var(--yellow);
}
.emoji-mart-anchor-selected:hover {
	color: var(--yellow);
}
.emoji-mart-anchor-bar {
	background-color: var(--yellow);
}
.emoji-mart-search {
	background: var(--darkest);
}
.emoji-picker-dropdown__menu {
	background: var(--darkest);
}
.emoji-mart-category-label span {
	background: var(--darkest);
	color: var(--lightgrey);
}
.emoji-mart-search input {
	background: var(--darkbrown);
	color: var(--lightgrey);
	border: 1px solid var(--darkest);
}
.emoji-mart-scroll {
	background: var(--darkest);
}
.emoji-mart-anchor-selected {
	color: var(--yellow);
}
.reduce-motion button.icon-button.active i.fa-retweet {
	color: var(--yellow);
}
.notification__message .fa {
	color: var(--yellow);
}
.emoji-mart-no-results {
	color: var(--lightgrey);
}
.emoji-picker-dropdown__modifiers__menu {
	background: var(--darkbrown);
}
.activity-stream .entry {
	background: var(--darkest);
}
.public-layout .header {
	background: var(--darkest);
}
.button.logo-button {
	background: var(--darkbrown);
	color: var(--yellow);
	border: 1px solid var(--yellowlight);
}
.button.logo-button:active,
.button.logo-button:focus,
.button.logo-button:hover {
	background: var(--yellow);
	color: var(--darkbrown)
}
.button.logo-button:active svg path:last-child,
.button.logo-button:focus svg path:last-child,
.button.logo-button:hover svg path:last-child {
	fill: var(--yellow);
}
.button.logo-button:active svg path:first-child,
.button.logo-button:focus svg path:first-child,
.button.logo-button:hover svg path:first-child {
	fill: var(--darkbrown);
}
.public-layout .public-account-header__tabs__tabs .counter.active::after {
	border-bottom: 4px solid var(--yellow);
}
.account__header__fields dt,
.account__header__fields dd {
	color: var(--lightgrey);
	background-color: var(--darkest);
	box-sizing: inherit;
	text-align: left;
}
.account__header__fields dl {
	border-bottom: 0px solid var(--darkest);
}
.public-layout .public-account-bio .account__header__content {
	color: var(--lightgrey);
	background-color: var(--darkest);
}
.public-layout .public-account-bio .roles,
.public-layout .public-account-bio__extra {
	color: var(--lightgrey);
	background-color: var(--darkest);
}
.public-layout .header .nav-link {
	color: var(--yellow);
}
.public-layout .public-account-header__tabs__tabs .counter .counter-label {
	color: var(--lightgrey);
}
.public-layout .public-account-bio .account__header__fields a {
	color: var(--yellow);
}
.public-layout .public-account-header__tabs__tabs {
	background-color: var(--darkest);
}
.public-layout .public-account-header__bar .avatar img {
	border: 4px solid var(--yellow);
	background: var(--yellow);
}
.simple_form p.hint {
	color: var(--lightgrey);
}
.hero-widget__text {
	background: var(--darkest);
	color: var(--lightgrey);
}
.public-layout .public-account-header {
	background-color: var(--darkest);
}
.status-card__description {
	color: var(--lightgrey);
}
.card__bar {
	background: var(--darkest);
}
.card__bar .display-name span {
	color: var(--blue);
}
.card__bar .display-name strong {
	color: var(--yellow);
}
.card > a:active .card__bar,
.card > a:focus .card__bar,
.card > a:hover .card__bar {
	background: var(--darkbrown);
}
.public-layout .footer h4,
.public-layout .footer .grid .column-2 h4 a {
	color: var(--blue)
}
.public-layout .footer,
.public-layout .footer ul a {
	color: var(--lightgrey);
}
.public-layout .footer .brand svg path {
	fill: var(--yellow)
}
.public-layout .footer .brand:active svg path,
.public-layout .footer .brand:focus svg path,
.public-layout .footer .brand:hover svg path {
	fill: var(--yellowlight);
}
.button.logo-button svg path:first-child {
	fill: var(--yellow);
}
.button.logo-button svg path:last-child {
	fill: var(--darkest);
}
.public-layout .header .nav-button {
	background: var(--darkbrown);
}
.public-layout .header .nav-button:active,
.public-layout .header .nav-button:focus,
.public-layout .header .nav-button:hover {
	background: var(--yellow);
}
.public-layout .header .nav-link:active,
.public-layout .header .nav-link:focus,
.public-layout .header .nav-link:hover {
	color: var(--darkbrown);
}
.public-layout .header .brand:active,
.public-layout .header .brand:focus,
.public-layout .header .brand:hover {
	background: var(--darkbrown);
}
.public-layout .public-account-header__image {
	background: var(--darkest);
	opacity: .2;
}
.video-player__seek__handle {
	background: var(--yellow);
}
.video-player__seek__progress {
	background: var(--yellow);
}
.video-player__volume__current {
	background: var(--yellow);
}
.video-player__volume__handle {
	background: var(--yellow);
}
.regeneration-indicator {
	color: var(--blue);
	background: var(--darkest);
}
.regeneration-indicator__label strong {
	color: var(--yellow);
}
.icon-button.inverted.active.disabled {
	color: var(--yellow);
}
.media-modal__button--active {
	background-color: var(--yellow);
}
.status.status-direct:not(.read) {
	background: var(--notifbrown);
}
.focusable:focus .detailed-status,
.focusable:focus .detailed-status__action-bar {
	background: var(--darkbrown);
}
.display-name__html {
	color: var(--yellow);
}
.compose-form__poll-wrapper {
	background-color: var(--darkbrown);
}
.compose-form__poll-wrapper .button.button-secondary {
	background-color: var(--yellow);
}
.compose-form__poll-wrapper .poll__footer button,
.compose-form__poll-wrapper .poll__footer select {
	background-color: var(--yellow);
	border: 1px solid var(--yellow);
}
.compose-form__poll-wrapper {
	border-top: 1px solid var(--darkbrown);
}
.poll__text input[type="text"] {
	background: var(--lightgrey);
}
.poll__input {
	border: 1px solid var(--yellow);
}
.compose-form__poll-wrapper .poll__footer {
	border-top: 1px solid var(--darkbrown);
}
.poll__chart {
	background: var(--yellow);
	min-height: 5px;
}
.poll__chart.leading,
.muted .poll__chart.leading {
	background: #00CE99;
	min-height: 5px;
}
.poll__chart {
    top: 30px;
    height: 20%;
}
.muted .poll__chart {
    background: var(--yellowlight);
	min-height: 5px;
}
.muted .poll {
	color: var(--lightgrey);
	text-shadow: 1px 1px 2px var(--darkest);
}
.poll__link {
	color: var(--yellow);
}
.poll__footer {
	color: var(--yellow);
}
.account__header__tabs__name h1 small {
	color: var(--blue);
}

.account__header__extra__links a {
	color: var(--yellow);
}
.account__header__extra__links a strong {
	color: var(--yellow);
}
.account__header__bio .account__header__fields a {
	color: var(--blue);
}
