.compose-form__sensitive-button {
	background-color: var(--darkbrown);
}
.compose-form__sensitive-button .icon-button {
	color: var(--lightgrey);
}
.icon-button.active {
	color: var(--yellow);
}
.upload-area {
	background: rgba(50, 56, 55, .8);
}
.upload-area__background {
	background: var(--darkbrown);
}
.upload-area__content {
	color: var(--yellow);
	border: 2px dashed var(--yellow);
}
.boost-modal__action-bar,
.confirmation-modal__action-bar,
.mute-modal__action-bar {
	background: var(--darkest);
}
.confirmation-modal__action-bar .confirmation-modal__cancel-button,
.confirmation-modal__action-bar .confirmation-modal__secondary-button,
.confirmation-modal__action-bar .mute-modal__cancel-button,
.mute-modal__action-bar .confirmation-modal__cancel-button,
.mute-modal__action-bar .confirmation-modal__secondary-button,
.mute-modal__action-bar .mute-modal__cancel-button {
	background-color: var(--yellowlight);
	color: var(--lightgrey);
}
.actions-modal,
.boost-modal,
.confirmation-modal,
.mute-modal,
.report-modal {
	background-color: var(--darkest);
	color: var(--lightgrey);
	border: 1px solid var(--yellowlight);
}
.button:active,
.button:focus,
.button:hover {
	background-color: var(--yellow);
	opacity: .8;
}
.media-modal .extended-video-player video,
.zoomable-image img {
	border: 2px dashed var(--yellowlight);
	border-radius: 5px;
	padding: .75em;
	background-color: var(--darkest);
}
.modal-root__overlay {
	background: var(--darkest)!important;
	opacity: .95
}
.media-modal__nav {
	background: transparent;
}
.fa-close::before,
.fa-remove::before,
.fa-times::before {
	color: var(--yellow);
}
.fa-chevron-right::before {
	color: var(--yellow);
}
.fa-chevron-left::before {
	color: var(--yellow);
}
.fa.fa-chevron-left.column-back-button__icon.fa-fw::before {
	color: var(--blue);
}
.spoiler-button__overlay__label {
	color: var(--yellow);
}
.button.logo-button.button--destructive:active,
.button.logo-button.button--destructive:focus,
.button.logo-button.button--destructive:hover {
	background: var(--yellow);
	color: var(--darkest)
}
.relationship-tag {
	color: var(--darkest);
	background-color: var(--yellow);
	font-weight: bold;
	padding: 5px;
	border: 1px solid var(--yellow);
	opacity: 1;
	box-shadow: 2px 2px 5px var(--darkest);
}
.relationship-tag:hover {
	opacity: .75;
}
.account__header__tabs__buttons .icon-button {
	border: 1px solid var(--yellowlight);
}
.fa-ellipsis-v::before {
	color: var(--yellow);
}
.embed-modal,
.error-modal,
.onboarding-modal {
	background: var(--darkbrown);
	color: var(--lightgrey);
	padding: .5em;
	border: 2px dashed var(--yellow);
}
.embed-modal .embed-modal__container .embed-modal__html {
	background: var(--darkest);
	color: var(--lightgrey);
}
body.embed {
	background: var(--darkest);
}
.embed-modal h4 {
	font-size: 18px;
	border-bottom: 1px solid var(--yellow);
	color: var(--yellow);
}
.report-modal__statuses .status__content,
