.status__content {
    hyphens: auto;
}
.account__avatar,
.account__avatar-overlay-base,
.account__avatar-overlay-overlay {
    border-radius: 50px;
}
.emoji-mart-anchors svg {
  fill: var(--lightgrey);
}
.account__header__bio .account__header__joined {
  color: var(--yellow);
}
.fa-bullhorn::before {
  color: var(--yellow);
}
.reply-indicator__content .status__content__spoiler-link:focus, .reply-indicator__content .status__content__spoiler-link:hover, .status__content .status__content__spoiler-link:focus, .status__content .status__content__spoiler-link:hover {
  background:  var(--yellow);
}
.language-dropdown__dropdown__results__item {
  color: var(--lightgrey);
}
.language-dropdown__dropdown__results__item__common-name {
  color: var(--blue);
}
.nothing-here {
  background: var(--notifbrown);
}
.circular-progress {
  color: var(--blue);
}
.scrollable .account-card {
  background: var(--darkbrown);
}
.account-card__counters__item small {
  color: var(--lightgrey);
}
.account__header__bio .account__header__fields {
  background: var(--notifbrown);
}
.language-dropdown__dropdown__results__item:active, .language-dropdown__dropdown__results__item:focus, .language-dropdown__dropdown__results__item:hover {
  background: var(--darkbrown);
}
.language-dropdown__dropdown__results__item.active {
  background: var(--darkbrown);
}
.language-dropdown__dropdown__results__item.active:hover {
  background: var(--darkbrown);
}
.dismissable-banner {
  background: var(--notifbrown);
  border-bottom: 0px solid;
  display: none;
}
.link-footer p a, .link-footer p {
  color: var(--yellowlight);
}
.server-banner__introduction,.sign-in-banner p {
  color: var(--yellow);
}
.server-banner h4 {
  color: var(--yellow);
}
.server-banner__number-label {
  color: var(--lightgrey);
}
.button.button-tertiary {
  color: var(--yellow);
  border: 1px solid var(--yellow);
}
.button.button-tertiary:active, .button.button-tertiary:focus, .button.button-tertiary:hover {
  background: var(--yellow);
  color: var(--darkest);
}
.notification.unread::before, .status__wrapper.unread::before {
  border-left: 0px solid #8c8dff;
}
.picture-in-picture__header .display-name span {
  color: var(--yellow);
}
.applications-list__item, .filters-list__item {
  background: var(--notifbrown);
  border: 1px solid var(--yellowlight);
}
.permissions-list__item {
  color: var(--yellow);
  border-bottom: 1px solid var(--yellowlight);
}
.permissions-list__item__text__type {
  color: var(--lightgrey);
}
.announcements-list__item__meta, .filters-list__item__meta {
  color: var(--lightgrey);
