style.css:
@-moz-document domain("youtube.com"){

/* ======= Theme Youtube Non Premium ======= */  

/* Enlever le bouton Premium "Rejoindre" à gauche a côté du nom de la chaine. */
#sponsor-button{display: none !important;}

/* Enlever la zone Premium "Rejoignez cette chaîne" a l'accueil de la chaine. */
ytd-recognition-shelf-renderer{display: none;}

/* Enlever le dialogue de propositions "Devenir membre de cette chaîne" */
yt-mealbar-promo-renderer{display: none;}

/* Enlever le bouton Premium "Télécharger" de la liste des boutons */
ytd-download-button-renderer{display: none;}
ytd-menu-service-item-download-renderer{display: none;}

/* Enlever le bouton Premium "Merci" de la liste des boutons */
button[aria-label="Merci"]{display: none;}

/* Enlever le bouton Premium à gauche "YouTube Premium" */
a[title="YouTube Premium"]{display: none;}

/* Enlever le bouton Premium à gauche "YouTube TV" */
a[title="YouTube TV"]{display: none;}

/* Enlever le bouton Premium à gauche "Films et TV" */	
a[title="Films et TV"]{display: none;}

/* Enlever les propositions "Films Youtube" à droite du lecteur video*/
ytd-compact-movie-renderer{display: none;}

/* Enlever le champ "S'abonner à YouTube Premium" dans les contenus associés en bas de la description du lecteur */
#premium-upsell-link{display: none;}

/* Enlever le champ "Achats et abonnements" du menu avatar en haut à droite */
a[href*="/paid_memberships"]{display: none;}

/* Enlever les emojis payant en commentaire */
button[aria-label="Emoji personnalisés"]{display: none;}
yt-emoji-picker-upsell-category-renderer[aria-label*=" to unlock their custom emoji."]{display: none;}
#categories.yt-emoji-picker-renderer>*.yt-emoji-picker-renderer:not(:first-child) {margin-top: 0px;}

/* Enlever la pub dans les recommandation de vidéos a droite */
ytd-ad-slot-renderer{display: none;}
  
/* Enlever le contenu sponsorisé à droite du lecteur */
#player-ads{display: none;}



/* ======= FIN Theme Youtube Non Premium ======= */    

/* ======= Conf Perso ======= */    

/* Réduire le vide inutile a droite du nom de la chaine pour forcer l'affichage du bouton "enrigistrer" */
#owner.ytd-watch-metadata {min-width: calc(30% - 6px);}

@media (min-width:600px) and (max-width:678px) {
ytd-menu-renderer[has-flexible-items] {width: 150%;}
}
@media (min-width:679px) and (max-width:1274px) {
#owner.ytd-watch-metadata {min-width: calc(18% - 6px)!important;flex-direction: column;}
ytd-watch-metadata[flex-menu-enabled] #actions.ytd-watch-metadata ytd-menu-renderer.ytd-watch-metadata {justify-content: flex-start;}
ytd-menu-renderer[has-flexible-items] {width: 150%;}
}

/* Augmenter la taille de la saisie des emojis */    
yt-emoji-picker-renderer {height: var(--yt-emoji-picker-renderer-height,285px);}

/* Enlever le bouton de Micro dans la recherche */    
button[aria-label="Effectuer une recherche vocale"]{display: none;}

/* Enlever la partie prévention en bas du lecteur */ 
#clarify-box{display: none;}

/* Enlever la partie contenus associés en bas de la description */  
ytd-rich-metadata-renderer[component-style="RICH_METADATA_RENDERER_STYLE_TOPIC"]{display: none;}
ytd-rich-metadata-renderer[component-style="RICH_METADATA_RENDERER_STYLE_BOX_ART"]{display: none;}

/* ======= FIN Conf Perso ======= */
}
