style.css:
@-moz-document url-prefix("https://courses.fit.cvut.cz") {
header {
    background: #262626;
    padding: 5em 0;
}

.App {
    background: #323232;
}

.App.in-search {
    background: #323232;
}

.CoursesItemDetailed .code {
    background: #e5e5e5;
    color: #dfdfdf;
}

section h2 {
    font-size: 32px;
    font-weight: 800;
    margin: 0 0 1em 0;
    color: #a5a5a5;
}

.CoursesItem .name {
    color: #629edb;
}

.Upcoming .event {
    background: #5e5e5e;
    border: 1px solid rgba(0, 0, 0, .1);
    border-radius: 1em;
    box-shadow: 0 0.25em 0.5em rgba(0, 0, 0, .2);
}

.Upcoming .event .name > a {
    color: #91c8ff;
}

.Upcoming .event .time, .Upcoming .event .location {
    font-size: 1em;
    color: #cbcbcb;
}

.Upcoming .timeline > .date {
    background: #7caf00;
    color: white;
}

.Upcoming .timeline::before {
    border-top: 3px solid #7caf00;
}

.Upcoming .event .type::before {
    background: #cbcbcb;
}

.Upcoming .event .type {
    color: white;
}

.CoursesItemDetailed .column-grades li .description {
    color: #a5a5a5;
    font-size: 0.9em;
}

.CoursesItemDetailed .column-grades li .value {
    display: inline-block;
    width: 2em;
    height: 2em;
    line-height: 2em;
    text-align: center;
    margin: 0.25em 1.25em;
    background: #bbbbbb;
    border: 1px solid rgba(0, 0, 0, .1);
    border-radius: 0.75em;
    box-shadow: 0 0.125em 0.25em rgba(0, 0, 0, .15);
    font-weight: 800;
    font-size: 1em;
    color: #555555;
}

.SemesterSelector .selected-item::after {
    background: #a5a5a5;
    clip-path: polygon(50% 100%, 0 0, 100% 0);
    -webkit-clip-path: polygon(50% 100%, 0 0, 100% 0);
}

.SemesterSelector h2 {
    color: #a5a5a5;
}

.CoursesItem .tags li {
    background: #5e5e5e;
    color: #dfdfdf;
}

.Courses .list.shortened::before {
background: linear-gradient(0deg, rgb(50 50 50) 0%, rgb(255 255 255 / 0%) 100%);
}

.button:not(.button-light), .button.button-light:hover {
    background: #5e5e5e;
    color: #dfdfdf;
}

.Footer {
    background: #262626;
    color: #999999;
}

.search-results.search-field-focused .CoursesItem.link:first-child .column-info > a {
    background: #323232;
}

.CoursesItem .column-info > a:hover {
    background: #323232;
    text-decoration: none;
}

body {
    background: #323232;
}

.header {
     background: url('https:/images/cvut-logo.svvg');
}

.subheading-portal a {
    color: #33aaff;
}

.course-heading {
    color: #7caf00;
}

:root {
    --link-color: #33aaff;
  --light-bg-color: #d1d1d1;
}

main {
    color: #d7d7d7;
}

.page-footer {
    background: #262626;
}

.toc {
    background: rgb(78 78 78);
}

.toc h1, .toc h2, .toc h3 {
    color: #d7d7d7;
}

.admonition-block {
    background-color: #4e3737;
}

main h2 {
    color: white;
}

.app-menu li.user {
    background: #5e5e5e;
    border-radius: 5px;
}

.app-menu li.user.dropdown:hover, .app-menu li.user.dropdown:active {
    background: #5e5e5e;
    border-radius: 5px;
}

.app-menu a {
    color: white;
}

.menubar ul > li > ul {
    background: #1414145a;
    border-radius: 5px;
}

button.link {
    color: white;
}

.Upcoming .event.now {
    border: 2px solid #7caf00;
}

.Upcoming .event.now::before {
    color: #7caf00;
}

code {
  background: #454545;
}

.listing-block * code{
    background:#d1d1d1;
}
}
