style.css:
.styles__titleText___3o2CN-camelCase {
    font-size: 80px;
    font-family: Titan One,sans-serif;
    color: #2c2c2c;
    text-shadow: 2px 2px 8px grey;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
    width: 100%;
    text-align: center;
}
.styles__header___1c59U-camelCase {
    background-color: #333;
    width: 100%;
    height: 55px;
    padding-bottom: 6px;
    box-shadow: inset 0 -6px rgb(0 0 0 / 20%);
    box-sizing: border-box;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    z-index: 10;
    position: absolute;
    top: 0;
    left: 0;
}
.styles__titleText___2nfVS-camelCase {
    font-size: 100px;
    font-family: Titan One,sans-serif;
    color: #4e4e4e;
    text-shadow: 2px 2px 8px grey;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
}
.styles__buttonRow___2O5tw-camelCase {
    margin-top: 30px;
    width: 285px;
    display: flex;
    flex-direction: column;
    align-content: center;
    justify-content: center;
    align-items: center;
}
.styles__loadGameButton___383pl-camelCase {
    position: absolute;
    bottom: 10px;
    left: 50%;
    padding: 5px 15px 9px;
    box-shadow: inset 0 -4px rgb(0 0 0 / 20%), 0 0 4px rgb(0 0 0 / 15%);
    font-size: 22px;
    width: 185px;
    background-color: #333;
    border-radius: 7px;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    outline: none;
    font-family: Nunito,sans-serif;
    font-weight: 700;
    color: rgb(255 255 255 / 0.9);
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
    transform: translateX(-50%);
    transition: .2s;
}
.styles__inputRow___wymbk-camelCase, .styles__randomButton___3CTp0-camelCase {
    display: flex;
    align-items: center;
    flex-direction: column;
}
.arts__body___3acI_-camelCase, .arts__hostBody___1Z9ui-camelCase {
   background-color: #333;
    overflow: hidden;
}
.styles__left___2qhHs-camelCase {
    position: absolute;
    top: 15px;
    left: 5%;
    height: calc(100% - 30px);
    width: calc(90% - 430px);
    border-radius: 10px;
    background-color: #333;
    padding: 10px 10px 19px;
    box-shadow: inset 0 -9px rgb(0 0 0 / 20%), 0 5px rgb(0 0 0 / 25%);
}
.styles__front___vcvuy-camelCase {
    color: #fff;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    text-align: center;
    padding: 5px;
    border-radius: 5px;
    transform: translateY(-4px);
    transition: transform .6s cubic-bezier(.3,.7,.4,1);
}
.styles__edge___3eWfq-camelCase {
    filter: brightness(11111110);
}
.styles__front___vcvuy-camelCase {
    filter: grayscale(1);
    color: #fff;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    text-align: center;
    padding: 5px;
    border-radius: 5px;
    transform: translateY(-4px);
    transition: transform .6s cubic-bezier(.3,.7,.4,1);
}
.styles__headerBox___KZu8g-camelCase {
    position: absolute;
    height: 20vh;
    width: 100vw;
    top: 0;
    left: 0;
    background-color: #363636;
    padding: 0 2vw 10px;
    box-shadow: inset 0 -10px rgb(0 0 0 / 20%), 0 6px rgb(0 0 0 / 20%);
    box-sizing: border-box;
    z-index: 10;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    animation: styles__one___3WiBR-camelCase 2s linear;
}
.styles__clientBg___2M2rn-camelCase {
    box-shadow: inset 0 -7px rgb(0 0 0 / 20%);
    background-color: #484848;
    border-radius: 10px;
}
.styles__clientNameText___TxzCV-camelCase {
    text-align: center;
    width: 75%;
    margin-left: 20%;
    height: 70%;
    padding-bottom: 7px;
    box-sizing: border-box;
    color: #ffffff;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    text-shadow: 1px 1px rgb(0 0 0 / 20%);
}
.styles__selectText___OpD7c-camelCase {
    top: 30px;
    width: calc(80% - 400px);
    min-width: 700px;
    padding-right: 70px;
    height: 90px;
    font-size: 56px;
    background-color: #404040;
    font-family: Titan One,sans-serif;
    text-shadow: 4px 4px rgb(0 0 0 / 20%);
    color: #fff;
    display: flex;
    align-items: center;
    justify-content: center;
    border-top-right-radius: 10px;
    border-bottom-right-radius: 10px;
    box-shadow: 0 4px rgb(0 0 0 / 20%);
    flex-wrap: nowrap;
    align-content: center;
}
.styles__leftBackground___3vASY-camelCase {
    background-color: #000000;
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: calc(100% - 500px);
    box-shadow: 4px 0 rgb(0 0 0 / 20%);
}
.styles__background___2LDVr-camelCase {
    position: fixed;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    background: linear-gradient(#646464,#000000);
    overflow: hidden;
}
.styles__infoDesc___1HRIm-camelCase {
    font-family: Titan One,sans-serif;
    color: #fff;
    font-size: 26px;
    text-align: center;
    width: 100%;
    padding: 8px 10px;
    background-color: #383838;
    border-radius: 7px;
    text-shadow: 2px 2px rgb(0 0 0 / 20%);
    box-shadow: 0 4px rgb(0 0 0 / 20%);
}
.styles__playButton___2Y5DP-camelCase {
    height: 60px;
    box-sizing: border-box;
    font-family: Titan One,sans-serif;
    font-size: 30px;
    text-shadow: 2px 2px rgb(0 0 0 / 20%);
    background-color: #2a2a2a;
    color: #fff;
    border-radius: 7px;
    display: flex;
    justify-content: center;
    align-items: center;
    padding-bottom: 7px;
    box-shadow: inset 0 -7px rgb(0 0 0 / 20%), 0 0 4px rgb(0 0 0 / 15%);
    -webkit-user-select: none;
    -moz-user-select: none;
}
.styles__mainContainer___1doVE-camelCase {
    width: 850px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #0c0c0c;
    border-radius: 10px;
    padding-bottom: 7px;
    box-shadow: inset 0 -7px rgb(0 0 0 / 20%), 0 0 4px rgb(0 0 0 / 15%);
    margin-bottom: 50px;
}
.styles__titleText___2xUQP-camelCase {
    color: #fff;
    text-align: center;
    font-size: 54px;
    font-weight: 700;
    padding: 25px 50px;
    width: 100%;
    box-sizing: border-box;
    font-family: Nunito,sans-serif;
    text-shadow: 0 4px rgb(0 0 0 / 20%);
    background-color: #505050;
    box-shadow: 0 6px rgb(0 0 0 / 20%);
    border-top-right-radius: 10px;
    border-top-left-radius: 10px;
}
