style.css:
@-moz-document domain(dtf.ru) {
:root {
  --color-success: #2EA83A;
  --color-error: #E92A40;
}
.comment .comment-writing {
  order: 999;
}
.comment__text~.comment__media,
.comment__attaches {
    margin-top: 6px;
    order: 9;
}
.comment__action {
  order: 10;
}
.like-button--action-dislike {
  -ms-flex-order: 6;
  order: 6;
}
.like-button--action-like {
  -ms-flex-order: 7;
  order: 7;
  margin-right: 0 !important;
  flex-direction: row-reverse;
  margin-left: 15px;
}
.comment__content .like-button--action-dislike,
.comment__content .like-button--action-like {
  margin-top: -64px;
}
.like-button--action-like .like-button__icon,
.like-button--action-dislike {
  background-size: 12px;
  background-repeat: no-repeat;
  background-position: center;
}
.like-button--action-like .like-button__icon {
  background-image: url("data:image/png;base64,");
}
.like-button--action-like.like-button--active .like-button__icon {
  background-image: url("data:image/png;base64,");
}
.like-button--action-dislike {
  color: var(--color-error);
  background-position: 4px center;
  background-image: url("data:image/png;base64,")
}
.like-button--action-dislike.like-button--active {
  background-image: url("data:image/png;base64,")
}
.like-button__icon {
  margin-left: 0px;
}
.like-button__icon>svg,
.like-button__lottie {
  opacity: 0;
}
.like-button--lottie-animated .like-button__icon {
  visibility: visible !important;
}
.like-button__count {
  margin-right: 15px;
  margin-left: 0;
  font-weight: 500;
}
.comment__text {
  -ms-flex-order: 8;
  order: 8;
}
.comment__expand-branch,
.comment--with-no-replies .comment__load-more {
  -ms-flex-order: 99;
  order: 99;
  -ms-flex-preferred-size: 100%;
  flex-basis: 100%;
}
.like-button__icon::before {
  background-color: var(--color-success);
}
.like-button--action-dislike .like-button__icon::before {
  background-color: var(--color-error);
}
.like-button .like-button__count,
.like-button:hover .like-button__count {
  color: var(--color-success);
}
.like-button--action-dislike .like-button__count {
  color: var(--color-error);
  margin-right: 0;
  margin-left: 15px;
}
.content-footer>*:first-child {
  -ms-flex-order: 99;
  order: 99;
  margin-right: 0;
}
.comment .like-button--action-like:hover .like-button__icon::before {
  opacity: 0;
}
.comment .like-button--action-like .like-button__icon:hover::before {
  opacity: 0.12;
}
}
