style.css:
@-moz-document domain("concursosnobrasil.com") {
body {
    color: #fff;
    background-color: black;
  }

  body.bs-theme,
  body.bs-theme .btn-bs-pagination,
  body.bs-theme .body-typo {
    background-color: black;
  }

  li {
    color: white;
  }

  p {
    color: #cbcbcb;
  }

  a {
    color: #fff;
  }

  img {
    height: 2rem;
    width: 150px;
    background-color: white;
    position: absolute;
  }

  nav {
    margin-left: 150px;
  }

  .full-screen-menu .nav-menu li a {
    background-color: #4f4e4ead;
    border-right: 35px solid #606060;
    margin-left: 5px;
    margin-right: 5px;
    color: #cbcbcb;
  }

  #header {
    height: 2rem;
  }

  .site-header .main-menu > li > a {
    color: #ffffff;
  }

  .listing-item-text-1 .title a {
    color: white;
  }

  .site-header.header-style-5 .main-menu-container.menu-actions-btn-width-1,
  .site-header.header-style-6 .main-menu-container.menu-actions-btn-width-1,
  .site-header.header-style-8 .main-menu-container.menu-actions-btn-width-1 {
    background-color: #020202;
  }

  #menu-estados {
    border: 1px solid #c3c3c3;
    background-color: #000000;
  }

  .site-footer.full-width .copy-footer {
    color: aqua !important;
    background-color: black;
  }

  .list-concursos table tbody td {
    color: #fff1f1;
  }

  .list-concursos table tbody td,
  .list-concursos table thead th {
    border: 1px solid #727272;
    padding: 10px 10px 9px;
  }

  #menu-estados nav a {
    background: #560000;
    color: #ffffff;
  }

  .menu-ufs-container {
    background-color: #53534d;
  }

  .menu-ufs-container ul li a {
    background-color: #000000;
    color: #81beff;
  }

  h3,
  .h3,
  .heading-3 {
    color: aliceblue;
  }

  .box-scroll a {
    background: #000000;
    color: #ffffff;
  }

  .post-summary,
  .post-meta,
  .post-meta a {
    color: #fffefe !important;
  }

  .section-heading.sh-t2.sh-s2 {
    background: #000000 !important;
  }

  .list-concursos table thead th {
    background-color: #000;
    color: #fff;
    text-shadow: #6a6a6a 1px 1px 1px;
  }

  .list-concursos .post-title {
    color: #fffefe;
  }

  #menu-estados nav a {
    color: #ffffff;
  }

  .list-concursos table tr.destaque {
    background: #617362;
  }

  #conteudo article .article-content {
    font-size: 20px;
    position: inherit;
    color: #333;
    margin-left: -600px;
  }

  .taxonomy table tbody td {
    color: #d7d7d7;
  }

  .taxonomy table tbody tr.destaque {
    background: #434343;
  }

  .taxonomy table thead th {
    background-color: #5d5d5d;
    color: #fff;
    text-shadow: #000 1px 1px 1px;
  }

  #conteudo > header h1,
  #conteudo > .hentry > header h1 {
    color: #bdbdbd;
  }

  #conteudo > header h2 {
    color: #b7b7b7;
  }
}
