style.css:
@-moz-document domain("media.ibroadcast.com") {
:root {
	--ibroadcast-logo: url("https://logo.ibroadcast.com/ibroadcast-dark-compact.svg");
	--ibroadcast-logo-opacity: 1;

	--container-size: 175px;

	--background-color: #111111;
	--container-background-color: black;
	--archived-hover-background-color: #000;
	--background-color-rgba: rgba(255,255,255,.2);
	--library-background-color: black;
	--input-background-color: black;
	--alt-background-color: black;
	--playlist-panel-background-color: black;
	--notification-background-color: black;

	--background-color-container-overlay: rgba(0, 0, 0, .8);
	/* --playlist-overlay-background: rgba(0, 0, 0, .8); */
	--playlist-overlay-background: black;

	--button-active-background-color: black;
	--button-border-color: transparent;
	--button-background-hover-color: black;
	--button-background-hover-color-alt: black;
	--button-text-hover-color: black;

	--link-color: black;
	
	--slider-fill-background-color: white;
	--slider-background-color: black;
	--slider-background-color-buffering: gray;

	--scrollbar-track-color: black;
	--scrollbar-thumb-color: white;
	--scrollbar-thumb-hover-color: gray;

	--font-size-sm: 12px;
	--font-color: #CCC;
	--archived-background-color: #222;
	--archived-font-color: #666;
	--title-font-color: light-gray;
	--playing-color: #a2ddfb; /* very very light blue */
	--tag-active-color: white;
	--tag-active-font-color: #EEE;
	--search-active-color: gray;
	--search-active-font-color: black;
	--alt-font-color: gray; 
	--alt-border-color: gray;
	--border-color: gray;
	--ui-droppable-active: gray;

	--shadow-color: gray;
	--notification-shadow-color: black;
	--container-name-color: white;
	--container-subtitle-color: gray;

	--hr-color: #111;
	--icon-remove: url("https://media.ibroadcast.com/svg/dark/remove.svg");
