style.css:
@-moz-document domain("zalo.me"),
regexp("http(s?)://[^/]*\\.zalo(\\.me)?(\\.[a-z][a-z])?/.*"),
regexp("http(s?)://zalo(\\.me)?(\\.[a-z][a-z])?/.*")
{

/* Dark Mode for Zalo Web */
/* by 2023 QuangDD x Kapok Studios */
/* #More about us: studio.thekapoks.com */

/* Begin - Zalo Login page */
body {
    background: #000;
}

.body .body-container .tabs {
    background-color: #000;
    border-bottom: 1px solid rgba(161,161,161,0.5);
}

.header h2 {
    color: #fff;
}

.body .body-container .form-signin {
    background-color: #000;
}

.body .body-container .tabs ul li a.active {
    color: #fff;
}

.body .body-container .tabs ul li a:hover {
    color:#fff;
}

.body .body-container .tabs ul li a {
    background-color: #2e2e2e;
    color: #adadad;
}

.body .body-container .username {
    color: #f5f5f5;
}

.body .body-container .form-signin .line-form.has-ico i {
    color: #919191;
}

.body .body-container .form-signin .line-form input {
    color: #fff;
}

.btn.btn-default {
    background-color: #282828;
}

.body .body-container .btn-wrap-more .login-or a {
    color: #9b9b9b;
}

.animated.delay-1s {
    -webkit-animation-delay: 0s; 
    animation-delay: 0s; 
}

.data-tooltip {
    background-color: #376281;
    color: #fff;
}

.data-tooltip:after {
    border-top: 6px solid transparent;
    border-bottom: 6px solid transparent;
    border-right: 6px solid #376281;
}

.body .body-container .bottom {
    background-color: #181818;
}

.bg-svg {
    background-color: #13202c;
}

.bg-svg svg {
  mix-blend-mode: saturation;
}

.footer p {
    color: #c9c9c9;
}
/* End - Zalo Login page */

/* Begin - Zalo Chat page */
.chat-onboard{
  height: calc(100vh);
}

#main-tab {
    background-image: linear-gradient(#0091ff,#002541);
}

.tds-bubble-info-ecard__container {
    background-color: #141414cc;
  }
  
.bubble-message-status {
    background-color: var(--BA20);
}





.event-message>div, .onboard-message>div {
    background: #000000ad;
    color: #b1bdcc;
}

.card.card--undo>span {
    color: #ffffff60;
}

:root{
  --bg-default: #000;
  --title-bar: #151515;
  --surface-alt: #000;
  --item-wa-hover: #3a3a3b;
  --item-wa-select: #141515;
  --text-primary: #E4E6EB;
  --surface-background: #303030;
  --surface-background-subtle: #282828;
  --layer-background:#1c1c1c;
  --layer-background-subtle:#1e2933;
  --layer-background-hover:#313131;
  --layer-background-selected:#000000;
  --button-neutral-normal:#333333;
  --button-neutral-hover:#444444;
  --button-neutral-focus-bg: #323232;
  --button-tertiary-primary-pressed: #c7e0ff;
  --B10: #242526;
  --BA50: rgba(255,255,255,0.39);
  --B20: #1a1a1a;
  --WA100:#000;
  --border:#343535;
  --icon-primary:#2075e3;
  --button-secondary-normal:#070708;
  --button-secondary-hover:#1e2227;
  --button-secondary-pressed:#252532;
  --white-message:#1c1c1c;
  --white-message-selected:#1a1e22;
  --white-quote:#272829;
  --button-neutral-text:#2174e0;
  --button-tertiary-neutral-hover:#2d2e30;
  --button-tertiary-neutral-pressed:#1c1d1e;
  --button-tertiary-neutral-focus-bg:#0e0e0f;
  --button-tertiary-neutral-focus-text:#e0ddff;
  --button-primary-disabled: #323e4f;
  --button-primary-disabled: #323e4f;
  --blue-message: #0c0c0c;
  --blue-message-selected: #181c20;
  --blue-quote: #242930;
  --blue-quote-selected: #2d3746;
  --text-errors: #ff8e8e;
  --field-bg-filled: #282828;
  --field-bg-filled-hover: #292a2c;
  --field-bg-outline: #000;
  --field-bg-outline-hover: #252525;
  --background-inactiveness: #000;
  --background-inactiveness-subtle: #56585b;
  --background-inactiveness-disabled: #3a3b3b;
  --text-information: #1d70eb;
}

#contact-search-input {
    color: #fff;
}

.message-view {
 background-color:#282828;
}

.chat-date{
  color:#959595;
}

.chat-message__actions{
      background-color: hsl(0deg 0% 0% / 50%);
}
.chat-message .extra-btn {
    background-color: rgb(0 0 0 / 39%);
    color: #0091ff;
}
  
.sticker-selector{
      background-color: #1c1c1c !important;
}

.sticker-selector__content__item {
    background-color: #000;
}

.zl-modal {
  background: rgb(0 0 0 / 66%);
}
/* End - Zalo chat screen */

}
