style.css:
@-moz-document regexp(".*jenkins32.*lsnlycee-ci/job.*") {
/* Pour rappel : 

#description > div > div:nth-child(3) = .etabs
#description > div > div:nth-child(3) > div = .etab
#description > div > div:nth-child(3) > div > div:nth-child(1) = .titre-etab
#description > div > div:nth-child(3) > div > div:nth-child(2) = .identite-etab
#description > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(1) = .code-rne
#description > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2) = .uaj-id
#description > div > div:nth-child(3) > div > div:nth-child(3) = .type-etab
#description > div > div:nth-child(3) > div > ul = .personnel

url de recupération du script : https://userstyles.org/styles/257140/jenkins32-lsl 
*/

#description > div:nth-child(1) > div:nth-child(1) > p:nth-child(1) {
    display: none;
}  


#description > div > div:nth-child(3) {
    display: flex;
    margin-top: 5px;
    margin-bottom: 10px;
    flex-grow: 1;
    flex-wrap: wrap;
}

#description > div > div:nth-child(3) > div {
    display: block;
    margin-left: 5px;
    margin-right: 5px;
    padding: 8px;
    flex-grow: 1;
    max-width: 20em;
}

#description > div > div:nth-child(3) > div:hover {
    background: rgba(0, 0, 0, 0.07);
}

#description > div > div:nth-child(3) > div > ul {
    margin: 0;
    padding: 0;
    padding-left: 15px;
    list-style-type: "– ";
    max-height: 400px;
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
}

#description > div > div:nth-child(3) > div > div:nth-child(3):before {
    content:"(";
}
#description > div > div:nth-child(3) > div > div:nth-child(3):after {
    content:")"
}
#description > div > div:nth-child(3) > div > div:nth-child(3) {
    display: block;
    font-style: italic;
}

#description > div > div:nth-child(3) > div > div:nth-child(1) {
    display: inline-block;
    font-size: 1.1em;
    font-weight: bold;
}

#description > div > div:nth-child(3) > div > div:nth-child(2) {
    display: block;
    margin-left: 5px;
    opacity: 80%;
    font-size: 0.95em;
}

#description > div > div:nth-child(3) > div > div:nth-child(2) div {
    display: inline;
}

#description > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(1):before {
    content:"RNE : ";
}
#description > div > div:nth-child(3) > div > div:nth-child(2) > div:nth-child(2):before {
    content:" ; id : ";
}
}
