style.css:
@-moz-document domain("old-games.ru") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#utilmenu li,

.bluetitle,
.catlinks,
.breadcrumb,
.gradient-box,
.gradient-gray-box,
.imageWrapper,
.news-image,
.ui-button,
.ui-state-default,
.ui-widget-content .ui-state-default,.ui-widget-header .ui-state-default,.ui-widget-header,

:focus,
html .ui-button.ui-state-disabled:active,
html .ui-button.ui-state-disabled:hover,
table,
ul.leftlist li,
ul.pager li
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.bluetitle,
.head,
.title
{
	font-size: 1.55rem!important;
}

.news-text,
.tiptext,

p
{
	font-size: 1.52rem!important;
}

.middlesmall,
.navTabs .publicTabs .navLink,
.node .nodeStats,
.nodeList .nodeDescription,
.small,

body
{
	font-size: 1.22rem!important;
}

.alt-titles-list,

[style="font-size:8pt"]
{
	font-size: .85rem!important;
}

.discussionListItem .secondRow,
.smallarticle,

ul.leftlist
{
	line-height: 1.5rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.pager-title,

ul.pager li a
{
	padding: 13px 26px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.ui-dialog .ui-dialog-content
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

body
{
	max-width: none!important;
}

body
{
	width: 98%!important;
}

#rside,
#lside {
    width: 20%;
}

.ui-dialog.ui-corner-all.ui-widget.ui-widget-content.ui-front
 {
    width: 26rem!important;
}
}

@-moz-document domain("old-games.ru") {
/* Font Settings */

.PanelScrollerOff .panel a:link,
.sidebar .secondaryContent a,

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.game_comments_text blockquote font:first-child,
.navTabs .navTab.PopupClosed:hover a,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.alt-titles-list,
.game_comments_text blockquote > font > font:last-child,
.mw-body,
.nodeList .nodeDescription,
.parsoid-body,
.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading,
.tiptext,
.ubsListSidebar .blogEntryList .extraData, .ubsListSidebar .blogEntryList .extraData a,

[style="background-color: ; color: #505050"],
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.bluetitle,
.head,
.ui-widget-header,

h1,
h2,
i
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.game_comments_title,

b,
h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.pairs dt,
.pairsColumns dt,
.pairsInline dt,
.pairsJustified dt,
.pairsRows dt,
.subForumsPopup .dt,

h5,
h6,
label,
strong
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.node .nodeLastPost .DateTime,
.sectionMain .posterDate a,

time
 {
	color: #07beff!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.game_comments_unregistred
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

}

@-moz-document domain("old-games.ru") {
/* Background Settings */

#main,

body,
html
{
	background: #000!important;
}

.ui-button .ui-icon,
.vector-menu-tabs li
{
	background-image: none!important;
}

.ui-widget-overlay {
    background: #000000b3;
}

/* Background 
_____________________________________________________________________________________________________*/

#lside,
#rside,

div.headerpanel
{
    background: #101010!important;
    background-color: #101010!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#QuickSearch,
#content .pageContent,
#nav li a,
#utilmenu,
#utilmenu li,

.navTabs .navTab.PopupClosed:hover,
.footer .pageContent,
.ui-widget-header,
.bglitegray,
.bgstripe1,.bgstripe2,
.breadcrumb,
.button,
.category_view .sectionMain,
.catlinks,
.conversation_list .sectionMain,.conversation_list_starred .sectionMain,.conversation_list_yours .sectionMain,
.discussionListItem,
.find_new_posts .section,
.formPopup,
.forum_list .sectionMain,.forum_view .sectionMain,
.game-groups li,.game_comments_text blockquote,.game_newcomment_container,.game_tabs,
.gradient-gray-box,
.listtable tr:hover,
.mw-body,
.node .nodeText,.node,
.pager-title,
.parsoid-body,
.watch_forums .sectionMain,.watch_threads .sectionMain,.watch_threads_all .sectionMain,

[bgcolor="white"],
div.left > table,div.left > table td,
ol.subForumList,ol.subForumList h4.nodeTitle,
ul.leftlist li
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button,

div.showmore > a
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button::before,

div.showmore > a::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button:hover::before,

div.showmore > a:hover::before
 {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.game_review_button > a,

[href="help/cookies"]
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.game_review_button > a::before,

[href="help/cookies"]::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.game_review_button > a:hover::before,

[href="help/cookies"]:hover::before
{
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

a.button.CustomDismissCtrl
{	
	background: radial-gradient(circle, #09b705a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

a.button.CustomDismissCtrl::before
{
	background: radial-gradient(circle, #000 0%, #09b705 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

a.button.CustomDismissCtrl:hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.PanelScroller .panel, .PanelScrollerOff .panel,
.sidebar .secondaryContent,
.ui-widget.ui-widget-content
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.PanelScroller .panel:hover, .PanelScrollerOff .panel:hover,
.sidebar .secondaryContent:hover,
.ui-widget.ui-widget-content:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

#header,
#mw-head,

.bluetitle,
.discussionList .sectionHeaders,
.gradient-box,
.gradient-table-block,
.nodeList .categoryStrip,

div.header,
th
{
	background: linear-gradient(to bottom, #2D58CB, #000 100%)!important;
}

#pageFooter
{
	background: linear-gradient(to top, #2D58CB, #000 80%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.discussionListItem .stats,
.navTabs .navTab.selected .tabLinks
{
	background: linear-gradient(to bottom, #242323 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.bgstripe1:hover,
.bgstripe2:hover,
.discussionListItem:hover,
.node .nodeInfo:hover,
.small:hover,

section:hover,
table[width="100%"]:hover,
ul.leftlist li:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.tabs li.current, .tabs li.current:hover,
.vector-menu-tabs .selected,

ul.pager li.page-current
{
	background: radial-gradient(ellipse at center, #00809d 0%,#000 70%)!important;
}


}

@-moz-document domain("old-games.ru") {
/* Image Settings */

.bgstripe1,
.bgstripe2,
.discussionListItem,
.news-text,
.nodeList .node,
.small,

font,
p,
section,
table[width="100%"],
ul.leftlist li
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.bgstripe1:hover,
.bgstripe2:hover,
.discussionListItem:hover,
.news-text:hover,
.nodeList .node:hover,
.small:hover,

font:hover,
p:hover,
section:hover,
table[width="100%"]:hover font,
table[width="100%"]:hover,
ul.leftlist li:hover a
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img:not([src="styles/default/old-games/logo.png"])
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

.ui-widget-overlay
 {
	opacity: 1!important;
}

}

