style.css:
@-moz-document domain(old-games.ru) {
html {
  background: #101010;
}
body{
  background: #161616;
  margin: 10px auto;
  color: #757575;
}
a {
  transition: all 0.3s;
}
input[type="text"], #quicksearchform, .game_newcomment_textarea {
  background: #292929;
  outline: none !important;
  border: none !important;
 padding: 2px 0px 2px 0px;
	color: #757575;
}
.advitems {
  margin:  0px 5px 2px 5px;
}
.header {
  background-color: #222 !important;
  opacity: 0.1;
}
.headerpanel li {
  border: none !important;
}
.headerpanel  a:hover{
  text-decoration: none !important;
}
.headerpanel, .usermenulink, #nav a, .headerpanel li a {
	background:  -webkit-gradient(linear, left top, left bottom, from(#1d1d1d), to(#1d1d1d)) !important;
	border: none !important;
   transition: all 0s !Important;
}
 .whitelinkclicked {
  font-weight: bold !important;
}
.main a,  .head {
  color: #c5c5c5 !important; 
  font-weight: 200;
}
.main a:hover{
    color: #dc1407 !important;
  	text-decoration: none !important;
}
.main a:visited {
  color: #c5c5c5;
  }
#rside img {
	max-width: 100%;  
}

.main{
  margin-top: 10px;
  margin-bottom: 10px;
  border-bottom: none;

}
#main {
  border: none;
  background: #292929;
  border-radius: 10px;
   overflow: hidden;
}
#main img {
  border-radius: 5px;
  overflow: hidden;
  border: none !important;
  transition: all 0.3s;
  opacity: 0.5;
}
#main img:hover {
  opacity: 1;
}
.main .title, .game_title {
  padding: 7px;
  color: #5a5a5a;
  text-transform: uppercase;
  font-weight: 200;
}
.random-game {
  height: 210px;
}
#random_screen_game {
	float:  none !important;
  margin: 0 auto;
}
.imageWrapper {
  border: none;
}
#lside, #rside {
  width: 235px;
  background: none;
  border: none;
}
.right,.left,.tiptext {
  background: #292929;
  padding: 10px;
  margin: 0px 10px 10px 10px;
  border-radius: 0px 0px 10px 10px;
  color: #757575;
}
#lside img {
  opacity: 0.3;
  width: 218px;
  height: auto;
  border-radius: 10px;
  visibility: hidden;
}

.bluetitle, .breadcrumb, .left .head{
  margin: 0px 10px 0px 10px;
  border-radius: 10px 10px 0px 0px;
  padding: 7px;
  background: #1d1d1d;
  border: 0px;
  text-transform: uppercase;
  font-weight: 200;
  color: #5a5a5a;
}

.left .head {
  border-radius: 0px;
  position: relative;
  width: 201px;
  left: -20px;
  bottom: 10px;
 text-align: center;
  overflow: hidden;
}

.breadcrumb{
  margin: 0;
  padding: 6px;
}
.showmore {
  min-width: 110px;
}
.main div .showmore a, .news-more, input[type=submit] {
  margin: 0 auto;
  border-radius: 10px;
  padding: 7px;
  background: #1d1d1d;
  border: 0px;
  text-transform: uppercase;
  font-weight: 200;
  color: #5a5a5a;
}
#main input[type=submit] {
  background: #252525;
}
.news-more  {
  margin-top: 10px;
}
.left table, #releaseinfo td{
  background: none !important;
}
 .leftlist li, .left td {
   background: #1d1d1d !important;
   border: 1px solid #292929 !important;
   border-radius: 5px;
}
.leftlist li:hover, .left td:hover {
  background: #202020 !important;
  text-decoration: none !important;
}
.game-groups li, .ui-widget-content {
  background: #1d1d1d;
  color: #5a5a5a;
  border-radius: 10px;
  border: none !important;
  box-shadow: none;
}
.game-groups li:hover {
  background: #202020;
}
.game_tabs{
    background: #1d1d1d;
  color: #5a5a5a;
}
li.current a {
  background: #222;
}
.tabs  li:hover {
  background: #222;
}
#pageFooter {
  border-top: 10px solid #1d1d1d;
  border-bottom: 10px solid #1d1d1d;
  background: #292929;
}
#pageFooter a:hover{
  color: #fff;
  text-decoration: none;
  opacity: 0.6;
}
.socialbutton {
  opacity: 0.5;
  border-radius: 10px;
  transition: 0.3s;
}
.socialbutton:hover {
  opacity: 0.8;
}
ul.pager {
 max-width: 400px;
  margin: 0 auto;
}
ul.pager li{
  border: 0px solid;
   background: #1d1d1d;
  color: #5a5a5a;
  margin-left: 2px;
  border-radius: 5px;
}
li.page-current, .pager li:hover {
  background: #202020 !important;
  color: #dc1407 !important;
}
tr.catalog_header, th {
  background: #202020;
}
.game_comments_container, .listtable{
  position: relative;
  left: -10px;
  width: 865px;
}
.bgstripe1, #newcomment, .game_techinfo_container {
  background: #1d1d1d;
  padding: 5px;
  border: 0px;
}
.bgstripe2 {
  background: #202020;
  padding: 5px;
}
.game_video_container, .game_screen, .gamefiles-right, .game_infotable_wrapper.platform, .game-cover-item, .user-stat, .user-chracteristic {
  background: #1d1d1d !important;
  border-radius: 10px;
}
li.video_screen, li.video_screen:hover {
  background: none;
}

li.game_screen {
  width: 208px;
  margin: 0 auto;
  margin-bottom: 5px;
}
.screen_img_container {
	width: 192px !important;
	overflow: hidden;
 	max-height: 140px !important;
  border-radius: 5px;
  margin: 0 auto;
}
.screen_img_container img {
  width: 192px;
}
.listtable tr:hover {
  background: none;
}
.comments .red {
  color: white;
  opacity: 0.4;
}
.game_review_gradient{
  opacity:0.1;
}
.game_review_button {
  border-top: #1d1d1d;
}
.game_review_button a {
  background: #1d1d1d;
  border-radius: 0px 0px 5px 5px;
}
.bookmark-icon {
  background-color: #292929 !important;
}
.game_newcomment_textarea {
  border-radius: 5px;
  padding: 5px;
}
blockquote {
  background: #292929 !important;
  border-radius: 5px;
  border-left: 2px solid darkcyan !important;
  color: #757575 !important;
  opacity: 0.7 !important;
  transition: 0.5s;
}
blockquote:hover {
  opacity: 0.9 !important;
}
}
