style.css:
@-moz-document url-prefix("https://forum.truckersmp.com")
{
/**
  * TruckersMP Forum Red Accent (Dark Theme)
  * Created by Digital (https://forum.truckersmp.com/index.php?/profile/4963-digital); edited by Joschi (https://forum.truckersmp.com/index.php?/profile/222408-joschi/)
  */

  .ipsApp .ipsButton_important
  {
    background: #B92025 none repeat scroll 0 0;
  } 
  .ipsApp .ipsButton_normal
  {
    background-color: #B92025;
  } 
  .ipsBadge_positive
  {
  --badge--color: #B92025;
  } 
  .cAnnouncementIcon
  {
    background-color: #B92025;
  } 
  :root
  {
  --positive-dark: #732025;
  --theme-brand_primary: 185, 32, 37;
  --theme-featured: 185, 32, 37;
  --theme-comment_count: 185, 32, 37;
  --theme-mentions: 115, 32, 37;
  --theme-primary_button: 185, 32, 37;
  --theme-notification_bubble: 185, 32, 37;
  } 
  .ipsRepBadge_positive,
.ipsRepBadge_positive:hover
  {
    color: #EEEEEE!important;
  }
  .fa-plus-circle:before
  {
    content: "\f055";
    color: #b92025;
  }
  .fa-comment:before
  {
    content: "\f075";
    color: #b92025;
  }
  .fa-check-circle:before
  {
    content: "\f058";
    color: #b92025;
  }
  .fa-circle:before
  {
    content: "\f111";
    color: #b92025;
  }
  .ipsDataItem_unread>a,
.ipsDataItem_unread>a>span
  {
    color: #EEEEEE!important;
  }
  .ipsNavBar_primary>ul>li>a:hover
  {
    border-bottom: 1px solid #b92025;
  }
  .cProfileRepScore_positive
  {
    background-color: rgb(185 32 37/0.5);
  }
  .ipsOnlineStatus,
.ipsOnlineStatus_online
  {
    color: #b92025;
  }
}
