style.css:
@-moz-document url-prefix("https://forum.truckersmp.com")
{
/**
  * TruckersMP Forum Purple Accent (Dark Theme)
  * Created by Digital (https://forum.truckersmp.com/index.php?/profile/4963-digital); edited by Joschi (https://forum.truckersmp.com/index.php?/profile/222408-joschi/)
  */

  .ipsApp .ipsButton_important
  {
    background: #6D41A1 none repeat scroll 0 0;
  } 
  .ipsApp .ipsButton_normal
  {
    background-color: #6D41A1;
  } 
  .ipsBadge_positive
  {
  --badge--color: #6D41A1;
  } 
  .cAnnouncementIcon
  {
    background-color: #6D41A1;
  } 
  :root
  {
  --positive-dark: #613a8f;
  --theme-brand_primary: 109, 65, 161;
  --theme-featured: 109, 65, 161;
  --theme-comment_count: 109, 65, 161;
  --theme-mentions: 97, 58, 143;
  --theme-primary_button: 109, 65, 161;
  --theme-notification_bubble: 109, 65, 161;
  } 
  .ipsRepBadge_positive,
.ipsRepBadge_positive:hover
  {
    color: #EEEEEE!important;
  }
  .fa-plus-circle:before
  {
    content: "\f055";
    color: #6D41A1;
  }
  .fa-comment:before
  {
    content: "\f075";
    color: #6D41A1;
  }
  .fa-check-circle:before
  {
    content: "\f058";
    color: #6D41A1;
  }
  .fa-circle:before
  {
    content: "\f111";
    color: #6D41A1;
  }
  .ipsDataItem_unread>a,
.ipsDataItem_unread>a>span
  {
    color: #EEEEEE!important;
  }
  .ipsNavBar_primary>ul>li>a:hover
  {
    border-bottom: 1px solid #6D41A1;
  }
  .cProfileRepScore_positive
  {
    background-color: rgb(109 65 161/0.5);
  }
  .ipsOnlineStatus,
.ipsOnlineStatus_online
  {
    color: #6D41A1;
  }
}
