@namespace url(http://www.w3.org/1999/xhtml);

/*
 * Style	: Google services in Segoe UI 8pt
 * Author	: kilroy
 * email	: vishal.17@gmail.com
 */

@-moz-document domain("mail.google.com") {

/* HIDE INVITE BOX */
    .nH.pY {
        display: none !important;
    }
}


/* SERVICES WHICH WILL SHOW CONTENT IN SEGOE UI 8pt */

@-moz-document url-prefix("https://www.google.com/accounts"),
               url("https://mail.google.com/mail/?shva=1"),
               url("https://mail.google.com/mail/?ui=2&ik=97e985c52b&view=ac"),
               domain("mail.google.com"),
               /* REMEMBER THE MILK */
               url-prefix("http://www.rememberthemilk.com/services/modules"),
	       url-prefix("http://www.google.com/calendar"),
               url-prefix("https://www.google.com/calendar"),
               domain("googleusercontent.com"),
               url-prefix("https://www.google.com/reader") { 

/* Universal Segoe UI 8pt Font */

    * { 
        font-family: "Segoe UI" !important;
        font-size: 8pt !important;
    }
}
