    /*[[colortheme]]*/
ytd-macro-markers-list-item-renderer:hover, ytd-macro-markers-list-item-renderer[active] {
    border-radius: 10px !important;
}
/*chapter background delete*/
#content.ytd-engagement-panel-section-list-renderer {
    background-color: transparent !important;
}
/*chapter border radius*/
ytd-engagement-panel-section-list-renderer {
    border-radius: 15px;
}
/*already watched backdrop filter / radius*/
yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer {
    background-color: rgb(0 0 0 / 50%) !important;
    backdrop-filter: blur(10px);
    border-radius: 5px !important;
}
/*feed filter before / after - background remove*/
ytd-feed-filter-chip-bar-renderer[is-dark-theme] #right-arrow.ytd-feed-filter-chip-bar-renderer:before, #right-arrow.ytd-feed-filter-chip-bar-renderer:before, ytd-feed-filter-chip-bar-renderer[is-dark-theme] #left-arrow.ytd-feed-filter-chip-bar-renderer:after, #left-arrow.ytd-feed-filter-chip-bar-renderer:after {
    background: transparent !important;
}
ytd-thumbnail.ytd-rich-grid-media:before {
    background-color: transparent !important;
}
/*feed filter background remove*/
#left-arrow-button.ytd-feed-filter-chip-bar-renderer, #right-arrow-button.ytd-feed-filter-chip-bar-renderer {
    background-color: transparent !important;
}
/*feed filter background remove / border delete*/
#chips-wrapper.ytd-feed-filter-chip-bar-renderer {
    background-color: transparent !important;
    border-top: none !important;
    border-bottom: none !important;
}


yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT], yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER], yt-chip-cloud-chip-renderer[chip-style=STYLE_REFRESH_TO_NOVEL_CHIP] {
    background-color: var(--yt-spec-badge-chip-background);
    border: 0px solid var(--yt-spec-10-percent-layer);
    color: var(--yt-spec-text-primary);
    padding: 0 var(--ytd-margin-3x);
    transition: background-color 0.5s cubic-bezier(0.05, 0, 0, 1);
}
yt-chip-cloud-chip-renderer {
    height: 0px;
    min-width: 0px;
    padding: 0 0px;
    border-radius: 0px;
    box-sizing: border-box;
    outline: none;
    overflow: hidden;
    cursor: pointer;
    user-select: none;
    position: relative;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    display: -ms-inline-flexbox;
    display: -webkit-inline-flex;
    display: inline-flex;
    font-size: var(--ytd-user-comment_-_font-size);
    font-weight: var(--ytd-user-comment_-_font-weight);
    line-height: var(--ytd-user-comment_-_line-height);
    letter-spacing: var(--ytd-user-comment_-_letter-spacing);
    font-family: var(--ytd-user-comment_-_font-family);
    color: var(--ytd-user-comment_-_color);
}
style attribute {
    -ytd-rich-grid-chips-bar-width: 0px;
    -ytd-rich-grid-chips-bar-top: 0px;
}
ytd-feed-filter-chip-bar-renderer {
    -ytd-rich-grid-chips-bar-top: 0px;
    width: 100%;
    height: 0px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
}
#masthead {
   background-color: rgba(0,0,0,0.7) !important;
    border-color: rgba(0,0,0,0.7) !important;
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
    backdrop-filter: blur(25px);
    border-bottom: 6px solid rgb(193 193 193) !important;
}

#masthead-container.ytd-app {
    position: absolute;
    top: 0;
    width: 100%;
    z-index: 2020;
    transform: translateY(0);
    transition: 1s;
    margin-top: -55px;
}


div#masthead-container:hover {
    margin-top: 0px;
    transition: 1s;
}
}
