    /* blah blah blah change text on da header and left navbar */
    
    a.font-header-2.nav-menu-title.text-header[href="/home"] {
        visibility: hidden;
        font-size: 0px;
        height: 34px;
    }
    
    a.font-header-2.nav-menu-title.text-header[href="/home"]::before {
