    .rbx-header .icon-robux-28x28, .rbx-header .icon-common-search-sm, .icon-nav-settings, .icon-nav-menu, .rbx-left-col .icon-nav-home, .rbx-left-col .icon-nav-profile, .rbx-left-col .icon-nav-message, .icon-nav-message-btr, .rbx-left-col .icon-nav-friends, .icon-nav-friend-btr, .rbx-left-col .icon-nav-charactercustomizer, .rbx-left-col .offers-icon .new-menu-icon, .rbx-left-col .icon-nav-inventory, .rbx-left-col .icon-nav-trade, .rbx-left-col .sandbox-icon .new-menu-icon, .rbx-left-col .icon-nav-group, .rbx-left-col .icon-nav-my-feed, .rbx-left-col .icon-nav-blog  {
