@-moz-document domain("youtube.com") {
/* Media Query 2560 */

@media (min-width: 1921px) and (max-width: 2560px) {

  #page-manager.ytd-app ytd-two-column-browse-results-renderer
	{
    width: 94%!important;
  }
	
	ytd-rich-grid-slim-media {
    width: 89%;
}
    
  #chips-wrapper.ytd-feed-filter-chip-bar-renderer {
    width: 79%!important;
  }
	
	ytd-browse[page-subtype=playlist][amsterdam] ytd-two-column-browse-results-renderer.ytd-browse {
    padding-left: 0;
    margin-left: 29%;
}
	
	ytd-rich-item-renderer[is-slim-media],
	ytd-playlist-header-renderer {
    width: 24%;
}

  tp-yt-app-drawer[persistent] {
    width: 17%!important;
  }

  iron-input.tp-yt-paper-input > input.tp-yt-paper-input,
  ytd-app[guide-persistent-and-visible] ytd-page-manager.ytd-app {
    margin-left: 17% !important;
  }

  ytd-playlist-thumbnail.ytd-grid-playlist-renderer {
    width: 40rem !important;
  }

  ytd-playlist-thumbnail.ytd-grid-playlist-renderer {
    height: 30rem!important;
  }

  .ytp-progress-bar-container,
  .ytp-larger-tap-buttons.ytp-small-mode .ytp-progress-bar-container {
    bottom: 121px !important;
  }

  .html5-video-player .video-stream[style*="height: 549px"] {
    margin-left: 16rem !important;
    top: -9px !important;
    width: 87% !important;
  }

  #movie_player.html5-video-player .video-stream,
  .html5-video-player .video-stream[style*="height: 1440px"] {
