	background: url(https://i.ibb.co/ZchWyQ7/red-fullscreen-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.atvwebplayersdk-fullscreen-button img:hover,
.atvwebplayersdk-fullscreen-button img {
	background-size: 45px!important;
	height: 46px!important;
	padding-left: 46px!important;
	width: 1px!important;
}

/* Exit Fullscreen */

.atvwebplayersdk-fullscreen-button.toggled img,

[aria-label="Fullscreen"] svg {
	background: url(https://i.ibb.co/FW3n4Sj/red-exit-fullscreen.png) no-repeat!important;
	box-sizing: border-box;
}

.atvwebplayersdk-fullscreen-button.toggled img:hover,

[aria-label="Fullscreen"] svg:hover {
	background: url(https://i.ibb.co/kS6Tfd1/red-exit-fullscreen-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.atvwebplayersdk-fullscreen-button img,
.atvwebplayersdk-fullscreen-button.toggled img,

[aria-label="Fullscreen"] svg:hover,
[aria-label="Fullscreen"] svg
{
	background-size: 45px!important;
	height: 46px!important;
	padding-left: 46px!important;
	width: 1px!important;
}

/* Volume
_______________________________________________________________________________*/

[aria-label="Volume"] img {
	background: url(https://i.ibb.co/26GGj7b/volume-soft-medium-loud.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label="Volume"] img {
	background-size: 55px!important;
	height: 56px!important;
	padding-left: 56px!important;
	width: 1px!important;
}

}

